INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (903, 0, 0, 'Marshal Marris expects the reserve from Stormwind any time now. . . .', 12, 7, 100, 1, 0, 0, 53, 0, 'Guard Howe');
