INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1364, 0, 7, 'I am Balgaras the Foul.  And you, $n, are about to become nothing more than ash and dust.', 12, 6, 100, 0, 0, 0, 456, 0, 'Balgaras the Foul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1364, 0, 3, 'Foolish $C!  You are no match for the minions of Ragnaros!', 12, 6, 100, 0, 0, 0, 451, 0, 'Balgaras the Foul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1364, 0, 4, 'A worthless $R such as you, $n, has no right to walk these lands!', 12, 6, 100, 0, 0, 0, 452, 0, 'Balgaras the Foul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1364, 0, 1, '$n!  Betrayer of Ragnaros!', 12, 6, 100, 0, 0, 0, 449, 0, 'Balgaras the Foul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1364, 0, 2, 'The Dark Iron Dwarves shall reclaim their rightful lands!', 12, 6, 100, 0, 0, 0, 450, 0, 'Balgaras the Foul');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 8, 0, 'Ha! I\'d take the sound of a Crater Cannon XT blasting off a shell over that any day!', 12, 7, 100, 5, 0, 0, 250, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 9, 0, 'Maybe, but if it did hit me upside the head I\'d know exactly which cannon to use to blow it into the smallest bits!', 12, 7, 100, 1, 0, 0, 252, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 6, 0, 'Har! You scared I\'ll hurt your little dainty racer?', 12, 7, 100, 6, 0, 0, 246, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 7, 0, 'Haha! Sturdy old Trollplow has just a few meager battle scars!', 12, 7, 100, 11, 0, 0, 248, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 4, 0, 'Bah! That\'s no fun!', 12, 7, 100, 274, 0, 0, 241, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 5, 0, 'Aye sure, just as long as we get to hit the arena right after!', 12, 7, 100, 1, 0, 0, 244, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 2, 0, 'Hey Fizzer, I ever tell you the time I was riding through the canals and some goblins in shredder suits tried to ambush me?', 12, 7, 100, 1, 0, 0, 237, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 3, 0, 'Brahaha! Tha look on their faces as old Trollplow\'s gun came swiveling around to meet them! Their saws couldn\'t even touch me!', 12, 7, 100, 11, 0, 0, 239, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 1, 0, 'Bah! By the time you got in range of old Trollplow you would be in reduced to rubble!', 12, 7, 100, 274, 0, 0, 235, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1377, 0, 0, 'Ah yes. I\'ve heard all about those new flarged things. Armor was removed from the casing to decrease weight. Bargh! Nonsense!', 12, 7, 100, 1, 0, 0, 233, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 9, 0, 'Bah! You wouldn\'t know a great steam engine if it smacked you upside the head!', 12, 7, 100, 274, 0, 0, 251, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 8, 0, 'Nothing beats the sound of a twin-carb, fork-ring steam engine burning pure firestone!', 12, 7, 100, 1, 0, 0, 249, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 6, 0, 'Nothing you can say will ever get me in the arena against that monstrosity of a machine you call a steam tank!', 12, 7, 100, 274, 0, 0, 245, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 7, 0, 'Ha! I\'m more afraid that those old holes and gashes would burst that thing and I would be showered by the shrapnel!', 12, 7, 100, 5, 0, 0, 247, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 5, 0, 'Hey Stone, let\'s hit the circuit some time. We could run a few laps and see who wins?', 12, 7, 100, 1, 0, 0, 242, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 2, 0, 'Argh! You\'re hopeless!', 12, 7, 100, 5, 0, 0, 236, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 3, 0, 'Ah, let me see. Yes probably nearing a hundred times now I\'d wager!', 12, 7, 100, 273, 0, 0, 238, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 4, 0, 'I would have just dropped some Evershine in my tank and been back home before they realized I\'d gone!', 12, 7, 100, 1, 0, 0, 240, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 0, 0, 'Hey Stone, did you hear about the new steam engine old Tonkrend has been runnin on?', 12, 7, 100, 1, 0, 0, 232, 0, 'Pilot Bellowfiz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (1378, 1, 0, 'Do you ever think about anything other than fighting? I could circle you down on the stretch course!', 12, 7, 100, 6, 0, 0, 234, 0, 'Pilot Bellowfiz');
