INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3626, 2, 4, 'Smoother than and elf, stronger than an orc! No better drinks in all of Stormwind!', 12, 7, 100, 0, 0, 0, 1205, 0, 'Jenn Langston');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3626, 2, 0, 'Feeling hot? Get something to cool you off down at the Pig and Whistle!', 12, 7, 100, 0, 0, 0, 1200, 0, 'Jenn Langston');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3626, 2, 1, 'Nightly brawls and great drinks! Get it all at the Pig and Whistle!', 12, 7, 100, 0, 0, 0, 1201, 0, 'Jenn Langston');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3626, 1, 7, 'Do you think I need more pieces of flair?', 12, 0, 100, 1, 0, 0, 1536, 0, 'Jenn Langston');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3627, 0, 7, 'Ask me how to get a free drink at the Blue Recluse!', 12, 7, 100, 0, 0, 0, 1192, 0, 'Erich Lohan');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3658, 0, 2, 'Lizzarik has blades. Lizzarik has maces. Lizzarik has all your weapon needs right here!', 12, 0, 100, 0, 0, 0, 1214, 0, 'Lizzarik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3658, 0, 3, 'Act quick! Once it\'s gone, it\'s gone! Get your weapons here now!', 12, 0, 100, 0, 0, 0, 1215, 0, 'Lizzarik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3658, 0, 0, 'Best weapons in the Barrens here!', 12, 0, 100, 0, 0, 0, 1212, 0, 'Lizzarik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3658, 0, 1, 'Step right up! Finest selection of weapons this side of Ashenvale!', 12, 0, 100, 0, 0, 0, 1213, 0, 'Lizzarik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3660, 2, 0, 'No! You can\'t defeat me!', 12, 0, 100, 0, 0, 0, 1450, 0, 'Athrikus Narassin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3660, 1, 0, 'My... My powers have diminished... You shall pay for this insolence!', 14, 10, 100, 0, 0, 0, 1449, 0, 'Athrikus Narassin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3660, 0, 0, 'Athrikus points at you as he bellows forth words that scrape apart the inside of your ears. They are the last thing you hear.', 16, 0, 100, 0, 0, 0, 1223, 0, 'Athrikus Narassin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3663, 0, 0, 'A simple enough task to break this soulstone!', 12, 7, 100, 0, 0, 0, 1459, 0, 'Delgren the Purifier');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3663, 1, 0, 'Then we must put an end to Athrikus Narassin. Tell me, spirit, where might we find the other soulgems, to undo his magic?', 12, 7, 100, 6, 0, 0, 1473, 0, 'Delgren the Purifier');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3692, 4, 2, 'We shall earn our deaths at the very least!', 12, 7, 100, 0, 0, 0, 1252, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3692, 4, 3, 'Don\'t give up! Fight to the death!', 12, 7, 100, 0, 0, 0, 1253, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3692, 4, 1, 'We can overcome these foul creatures!', 12, 7, 100, 0, 0, 0, 1251, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3692, 4, 0, 'Here they come!', 12, 7, 100, 0, 0, 0, 1250, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3692, 5, 0, 'We made it, my friend. Remember to find Terenthis and let him know we\'re safe. Thank you again.', 12, 7, 100, 0, 0, 0, 1243, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3693, 10, 0, '%s begins to cast a spell as you take the cloak from him.', 16, 7, 100, 0, 0, 0, 1249, 0, 'Terenthis');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3693, 11, 0, 'Go now, $n. Find Volcor... and remember, the magics protecting you will fail if you interact with anyone, save for talking.', 12, 7, 100, 1, 0, 0, 1289, 0, 'Terenthis');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3693, 8, 0, 'I shall get started right away, $n.', 12, 7, 100, 0, 0, 0, 1247, 0, 'Terenthis');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3693, 9, 0, 'Now... where was my thread and needle. Ah! There it is...', 12, 7, 100, 0, 0, 0, 1248, 0, 'Terenthis');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3694, 0, 0, 'Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale.', 12, 7, 100, 0, 0, 0, 1302, 0, 'Sentinel Selarin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3694, 1, 0, 'Thank you, Terenthis. I shall remain here for as long as I can.', 12, 7, 100, 0, 0, 0, 1303, 0, 'Sentinel Selarin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3694, 2, 0, 'Hopefully I can send a worthy few to help Ashenvale and the Sentinels.', 12, 7, 100, 0, 0, 0, 1304, 0, 'Sentinel Selarin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3694, 3, 0, 'Thank you again, Terenthis. I shall return again if more help is needed. Until then, please send more adventurers to Ashenvale', 12, 7, 100, 0, 0, 0, 1306, 0, 'Sentinel Selarin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 6, 0, '%s roars in excitement as he rushes Volcor. When he reaches his master, Grimclaw licks his face.', 16, 7, 100, 0, 0, 0, 1241, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 2, 0, '%s roars at Terenthis more, but this time the druid seems to understand the bear.', 16, 0, 100, 0, 0, 473, 1231, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 3, 0, '%s roars in acknowledgement at Terenthis.', 16, 0, 100, 0, 0, 473, 1233, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 1, 0, '%s begins to moan and roar at Terenthis while stomping his paws on the ground.', 16, 0, 100, 53, 0, 474, 1228, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 5, 0, '%s faces southeast and whimpers before looking back at you.', 16, 7, 100, 0, 0, 0, 1225, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3695, 4, 0, '%s growls in your direction before taking time to sniff you.', 16, 7, 100, 0, 0, 0, 1234, 0, 'Grimclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3701, 0, 0, 'Be cleansed!', 12, 0, 100, 5, 0, 0, 5937, 0, 'Tharnariun Treetender');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3825, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Ghostpaw Alpha');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3847, 0, 0, '%s mixes the Bathran\'s Hair...', 16, 0, 100, 0, 0, 0, 1307, 0, 'Orendil Broadleaf');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3847, 1, 0, 'The mixture is finished, $n.  Now we must save the child.', 12, 7, 100, 1, 0, 0, 1308, 0, 'Orendil Broadleaf');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3848, 2, 0, 'I must... RESIST!', 12, 7, 100, 0, 0, 0, 1318, 0, 'Kayneth Stillwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3848, 3, 0, 'I have survived the poisons of the Forsaken, $n, and I know now what must be done.', 12, 7, 100, 1, 0, 0, 1319, 0, 'Kayneth Stillwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3848, 1, 0, 'Uh... the disease is potent.  And it attacks the mind...', 12, 7, 100, 0, 0, 0, 1316, 0, 'Kayneth Stillwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3848, 0, 0, 'Putting the bottles away, %s sighs.', 16, 0, 100, 0, 0, 0, 14108, 0, 'Kayneth Stillwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3892, 4, 0, 'Mmh... my eyes are not so heavy now, father.  Can we go out and play?', 12, 7, 100, 0, 0, 0, 1349, 0, 'Relara Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3892, 2, 0, '%s stirs from her delirium.', 16, 0, 100, 0, 0, 0, 1441, 0, 'Relara Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3892, 3, 0, 'Mmmmh...', 12, 0, 100, 0, 0, 0, 1442, 0, 'Relara Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3892, 0, 0, '%s drinks the cure brought by $n.', 16, 0, 100, 0, 0, 0, 1336, 0, 'Relara Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3892, 1, 0, 'Uh... Father...?  Are you there?  I\'m hungry... can I have some stew?', 12, 7, 100, 0, 0, 0, 1337, 0, 'Relara Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 11, 0, 'Rest now, my daughter.', 12, 7, 100, 1, 0, 0, 1351, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 12, 0, '%s looks at $n, and then weeps with joy.', 16, 0, 100, 0, 0, 0, 1352, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 13, 0, 'Relara is saved, $n.  Thank you.', 12, 7, 100, 18, 0, 0, 1353, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 8, 0, '%s crushes the moonstones into a cup of tea.', 16, 0, 100, 0, 0, 0, 1346, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 9, 0, 'Here you are, Relara.  Drink this...', 12, 7, 100, 16, 0, 0, 1347, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 10, 0, 'Relara... you\'re awake?', 12, 7, 100, 0, 0, 0, 1350, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 6, 0, '%s places a poultice on Relara\'s head.', 16, 0, 100, 0, 0, 0, 1443, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 7, 0, '%s breathes a sigh of relief.', 16, 0, 100, 0, 0, 0, 1444, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 4, 0, 'Eat this, my daughter.  It will help you feel better.', 12, 7, 100, 0, 0, 0, 1439, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 5, 0, 'There you go...', 12, 7, 100, 0, 0, 0, 1440, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 3, 0, '%s places the tear into a bowl of stew.', 16, 0, 100, 0, 0, 0, 1438, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 2, 0, 'You\'re awake??  Your cure must have worked!  Thank you, $n.', 12, 7, 100, 0, 0, 0, 1339, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 0, 0, 'Here you are, Relara.  Take this...', 12, 7, 100, 0, 0, 0, 1335, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3894, 1, 0, 'What?', 12, 7, 100, 0, 0, 0, 1338, 0, 'Pelturas Whitemoon');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3916, 2, 0, 'Now, then... the rod. Let\'s see about putting it back together, shall we?', 12, 7, 100, 0, 0, 0, 1382, 0, 'Shael\'dryn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3916, 3, 0, 'There! That wasn\'t too bad. What did you say you and Raene were going to do with this thing? It\'s powerful!', 12, 7, 100, 0, 0, 0, 1383, 0, 'Shael\'dryn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3916, 1, 0, 'Hehe... I made a pun, didn\'t I? Druidic in nature... I\'m so funny.', 12, 7, 100, 0, 0, 0, 1381, 0, 'Shael\'dryn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3916, 0, 0, 'The rod\'s design is simple. Dartol didn\'t use his arcane knowledge in its creation... it is more druidic in nature.', 12, 7, 100, 0, 0, 0, 1380, 0, 'Shael\'dryn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3932, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Bloodtooth Guard');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3987, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Dal Bloodclaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3995, 0, 0, '%s drops the animal parts into the bubbling cauldron.', 16, 0, 100, 69, 0, 0, 1405, 0, 'Witch Doctor Jin\'Zil');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3995, 1, 0, 'Ah, feel the magic seep into the cave, my little elves... muhahaha.', 12, 1, 100, 5, 0, 0, 1406, 0, 'Witch Doctor Jin\'Zil');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (3995, 2, 0, 'So much for the power of nature... muhahahaha.', 12, 1, 100, 11, 0, 0, 1407, 0, 'Witch Doctor Jin\'Zil');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (4001, 0, 0, 'Hey! You\'re not supposed to be here! C\'mon boys!', 12, 0, 100, 0, 0, 0, 1428, 0, 'Windshear Tunnel Rat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (4004, 0, 0, 'I think the sounds came from over here. We\'d better check it out.', 12, 0, 100, 0, 0, 0, 1430, 0, 'Windshear Overlord');
