INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (6909, 0, 0, 'I know you are there, rogue. Leave my home or join the others at the bottom of the world tree.', 12, 0, 100, 0, 0, 0, 2981, 0, 'Sethir the Ancient - Near player');
