INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14498, 0, 3, 'It\'s my way or the Gold Road.  Is that understood?', 12, 1, 100, 0, 0, 0, 9696, 0, 'Tosamina');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14498, 0, 0, 'You are mine now, children.  You will grow up to be a strong part of the Horde!', 12, 1, 100, 0, 0, 0, 9693, 0, 'Tosamina');
