INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 0, 0, 'Hmmm... Where is my fishing hook? Oh, there it is.', 12, 0, 100, 0, 0, 0, 10249, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 1, 0, 'Let\'s see if the fish are biting.', 12, 0, 100, 0, 0, 0, 10250, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 2, 0, 'Hmmm... I think I need better bait. I\'ll check my father\'s wagon.', 12, 0, 100, 0, 0, 0, 10195, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 3, 0, 'Quillboar scraps! These should do the trick. Fish love Quillboar.', 12, 0, 100, 0, 0, 0, 10196, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 4, 0, 'Caught one! I\'ll see if any of the guards are hungry. Come on Fang!', 12, 0, 100, 0, 0, 0, 10205, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 5, 0, 'I caught a fish if you are hungry...', 12, 0, 100, 0, 0, 0, 10198, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14857, 6, 0, 'Zug zug! Happy to help!', 12, 0, 100, 0, 0, 0, 10206, 0, 'Erk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14859, 0, 0, 'More bread boy!', 12, 0, 100, 1, 0, 0, 10183, 0, 'Guard Taruc');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14859, 1, 0, 'Aaahh... Very good. Now scat!', 12, 0, 100, 1, 0, 0, 10265, 0, 'Guard Taruc');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14872, 0, 0, 'Help! I\'m being chased by a swarm of bees!', 12, 1, 100, 0, 0, 0, 10259, 0, 'Trok');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14872, 0, 1, 'I\'m so tired of running, but these bees won\'t leave me alone!', 12, 1, 100, 0, 0, 0, 10260, 0, 'Trok');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14872, 0, 2, 'I didn\'t know bees didn\'t like fire!', 12, 1, 100, 0, 0, 0, 10261, 0, 'Trok');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14872, 0, 3, 'Karu will you please help get these bees off of me? They sting!', 12, 1, 100, 0, 0, 0, 10262, 0, 'Trok');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 0, 0, 'Trok are you hungry? I\'m making some food for us to eat! ', 12, 1, 100, 0, 0, 0, 10251, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 1, 0, 'Here is some food for you Trok! Trok? Why are you running around like fearful human?', 12, 1, 100, 0, 0, 0, 10252, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 2, 0, 'Suit yourself, I guess you are not that hungry. You can make your own food next time!', 12, 1, 100, 0, 0, 0, 0, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 3, 0, 'Yummy. I love roasted Plainstrider.', 12, 1, 100, 0, 0, 0, 10292, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 4, 0, 'I wonder if the pigs are hungry...', 12, 1, 100, 0, 0, 0, 10255, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 5, 0, 'Are you piggies hungry? I have some food for you...', 12, 1, 100, 0, 0, 0, 10256, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 6, 0, 'I guess you were hungry! Trok was supposed to have fed you earlier... What has he been doing all this time?', 12, 1, 100, 0, 0, 0, 10257, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14874, 7, 0, 'What happened to our pigs?', 12, 1, 100, 0, 0, 0, 10305, 0, 'Karu');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (14893, 0, 0, 'A nice catch it is at that! You bring honor to the Horde boy, We thank you!', 12, 0, 100, 0, 0, 0, 10223, 0, 'Guard Kurall');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15180, 0, 0, '%s shifts uncomfortably.', 16, 0, 100, 0, 0, 0, 10853, 0, 'Baristolth of the Shifting Sands');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15180, 1, 0, 'When the time is right, my lady. All will be clear when the time is right. The master stirs even now. Look to the skies for the Brood of Nozdormu have returned.', 12, 0, 100, 1, 0, 0, 10854, 0, 'Baristolth of the Shifting Sands');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 22, 0, 'Hrm, curious... This ends abruptly. In Tanaris the Qiraji document facing a bronze dragon. There is much missing from this... More information must be found! Surely they are planning to invade Kalimdor once more!', 12, 0, 100, 6, 0, 0, 10852, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 21, 0, 'Fascinating, this coincides with theories of Un\'Goro being the home of the Titans when they inhabited Kalimdor. Perhaps Aman\'thul himself watched over \'The God Lands.\'', 12, 0, 100, 273, 0, 0, 10851, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 20, 0, 'The night elf army was pushed back through Un\'Goro, to the borders of the Tanaris desert. Something in Un\'Goro prevented the Qiraji from being able to take the land. I do not quite understand this word but I believe it to mean \'God Lands.\' It is stated that they could not \'take the God Lands.\'', 12, 0, 100, 1, 0, 0, 10850, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 19, 0, 'Poor man, this explains so much.', 12, 0, 100, 6, 0, 0, 10849, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 18, 0, 'The war continued but the will of the great leader was sapped. The whole of Silithus was soon engulfed by the silithid and their Qiraji hosts.', 12, 0, 100, 1, 0, 0, 10848, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 17, 0, 'Valstann, eager to please his father, convinced Fandral to allow him to take a small battalion to the defense of Southwind. Surely with the tide of battle turning their way, no harm could come to his beloved child. The trap was missed. Valstann was captured by the hiding Qiraji and Southwind Village obliterated. Rajaxx himself would take the captive Valstann to the front lines of the battle where - in front of Staghelm and the night elf forces - he would brutally execute the young night elf.', 12, 0, 100, 1, 0, 0, 10847, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 16, 0, 'The Qiraji forces were commanded to fall back and hide the bulk of their numbers beneath the sands of the desert. A small attack was ordered upon Southwind Village while the front lines of Staghelm\'s army were kept occupied.', 12, 0, 100, 1, 0, 0, 10846, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 15, 0, 'Valstann Staghelm served as the right hand of Fandral. A proud and noble warrior, his dedication and adoration for his father unyielding... and the Twin Emperors sensed this as weakness. A weakness that could be exploited.', 12, 0, 100, 1, 0, 0, 10845, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 14, 0, 'Vek\'nilash and Vek\'lor, known as the Twin Emperors of Ahn\'Qiraj, watched the war from the sanctuary of their temple. The clever brothers devised a plan to demoralize and divide Staghelm\'s forces.', 12, 0, 100, 1, 0, 0, 10844, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 13, 0, 'Unfortunately, the night elf defense was untenable and Rajaxx knew that they could not hold out much longer. Their tenacity, however, was causing great frustration to Rajaxx and causing even more frustration to his king. Wait, kings? Incredible...', 12, 0, 100, 6, 0, 0, 10843, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 12, 0, 'Staghelm and his armies were clearly overwhelmed as the endless flow of Rajaxx\'s troops poured into Silithus. It would seem as if Staghelm, however, was more than just a nuisance to the Qiraji. Under Staghelm, the whole of the night elf armies had come together to defend Kalimdor, greatly slowing the Qiraji forces.', 12, 0, 100, 1, 0, 0, 10842, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 11, 0, 'A mighty Qiraji known as General Rajaxx would command the initial incursion into Silithus. Only one would stand in his way: A night elf known as Staghelm. They refer to him as Khar\'sis or \'hand of the earth\' in the native Qiraji tongue.', 12, 0, 100, 1, 0, 0, 10841, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 10, 0, 'The Titans had long since vacated this world. Only the night elves that once inhabited this area were here to defend. Oh my! The next passage is fascinating!', 12, 0, 100, 1, 0, 0, 10840, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 8, 0, 'There is more.', 12, 0, 100, 0, 0, 0, 10838, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 9, 0, 'The moment that C\'Thun had so patiently waited for had finally arrived. His children had spent thousands of years building an army capable of exacting revenge upon the whole of Kalimdor and now the prize was within their grasp.', 12, 0, 100, 1, 0, 0, 10839, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 6, 0, 'This can\'t be right. An Old God could never be allowed to enter this world. The results would be... Oh dear!', 12, 0, 100, 5, 0, 0, 10836, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 7, 0, '%s regains her composure.', 16, 0, 100, 0, 0, 0, 10837, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 5, 0, 'The Old God would create avatars from the silithid in its own image. These avatars were to be known as Qiraji. Sentient and with purpose, the Qiraji would name their creator: C\'Thun was born... For many thousands of years the Qiraji worked feverishly to build a force capable of laying waste to the world that would betray their god. The great fortress city of Ahn\'Qiraj was created to house their growing armies and prepare for the coming of C\'Thun.', 12, 0, 100, 1, 0, 0, 10833, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 4, 0, 'The land of eternal starlight, Kalimdor, was a nurturing mother to all of its creatures. The magic of the Well of Eternity permeated the land and empowered the multitude of flora and fauna that would make the world their home. From this magical ether were born the silithid. It was through the silithid that the brooding Old God would reach and attempt to sunder the world that it once held in its unmerciful grasp. ', 12, 0, 100, 1, 0, 0, 10832, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 3, 0, 'For millennia this being lay dormant beneath the world - biding its time. From its prison it waited for the exact moment at which to strike back at those that would see it harmed.', 12, 0, 100, 1, 0, 0, 10831, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 2, 0, 'In the time before time, when the world was still in its infancy, a battle between a Titan and a being of unimaginable evil and power raged on this very soil. The prophecy is unclear about whether or not the Titan was vanquished in this battle but it illustrates that a Titan fell. An Old God had also fallen - or so it was thought.', 12, 0, 100, 1, 0, 0, 10830, 0, 'Geologist Larksbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15183, 1, 0, 'It is history... A history of Silithus... of Ahn\'Qiraj... of Titans and Old Gods... I read from the prophecy of C\'Thun as written by the Qiraji Prophet Skeram. A prophecy that portends a cataclysm...', 12, 0, 100, 1, 0, 0, 10829, 0, 'Geologist Larksbane');
