INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15990, 17, 0, 'Fools! You think yourselves triumphant? You have only taken one step closer to the abyss!', 14, 0, 100, 0, 0, 14464, 12985, 3, 'kelthuzad SAY_TAUNT2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15990, 18, 0, 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', 14, 0, 100, 0, 0, 14465, 12986, 3, 'kelthuzad SAY_TAUNT3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15990, 19, 0, 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', 14, 0, 100, 0, 0, 14466, 27382, 3, 'kelthuzad SAY_TAUNT4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (15990, 20, 0, 'Come, heroes... By the will of the Lich King, you shall be destroyed.', 14, 0, 100, 0, 0, 0, 12993, 3, 'Kel\'Thuzad SAY_DIALOGUE_SAPPHIRON_KELTHUZAD4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16059, 0, 0, 'I\'m going to kick yer ugly head in!', 14, 0, 100, 0, 0, 0, 11953, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16059, 0, 1, 'You want a fight? A fight you\'ll get!', 14, 0, 100, 0, 0, 0, 11952, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16059, 0, 2, 'I crush weaklings like you every day! Bring it on!', 14, 0, 100, 0, 0, 0, 11954, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16060, 4, 0, 'I have waited long enough. Now you face the harvester of souls.', 14, 0, 100, 0, 0, 8808, 13028, 3, 'Gothik SAY_PHASE_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16060, 5, 0, 'I... am... undone.', 14, 0, 100, 0, 0, 8805, 13026, 3, 'Gothik SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16060, 6, 0, 'Death is the only escape!', 14, 0, 20, 0, 0, 8806, 13027, 3, 'Gothik SAY_KILL');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16060, 7, 0, '%s teleports into the fray!', 41, 0, 100, 0, 0, 0, 32306, 3, 'Gothik EMOTE_PHASE_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16060, 8, 0, 'The central gate opens!', 41, 0, 100, 0, 0, 0, 32307, 3, 'Gothik EMOTE_GATE_OPENED');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 0, 0, 'Do as I taught you!', 14, 0, 100, 0, 0, 8855, 13075, 3, 'Razuvious SAY_AGGRO #1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 0, 1, 'The time for practice is over! Show me what you have learned!', 14, 0, 100, 0, 0, 8859, 13078, 3, 'Razuvious SAY_AGGRO #2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 0, 2, 'Show them no mercy!', 14, 0, 100, 0, 0, 8856, 13076, 3, 'Razuvious SAY_AGGRO #3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 1, 0, '%s lets loose a triumphant shout.', 41, 0, 30, 0, 0, 0, 13082, 3, 'Razuvious SAY_SLAY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 2, 0, 'You should have stayed home.', 14, 0, 50, 0, 0, 8861, 13081, 3, 'Razuvious SAY_TAUNTED #1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 2, 1, 'You disappoint me, students!', 14, 0, 50, 0, 0, 8858, 13077, 3, 'Razuvious SAY_TAUNTED #2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 2, 2, 'I\'m just getting warmed up!', 14, 0, 50, 0, 0, 8852, 13072, 3, 'Razuvious SAY_TAUNTED #3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 2, 3, 'Stand and fight!', 14, 0, 50, 0, 0, 8853, 13073, 3, 'Razuvious SAY_TAUNTED #4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 2, 4, 'Sweep the leg... Do you have a problem with that?', 14, 0, 50, 0, 0, 8861, 13080, 3, 'Razuvious SAY_TAUNTED #5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16061, 3, 0, 'An honorable... death.', 14, 0, 100, 0, 0, 8860, 13079, 3, 'Razuvious SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16063, 5, 0, 'Invaders, cease this foolish venture at once! Turn away while you still can!', 14, 0, 100, 0, 0, 8917, 13101, 3, 'zeliek SAY_ZELI_TAUNT1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16063, 6, 0, 'Perhaps they will come to their senses... and run away as fast as they can.', 14, 0, 100, 0, 0, 8918, 13102, 3, 'zeliek SAY_ZELI_TAUNT2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16063, 7, 0, '%s casts Condemnation on everyone!', 41, 0, 100, 0, 0, 0, 33088, 3, 'zeliek EMOTE_RAGECAST');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16064, 5, 0, 'To arms, ye roustabouts! We\'ve got company!', 14, 0, 100, 0, 0, 8903, 13038, 3, 'korthazz SAY_KORT_TAUNT1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16064, 6, 0, 'I\'ve heard about enough a\' yer snivelin\'! Shut yer flytrap before I shut it for ye\'!', 14, 0, 100, 0, 0, 8904, 13039, 3, 'korthazz SAY_KORT_TAUNT2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16065, 5, 0, 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun!', 14, 0, 100, 0, 0, 8896, 13014, 3, 'blaumeux SAY_BLAU_TAUNT1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16065, 6, 0, 'I do hope they stay alive long enough for me to... introduce myself.', 14, 0, 100, 0, 0, 8897, 13015, 3, 'blaumeux SAY_BLAU_TAUNT2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16065, 7, 0, '%s casts Unyielding Pain on everyone!', 41, 0, 100, 0, 0, 0, 33087, 3, 'blaumeux EMOTE_RAGECAST');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16144, 0, 3, 'Is that so? How very interesting. My dear, why don\'t you run along and powder your nose? I have much to think about.', 12, 1, 100, 1, 0, 0, 11999, 0, 'Lord Saltheril');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16144, 0, 1, 'Hmm, what was that? I\'m sorry my dear, I must have been daydreaming.', 12, 1, 100, 6, 0, 0, 11997, 0, 'Lord Saltheril');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16144, 0, 2, 'Oh, most definitely, Elisara. But please, can you show the least bit of patience?', 12, 1, 100, 1, 0, 0, 11998, 0, 'Lord Saltheril');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16144, 0, 0, 'As you say, my dear, as you say.', 12, 1, 100, 1, 0, 0, 11996, 0, 'Lord Saltheril');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 7, 'You promised to take me out for a night on the town!  Are we just going to stand around here again tonight?', 12, 1, 100, 5, 0, 0, 13356, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 6, 'Salthy, do you think we\'re letting too many \'undesirables\' into the party?  I do worry about what our friends might think of all of this.', 12, 1, 100, 6, 0, 0, 13355, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 4, 'I\'m so tired. We really shouldn\'t have stayed up so late last night... well this morning. But it was fun! You do throw the best soirees milord.', 12, 1, 100, 1, 0, 0, 0, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 5, 'Lord Saltheril, my dear, did you notice that supplies for the party are a little low? We really should send for one of those couriers from Fairbreeze Village to deliver more of whatever is needed. I especially like those little hors d\'oeuvres wrapped in the smoked bacon. What are those called?', 12, 1, 100, 6, 0, 0, 11995, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 3, 'Oh, my lordship, I do so hope that you\'ve arranged for more of that excellent wine from Silvermoon City. I dare say drinking it makes me feel almost as forward as a common woman!', 12, 1, 100, 1, 0, 0, 11993, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 2, 'Milord, do you think that we can join the party soon? I\'m so bored in here.', 12, 1, 100, 6, 0, 0, 11992, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 1, 'Milord, with much of the sin\'dorei nobility regrettably crushed, does that mean that you are likely to receive an elevation soon?  I\'m so excited for you!', 12, 1, 100, 6, 0, 0, 12071, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16147, 0, 0, 'I was just thinking... we really ought to take a trip south one of these nights, maybe have an evening picnic overlooking the fighting at the border? What do you think, milord? Wouldn\'t that just be so exciting?', 12, 1, 100, 6, 0, 0, 12070, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16151, 1, 0, '%s rushes to her master\'s aid.', 16, 0, 100, 0, 0, 0, 13455, 0, 'midnight EMOTE_MOUNT_UP');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16152, 0, 1, 'Another trophy to add to my collection!', 14, 0, 100, 0, 0, 9300, 15333, 0, 'attumen SAY_KILL2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16152, 2, 0, 'Weapons are merely a convenience for a warrior of my skill!', 14, 0, 100, 0, 0, 9166, 13490, 0, 'attumen SAY_DISARMED');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16204, 0, 0, 'Why doesn\'t Kaendris move against Dar\'Khan yet?  What is it we\'re waiting for?', 12, 0, 100, 1, 0, 0, 12594, 0, 'Magister Idonis');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16213, 0, 1, '%s glares at the guards.', 16, 0, 100, 0, 0, 0, 12591, 0, 'Ranger Lethvalin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16213, 0, 2, '%s sighs in apparent frustration.', 16, 0, 100, 0, 0, 0, 12592, 0, 'Ranger Lethvalin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16213, 0, 3, '%s peers inside the tower and then clenches his jaw.', 16, 0, 100, 0, 0, 0, 12593, 0, 'Ranger Lethvalin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16213, 0, 0, '%s taps his foot impatiently.', 16, 0, 100, 0, 0, 0, 12590, 0, 'Ranger Lethvalin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 5, 'They should have listened to me.  Then Id receive all of the accolades that are my due!', 12, 0, 100, 0, 0, 0, 12540, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 3, 'Im sure to receive a commendation upon our return. We may have suffered a few losses, but overall I think we did quite nicely, dont you, Valanna?', 12, 0, 100, 0, 0, 0, 12545, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 4, 'Did I not yell at them? Pull back! Pull back and regroup with suppressing fire!!', 12, 0, 100, 0, 0, 0, 12541, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 2, 'I tell you now, when we get back to Farstrider Enclave there will be a full accounting of your actions, Ranger Valanna! You alone were responsible for our losses and Im certain that the captain will see it my way.', 12, 0, 100, 0, 0, 0, 12543, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 0, 'Take that!', 12, 0, 100, 0, 0, 0, 12544, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16217, 0, 1, 'This isnt Silvermoon City! Where are we? Oh, I was confused there for a moment.', 12, 0, 100, 0, 0, 0, 12542, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16218, 0, 0, '%s loses its link!', 41, 0, 100, 0, 0, 0, 12156, 3, 'Tesla Coil EMOTE_TESLA_LINK_BREAKS');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16218, 1, 0, '%s overloads!', 41, 0, 100, 0, 0, 0, 12178, 3, 'Tesla Coil EMOTE_TESLA_OVERLOAD');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 4, 'Would you please shut up?!', 12, 0, 100, 0, 0, 0, 22240, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 5, 'With all due respect, sir, stick it!', 12, 0, 100, 0, 0, 0, 22243, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 2, 'Uh huh.  Whatever you say.', 12, 0, 100, 0, 0, 0, 22238, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 3, 'Idiot.', 12, 0, 100, 0, 0, 0, 22239, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 0, 'You have got to be the most incompetent officer I have ever met!', 12, 0, 100, 0, 0, 0, 22242, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16219, 0, 1, 'Remind me once again why it is that I dont just leave?', 12, 0, 100, 0, 0, 0, 22241, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16240, 0, 2, 'We must remain focused on our work.  Once the sanctum starts producing at full capacity, Silvermoon will be forced to send help.  Functioning arcane sanctums are in short supply.', 12, 0, 100, 1, 0, 0, 12597, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16240, 0, 1, 'He\'s got his reasons, Idonis.  He thinks the Farstriders have uncovered something.', 12, 0, 100, 1, 0, 0, 12596, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16240, 0, 0, 'Calm yourself, Idonis.  Brash actions will get us nowhere!  Take a break, tap into some mana crystals.  You\'ll feel better.', 12, 0, 100, 1, 0, 0, 12595, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16409, 1, 2, 'Leave us alone! Let us be!', 12, 0, 100, 0, 0, 0, 13840, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16414, 0, 3, 'How dare you spill the master\'s wine!', 12, 0, 100, 0, 0, 0, 13435, 0, 'Ghostly Steward - On Death');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16414, 1, 2, 'You\'ll never make it out alive....', 12, 0, 100, 0, 0, 0, 13879, 0, 'Ghostly Steward - On Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16441, 0, 0, '%s flees after seeing Kel\'Thuzad fall!', 16, 0, 100, 0, 0, 0, 12391, 3, 'Guardian of Icecrown EMOTE_FLEE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16441, 1, 0, 'A Guardian of Icecrown enters the fight!', 41, 0, 100, 0, 0, 0, 32804, 3, 'Guardian of Icecrown EMOTE_APPEAR');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16464, 0, 0, '%s cackles maniacally as he combines the two mixtures in his hands.', 16, 0, 100, 0, 0, 0, 13368, 0, 'Apothecary Venustus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16464, 1, 0, 'Drat!  This bloodthistle extract doesn\'t seem to want to combine properly with my poison.  I\'ll not be deterred - those trolls will be dealt with!  Perhaps a different mixture?', 12, 0, 100, 5, 0, 0, 13369, 0, 'Apothecary Venustus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16521, 0, 3, 'None of you are to leave this valley alive.', 12, 0, 100, 0, 0, 0, 13429, 0, 'Blood Elf Scout');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16551, 0, 0, 'Yes, Zhanna, it\'s me! Your signal is weak.', 12, 0, 100, 0, 0, 0, 13687, 0, 'Technician Dyvuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16551, 1, 0, 'Zhanna! Gah, I have to get this thing fixed!', 12, 0, 100, 0, 0, 0, 13692, 0, 'Technician Dyvuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16573, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 1191, 3, 'Crypt Guard EMOTE_FRENZY');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16573, 1, 0, 'A Crypt Guard joins the fight!', 41, 0, 100, 0, 0, 0, 29887, 3, 'Crypt Guard EMOTE_SPAWN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16573, 2, 0, 'Corpse Scarabs appear from a Crypt Guard\'s corpse!', 41, 0, 100, 0, 0, 0, 32796, 3, 'Crypt Guard EMOTE_SCARAB');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 0, 0, 'I say we fire it. Cut the rope.', 12, 1, 100, 1, 0, 0, 16981, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 1, 0, 'You cut the rope. You have the knife.', 12, 1, 100, 1, 0, 0, 16983, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 2, 0, 'Harumph! Coward.', 12, 1, 100, 1, 0, 0, 16982, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 3, 0, 'You know, I think we can hit Honor Hold from here.', 12, 1, 100, 1, 0, 0, 16986, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 4, 0, 'If General Krakork found out, he\'d eat us alive. Don\'t.', 12, 1, 100, 1, 0, 0, 16988, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 5, 0, 'Ah! We can tell him Hagash stumbled into it and fired it accidentally!', 12, 1, 100, 1, 0, 0, 16987, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16580, 7, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 16989, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 0, 0, 'The Horde fires will never be extinguished!', 12, 1, 100, 1, 0, 0, 24534, 0, 'Celebrant for the Horde');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 1, 0, 'I\'d like to see the Alliance try to steal our flame.', 12, 1, 100, 1, 0, 0, 24533, 0, 'Celebrant - Horde speech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 2, 0, 'The Horde have tried to steal our flames.  I hope we return the favor.', 12, 7, 100, 1, 0, 0, 24531, 0, 'Celebrant - Alliance speech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 3, 0, 'The Alliance fires burn strong and true!', 12, 7, 100, 1, 0, 0, 24532, 0, 'Celebrant - Alliance speech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 4, 0, 'Midsummer Fire Festival is a great time to celebrate the summer!', 12, 0, 100, 1, 0, 0, 24535, 0, 'Celebrant -  speech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16781, 5, 0, 'I love the firework show at the end of Midsummer Firefestival.', 12, 0, 100, 1, 0, 0, 24536, 0, 'Celebrant -  speech');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16790, 0, 0, 'Thank you, $N. It looks like they\'re going to get along just fine.', 12, 0, 100, 0, 0, 0, 13666, 0, 'Falconer Drenna Riverwind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16793, 0, 2, 'Did you find something fun to chase around, Twinkle?  I bet you did!', 12, 1, 100, 16, 0, 0, 13588, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16793, 0, 1, 'It\'s just you and me, Twinkle.  Daddy\'s not going to be coming home.', 12, 1, 100, 16, 0, 0, 13587, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16793, 0, 0, 'You\'re such a good friend, Twinkle.  You\'d never leave me, would you?', 12, 1, 100, 16, 0, 0, 13586, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16809, 1, 0, 'I... hate... you.', 14, 0, 100, 0, 0, 10322, 16925, 0, 'omrogg YELL_DIE_R');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16809, 2, 0, '%s enrages', 16, 0, 100, 0, 0, 0, 0, 0, 'omrogg EMOTE_ENRAGE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16833, 1, 0, 'The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.', 12, 0, 100, 0, 0, 0, 13996, 0, 'Makuru');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16834, 0, 0, 'No!  Not... Sedai!  The orcs must pay!', 12, 0, 100, 1, 0, 0, 13997, 0, 'Anchorite Obadei');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16834, 1, 0, 'What have you done, Makuru?!  These are not our ways!', 12, 0, 100, 0, 0, 0, 13991, 0, 'Anchorite Obadei');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16834, 2, 0, 'I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.', 12, 0, 100, 0, 0, 0, 13992, 0, 'Makuru');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16863, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 1191, 0, 'Deranged Helboar');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16915, 0, 0, 'I\'ll get you working yet, you bucket of junk.', 12, 0, 100, 1, 0, 0, 17798, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16915, 0, 1, 'What?!  You don\'t think I can do it?  I\'ll show you...once I get my parts, that is.', 12, 0, 100, 1, 0, 0, 17802, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16915, 0, 2, 'It moved!  I swear, I saw it move!', 12, 0, 100, 0, 0, 0, 17804, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16915, 0, 3, 'Ouch!  That\'s not right.', 12, 0, 100, 0, 0, 0, 17805, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16915, 0, 4, 'Oh yeah, it\'s real funny isn\'t it?', 12, 0, 100, 1, 0, 0, 17803, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16923, 0, 0, 'I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head.', 12, 1, 100, 0, 0, 0, 12832, 0, 'Novice Ranger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16938, 0, 2, 'I will do as you ask, demon.  At least for now.', 12, 0, 100, 0, 0, 0, 16470, 0, 'Dreghood Brute');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16972, 0, 0, '%s smells a fresh corpse nearby.', 16, 0, 100, 0, 0, 0, 16746, 0, 'Bonestripper Buzzard');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16973, 0, 0, '%s smells a fresh corpse nearby.', 16, 0, 100, 0, 0, 0, 16746, 0, 'Bonestripper Vulture');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16980, 2, 0, 'Invaders... here?! DESTROY them, Kel\'Thuzad! Naxxramas must not fall!', 14, 0, 100, 0, 0, 14769, 12989, 3, 'The Lich King SAY_DIALOGUE_SAPPHIRON_LICHKING2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16980, 3, 0, 'Very well. Warriors of the frozen wastes, rise up! I command you to fight, kill and die for your master! Let none survive!', 14, 0, 100, 0, 0, 14770, 12994, 3, 'The Lich King SAY_ANSWER_REQUEST');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16980, 4, 0, 'You will have your challenge, Fordring.', 14, 0, 100, 0, 0, 16321, 35747, 0, 'The Lich King SAY CHALLENGE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (16980, 5, 0, 'The souls of your fallen champions will be mine, Fordring.', 14, 0, 100, 0, 0, 16323, 35749, 0, 'The Lich King SAY SOULS WILL BE MINE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17015, 3, 0, 'Very well then, have it your way. When we turn others away, you will be responsible for their suffering. Think about that.', 12, 1, 100, 1, 0, 0, 13332, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17015, 2, 0, 'You\'ll do no such thing. We simply haven\'t the room to keep you here. Now, kindly be on your way.', 12, 1, 100, 1, 0, 0, 13330, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17015, 0, 0, 'Wake up, Gal\'ressa.', 12, 1, 100, 1, 0, 0, 13325, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17015, 1, 0, 'You\'ve had enough time to recover from your wounds, Gal\'ressa. The time has come for you to return to the road.', 12, 1, 100, 1, 0, 0, 13328, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17046, 3, 0, 'By the Sunwell, you\'ve a thick skull! You\'ll not bully me into leaving, Taleris. Now, begone from my sight!', 12, 1, 100, 1, 0, 0, 13331, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17046, 2, 0, 'I\'m not going on alone, Taleris, and that\'s final. I\'m going to wait here for the next group of pilgrims and set out with them when they depart.', 12, 1, 100, 1, 0, 0, 13329, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17046, 1, 0, 'What do you want, Taleris?', 12, 1, 100, 1, 0, 0, 13327, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17046, 0, 0, '%s rubs the sleep from her eyes.', 16, 0, 100, 0, 0, 0, 13326, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17093, 0, 0, '%s removes the reagents from the crate and mixes portions of each in a flask.', 16, 0, 100, 0, 0, 0, 13508, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17093, 1, 0, 'I\'ve endured much hardship to complete my research and with your help, $n, I\'ve finally done it. Here\'s to a brighter future!', 12, 0, 100, 7, 0, 0, 13509, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17093, 2, 0, 'I feel... lightheaded... What\'s... happening to me?', 12, 0, 100, 0, 0, 0, 13510, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17093, 3, 0, 'No, this can\'t be! This tonic... it was supposed to reverse my addiction, not hasten my descent into the ranks of the Wretched!', 12, 0, 100, 0, 0, 0, 13511, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17093, 4, 0, 'All those years of research... and for nothing! Perhaps they were right, perhaps this is something we must learn to live with.', 12, 0, 100, 0, 0, 0, 14318, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 3, 0, 'I need some fresh air, not that there\'s any to be had in this swamp!  I\'m going out for a bit.  Tidy up a bit while I\'m gone, would you?', 12, 0, 100, 1, 0, 0, 14101, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 4, 0, 'Must you follow me everywhere? <sigh>', 12, 0, 100, 6, 0, 0, 14102, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 1, 0, 'You have something to say to my face?', 12, 0, 100, 6, 0, 0, 14106, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 2, 0, 'I thought not!', 12, 0, 100, 11, 0, 0, 14107, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 5, 'Are you hungry?  I\'m hungry.  I just can\'t stand the thought of cured ham steak from Thultash again.  If only we could get some of that succulent roasted quail!', 12, 0, 100, 6, 0, 0, 14099, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 2, 'I cannot wait to see the results of this latest batch!', 12, 0, 100, 1, 0, 0, 14096, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 3, 'Avuun dares to oppose me?!  I will crush him!  When I am done with the draenei, they\'ll wish that they were Lost Ones!', 12, 0, 100, 15, 0, 0, 14097, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 4, 'Oh, would you please stop checking your nails and spanking yourself, for one moment?  Really, no one cares!', 12, 0, 100, 6, 0, 0, 14098, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 1, 'These orcs are fools!  So much power nearby just waiting to be taken, and all they do is sit on their hands and watch while others plunder the riches!', 12, 0, 100, 5, 0, 0, 14095, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17109, 0, 0, 'Hahaha!  That fool will never find out what I\'m up to!  Or, if he does, it\'ll be too late for the draenei!', 12, 0, 100, 11, 0, 0, 14094, 0, 'Cersei Dusksinger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17127, 2, 0, 'I must find a way to put an end to the evil that Anchorite Avuun is committing against our lost brothers and sisters!', 12, 0, 100, 5, 0, 0, 14085, 0, 'Anchorite Avuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17127, 3, 0, 'Magtoor, I have faith that there is a way to bring the Lost Ones back from the brink.  By studying them, I will find a way to cure the fel energies that have consumed them.  And in so doing, I will be able to reverse the damage to the Broken as well.', 12, 0, 100, 1, 0, 0, 14089, 0, 'Anchorite Avuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17127, 0, 0, 'Magtoor, thank you for allowing me to stay here with you.  I\'m sure that there is much that we can learn from each other.  And I\'m eager to learn all that I can of the Lost Ones to the east.', 12, 0, 100, 1, 0, 0, 14065, 0, 'Anchorite Avuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17127, 1, 0, 'Why do you traffic with the Horde?  Their foul ways are beneath you.', 12, 0, 100, 6, 0, 0, 14075, 0, 'Anchorite Avuun');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17220, 0, 0, '%s looks over at Temper as if reluctant to speak.', 16, 0, 100, 0, 0, 0, 13694, 0, 'Hauteur Reborn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17220, 1, 0, 'Thank you, shaman. I am reborn and will burn brighter than ever before!', 12, 0, 100, 0, 0, 0, 13693, 0, 'Hauteur Reborn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17228, 0, 0, 'How can I be expected to get any work done with that thing spreading its heresy?', 12, 0, 100, 0, 0, 0, 14383, 0, 'Draenei Artificer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17228, 1, 0, '%s sighs disgustedly as he turns back to his work.', 16, 0, 100, 0, 0, 0, 14384, 0, 'Draenei Artificer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17228, 2, 0, 'Turning to face Tuluun, the %s sneers contemptuously.', 16, 0, 100, 0, 0, 0, 14381, 0, 'Draenei Artificer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17228, 3, 0, 'We do not need your kind here.', 12, 0, 100, 0, 0, 0, 14382, 0, 'Draenei Artificer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17233, 4, 0, 'Why do you do this?  Did I somehow wrong you in life?', 12, 0, 100, 0, 0, 0, 13625, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17233, 5, 0, 'Ah, I see it now in your mind.  This is the work of one of my former students... Mehlar Dawnblade.  It is sad to know that his heart has turned so dark.', 12, 0, 100, 0, 0, 0, 13626, 0, 'Ghost of Uther Lightbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17233, 6, 0, 'Return to him.  Return to Mehlar and tell him that I forgive him and that I understand why he believes what he does.', 12, 0, 100, 0, 0, 0, 13627, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17233, 7, 0, 'I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas.', 12, 0, 100, 0, 0, 0, 13640, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17238, 3, 0, 'It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to us.  We must proceed with caution lest we be overwhelmed!', 12, 7, 100, 1, 0, 0, 13617, 0, 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17238, 4, 0, 'We are beset upon again! Defend yourself!', 12, 7, 100, 5, 0, 0, 13618, 0, 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17238, 5, 0, 'This land truly needs to be cleansed by the Light!  Let us continue on to the tomb.  It isn\'t far now.', 12, 7, 100, 1, 0, 0, 13619, 0, 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17238, 6, 0, 'Very well, let us continue.', 12, 7, 100, 1, 0, 0, 13615, 0, 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17238, 7, 0, 'Thank you my friend for making this possible.  This is a day that I shall never forget!  I think I will stay a while.  Please return to High Priestess MacDonnell at the camp.  I know that she\'ll be keenly interested to know of what has transpired here.', 12, 7, 100, 2, 0, 0, 13624, 0, 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17268, 0, 0, 'Dyvuun, is that you?', 12, 0, 100, 0, 0, 0, 13686, 0, 'Image of Technician Zhanaa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17268, 1, 0, 'I can barely hear you... <static> aking up!', 12, 0, 100, 0, 0, 0, 13688, 0, 'Image of Technician Zhanaa');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17311, 0, 0, 'Daughter!', 12, 7, 100, 0, 0, 0, 13749, 0, 'Cowlen');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17393, 2, 0, '[Furbolg] Follow me to Vark.', 12, 0, 100, 0, 0, 0, 13907, 0, 'Stillpine Ancestor Yor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17393, 1, 0, '[Furbolg] We go now, together. We will seek Vark.', 12, 0, 100, 0, 0, 0, 13903, 0, 'Stillpine Ancestor Yor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17393, 0, 0, '[Furbolg] Come, $n. Let us leave the water together, purified.', 12, 0, 100, 0, 0, 0, 13901, 0, 'Stillpine Ancestor Yor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17393, 3, 0, '[Furbolg] Hurry, $n. Vark awaits you.', 12, 0, 100, 0, 0, 0, 13927, 0, 'Stillpine Ancestor Yor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17414, 1, 0, '%s begins to arm the Proximity Bomb!', 16, 0, 100, 0, 0, 0, 15129, 0, 'Shadowmoon Technician');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17424, 0, 0, 'Now, let\'s see....', 12, 0, 100, 69, 0, 0, 14281, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17424, 1, 0, 'In the process of translating this, I believe I\'ve learned more about the drunken exploits of a certain \'Andrew Clementine\' than about the journal\'s author.', 12, 0, 100, 1, 0, 0, 14282, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17424, 2, 0, 'But, no matter. Mister Clementine\'s... err, \'contribution\' notwithstanding, this seems to be the journal of a man named Nolkai. The entries toward the end describe a brutal battle.', 12, 0, 100, 11, 0, 0, 14283, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17424, 3, 0, 'Take a look at this passage...', 12, 0, 100, 0, 0, 0, 14285, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17426, 0, 0, 'Morae... Is that you?', 12, 0, 100, 6, 0, 0, 14262, 0, 'Galaen');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17426, 1, 0, 'No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators.', 12, 0, 100, 274, 0, 0, 14263, 0, 'Galaen');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17426, 2, 0, 'Let her know... my last thought was of her...', 12, 0, 100, 1, 0, 0, 14264, 0, 'Galaen');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17433, 0, 0, 'These pictographs tell a story. This figure at the beginning, almost looks like one of the creatures we encountered at Azure Watch...', 12, 0, 100, 0, 0, 0, 14030, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17433, 1, 0, 'No, this can\'t be... It says this creature willingly became a servant of the Legion. He transforms into a satyr and receives the Legion\'s "blessing.', 12, 0, 100, 274, 0, 0, 14031, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17433, 2, 0, 'We must defeat these satyr, $n. We will not suffer the Legion\'s presence in these lands.', 12, 0, 100, 53, 0, 0, 14032, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17445, 0, 0, 'Stillpine raiders, the time to strike is at hand! Charge!', 14, 0, 100, 0, 0, 0, 14038, 0, 'Stillpine the Younger');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17528, 0, 0, 'Fools! Weaklings! Tzerak has no need of your assistance!', 14, 0, 100, 0, 0, 0, 14070, 1, 'Tzerak');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17612, 0, 0, 'Who dares wield magic in my presence?', 14, 0, 100, 0, 0, 0, 14229, 0, 'Quel\'dorei Magewraith');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17658, 0, 0, 'All hail $N, savior of Azuremyst and Bloodmyst. Hero of the Hand of Argus, champion of the draenei people!', 14, 0, 100, 22, 0, 0, 14691, 0, 'Exarch Admetius q9759 OnRewarded');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17663, 0, 0, 'If only I could get back to the mushroom thicket. I have not seen such a mature cluster in my lifetime!', 12, 7, 100, 0, 0, 0, 14269, 0, 'Jessera of Mac\'Aree');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17703, 0, 0, 'Don\'t you people ever check the mail?', 12, 7, 100, 0, 0, 0, 14328, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17703, 0, 1, 'How am I expected to deliver all of these messages?', 12, 7, 100, 0, 0, 0, 14329, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17703, 0, 2, 'I need to be re-assigned. This job is pushing me over the edge...', 12, 7, 100, 0, 0, 0, 14330, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17703, 0, 3, 'He thinks he\'s so damned important. Always pushing me around... I\'ll show him. I\'ll show all of them!', 12, 7, 100, 0, 0, 0, 14332, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17703, 0, 4, 'One of these days I\'m going to bust out the crystalline doomhammer and go positively orc on this town. Then they\'ll be sorry...', 12, 7, 100, 0, 0, 0, 14331, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17768, 1, 0, 'Don\'t get me wrong, $n. I very much appreciate the resurrection, but I think this is the last time I\'ll perform this particular duty. Maybe a goblet or two of wine will help with this headache.', 12, 0, 100, 0, 0, 0, 14368, 0, 'Blood Knight Stillblade');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17862, 3, 1, 'Did you really think you would leave here alive?', 14, 0, 100, 0, 0, 10410, 16676, 0, 'skarloc SAY_SLAY2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17862, 4, 0, 'Guards... guards!', 14, 0, 100, 0, 0, 10411, 16677, 0, 'skarloc SAY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17877, 4, 0, 'Uh oh...', 12, 0, 100, 0, 0, 0, 16443, 0, 'Fhwoor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17877, 5, 0, 'Ha ha, squishy naga!', 12, 0, 100, 0, 0, 0, 16444, 0, 'Fhwoor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17877, 6, 0, '%s places the Ark of Ssslith on the ground.', 16, 0, 100, 0, 0, 0, 16445, 0, 'Fhwoor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17877, 7, 0, 'Fhwoor do good!', 16, 0, 100, 0, 0, 0, 16446, 0, 'Fhwoor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17892, 0, 1, 'Death to the Last Guardian!', 12, 0, 100, 0, 0, 0, 15171, 0, 'Infinite Chronomancer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17892, 0, 2, 'We will not fail!', 12, 0, 100, 0, 0, 0, 23332, 0, 'Infinite Chronomancer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17893, 1, 0, 'Uh oh!  It would appear that all of the noise you\'ve been making has attracted some unwanted attention!', 12, 0, 100, 0, 0, 0, 14575, 0, 'Naturalist Bite');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17918, 0, 1, 'Carry on! Victory at all costs!', 14, 0, 100, 0, 0, 0, 15195, 0, 'Time Keeper');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17918, 0, 2, 'Continue the fight! Do not falter!', 14, 0, 100, 0, 0, 0, 15196, 0, 'Time Keeper');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17959, 2, 0, 'Assist me slaves!', 14, 0, 100, 0, 0, 0, 18972, 0, 'Coilfang Slavehandler');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 15, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_16');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 16, 0, '2...', 12, 0, 100, 0, 0, 0, 14654, 0, 'npc_demolitionist_legoso SAY_LEGOSO_17');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 17, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_18');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 18, 0, 'Holy mother of O\'ros!', 12, 0, 100, 5, 0, 0, 14663, 0, 'npc_demolitionist_legoso SAY_LEGOSO_19');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 19, 0, 'I... I can\'t believe it\'s over. You did it! You\'ve destoyed the blood elves and their leader!', 12, 0, 100, 1, 0, 0, 14664, 0, 'npc_demolitionist_legoso SAY_LEGOSO_20');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (17982, 20, 0, 'Get back to Blood Watch. I\'ll see you there...', 12, 0, 100, 1, 0, 0, 14665, 0, 'npc_demolitionist_legoso SAY_LEGOSO_21');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18096, 0, 1, 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her... ', 14, 0, 100, 0, 0, 10419, 16019, 0, 'epoch hunter SAY_ENTER2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18096, 0, 2, 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so... you and your troublesome friends must die!', 14, 0, 100, 0, 0, 10420, 16021, 0, 'epoch hunter SAY_ENTER3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18096, 1, 1, 'You cannot fight fate!', 14, 0, 100, 0, 0, 10422, 16693, 0, 'epoch hunter SAY_AGGRO2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18096, 2, 1, 'Thrall will remain a slave. Taretha will die. You have failed.', 14, 0, 100, 0, 0, 10426, 16695, 0, 'epoch hunter SAY_SLAY2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 0, 0, 'Lady Liadrin is bound to find a way. She must.', 12, 1, 100, 1, 0, 0, 14838, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 0, 1, 'Is... it true, what I\'ve heard?  That it was the Sun King himself?', 12, 1, 100, 1, 0, 0, 14839, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 0, 2, 'Still, the Blood Knights will endure through this loss. We have no other choice... we must find another source.', 12, 1, 100, 1, 0, 0, 14840, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 1, 0, 'Who do they think they are?', 12, 1, 100, 1, 0, 0, 14820, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 1, 1, 'I know what you mean... they whisper about us when they think we can\'t hear them. They\'ll get theirs.', 12, 1, 100, 1, 0, 0, 14821, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 1, 2, 'Jealous, that\'s what they are. They\'re afraid to dedicate themselves to true power.', 12, 1, 100, 1, 0, 0, 14822, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18174, 1, 3, 'They simply fear what they cannot begin to understand.', 12, 1, 100, 1, 0, 0, 14823, 0, 'Initiate Colin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18175, 0, 0, 'Matters are so uncertain now that the chamber below is empty...', 12, 1, 100, 1, 0, 0, 14831, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18175, 0, 1, 'It\'s gone. I can\'t believe it\'s gone. Who would have thought we\'d be betrayed like this?', 12, 1, 100, 1, 0, 0, 14833, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18175, 1, 0, 'Our neighbors are positively laughable. Have you seen the way they turn up their noses at us?', 12, 1, 100, 1, 0, 0, 14815, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18175, 1, 1, 'The rangers certainly have a high opinion of themselves.', 12, 1, 100, 1, 0, 0, 14818, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18175, 1, 2, 'I\'m quickly tiring of the haughty attitude I receive from those grubby bowslingers outside.', 12, 1, 100, 1, 0, 0, 14819, 0, 'Initiate Emeline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18241, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Crusty');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18248, 0, 0, 'Grunting, %s turns away from the wanted poster, returning to his warm spot by the fire.', 16, 0, 100, 0, 0, 0, 18782, 0, 'Nekthar');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18248, 1, 0, '%s begins to read the wanted poster.', 16, 0, 100, 0, 0, 0, 18781, 0, 'Nekthar');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18340, 0, 0, 'I will show you not to cross us again!', 12, 0, 100, 0, 0, 0, 15202, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18340, 0, 1, 'Warlord Kalithresh will hear of this insolence!', 12, 0, 100, 0, 0, 0, 15201, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18340, 0, 2, 'I\'ll make you pay for that!', 12, 0, 100, 0, 0, 0, 15199, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 0, 0, '%s dons his wolf-fur vestments.', 16, 0, 100, 0, 0, 0, 17039, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 1, 0, 'Wolf spirit of the forest, hear me!', 12, 0, 100, 0, 0, 0, 17044, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 2, 0, 'Long ago, you were a companion to my people. We gave ourselves to evil and you were forgotten.', 12, 0, 100, 0, 0, 0, 17046, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 3, 0, 'But we have returned, spirit! We have returned to honor you again. Favor us with your presence once more.', 12, 0, 100, 0, 0, 0, 17047, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 4, 0, 'May our bond with you never be broken again, spirit.', 12, 0, 100, 0, 0, 0, 17055, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18384, 5, 0, 'We have done it! Thrall would be proud.', 12, 0, 100, 0, 0, 0, 17066, 0, 'Malukaz');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18500, 0, 0, 'Light will save purge us all!', 12, 0, 50, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18584, 0, 0, '[Demonic] Ka kalix!', 12, 0, 100, 0, 0, 0, 15551, 0, 'Sal\'salabim SAY_DEMONIC_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18585, 0, 0, 'Raliq teach you lesson now!', 12, 0, 100, 0, 0, 0, 15567, 0, 'Raliq the Drunk SAY_RALIQ_ATTACK');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18586, 0, 0, 'F\'woooroonn! Coosh\'coosh pay nothing! Die!', 12, 0, 100, 0, 0, 0, 15565, 0, 'Coosh\'coosh');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18810, 0, 0, 'How can you have any pudding if you don\'t eat your fish?', 12, 7, 100, 5, 0, 0, 15941, 0, 'Otonambusi');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18810, 0, 1, 'If you don\'t eat your fish, you can\'t have any pudding!', 12, 7, 100, 5, 0, 0, 15942, 0, 'Otonambusi');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18827, 0, 0, '%s\'s eyes glow red as he begins to cackle madly!.', 16, 7, 100, 0, 0, 0, 16771, 0, 'Gan\'arg Sapper');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18897, 0, 0, 'Welcome, welcome, to the recently appropriated and callously renamed Steamwheedle Stadium!', 12, 0, 100, 0, 0, 0, 16072, 0, '"King" Dond');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18926, 3, 0, 'Once they\'re dealt with we can dedicate more resources to the war effort.', 12, 0, 100, 0, 0, 0, 16092, 0, 'Sleyin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18926, 1, 0, 'Just don\'t ask me where I got it...', 12, 0, 100, 0, 0, 0, 16059, 0, 'Sleyin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18926, 2, 0, 'Something must be done about these Wretched...', 12, 0, 100, 0, 0, 0, 16091, 0, 'Sleyin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18926, 0, 0, 'Having trouble dealing with the Wretched? I\'ve got just what you need.', 12, 0, 100, 0, 0, 0, 16058, 0, 'Sleyin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18929, 5, 0, 'Cakes, pastries, even bread - anything but cheese!', 12, 0, 100, 0, 0, 0, 16098, 0, 'Kyrenna');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18929, 4, 0, 'Why couldn\'t mother have chosen something else to dedicate herself to?', 12, 0, 100, 1, 0, 0, 16097, 0, 'Kyrenna');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18929, 3, 0, 'The hours would pass so much more sweetly.', 12, 0, 100, 0, 0, 0, 16096, 0, 'Kyrenna');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18929, 1, 0, 'Ooh what a dreadful job!', 12, 0, 100, 1, 0, 0, 16094, 0, 'Kyrenna');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18929, 2, 0, 'All day, surrounded by the stench of bad feet.', 12, 0, 100, 0, 0, 0, 16095, 0, 'Kyrenna');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18947, 0, 0, 'Gah! Every time I load something into the wagon, it reappears here within moments!', 12, 0, 100, 6, 0, 0, 16099, 0, 'Solanin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18947, 1, 0, 'I should have better than to have given Silanna that tome of cantrips.', 12, 0, 100, 1, 0, 0, 16100, 0, 'Solanin');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18966, 0, 1, 'With each passing moment, I see the people of Azeroth prove again the reasons we have come. It is with solemn pride that I fight with you, and if I die this day, then I die among friends.', 14, 0, 100, 0, 0, 0, 16633, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18966, 0, 2, 'The demons should be in no doubt that we are their nemesis and, though they wish our destruction, it is we who bring about their ruin. Kazzak and his forces will be diminished and defeated by this coalition. And to do this, we must throw at them our bodies and hearts, for they seek no less than the obliteration of this world.', 14, 0, 100, 0, 0, 0, 16627, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18966, 0, 3, 'Fight on, good people of Azeroth. And take heart! Your brethren rush to this hellish portal and will soon throw themselves at these demons as you have so bravely done. May the Light keep you!', 14, 0, 100, 0, 0, 0, 16933, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18966, 0, 4, 'Behold the power of the Light! Grace and glory to the Alliance!', 14, 0, 100, 0, 0, 0, 16286, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 0, 'Hunters, to me! Let us show these demons what it is to fight and die in Azeroth!', 14, 0, 100, 0, 0, 0, 16616, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 1, 'The spirits sing to us! They sing the song of battle!', 14, 0, 100, 0, 0, 0, 16617, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 2, 'Strike true, brothers and sisters! Today our quarry is demons!', 14, 0, 100, 0, 0, 0, 16618, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 3, 'Once again, we will show this Burning Legion that our world is not for the taking!', 14, 0, 100, 0, 0, 0, 16620, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 4, 'These fiends will fail! For Azeroth!', 14, 0, 100, 0, 0, 0, 16621, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 5, 'If it is our fate to die this day, then we will not go easy! Mark me, these demons will fall like wheat!', 14, 0, 100, 0, 0, 0, 16622, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 6, 'Though your sinews tire and your blood flows from cuts and wounds to this sodden ground, you fight for the spirits of your world. No evil force can stand against us!', 14, 0, 100, 0, 0, 0, 16624, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (18969, 0, 7, 'My brethern, my commands-in-arms! I must go to seek the forces of the Horde and direct them here! Keep your spirits strong, brave hunters! Do not fall to the portal-spawned fiends!', 14, 0, 100, 0, 0, 0, 16935, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19220, 1, 1, 'You work for me now!', 14, 0, 100, 0, 0, 11198, 20040, 0, 'pathaleon SAY_DOMINATION_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19308, 0, 3, 'I regret to inform you all that we sent home another ten soldiers today - in body bags.', 12, 7, 100, 1, 0, 0, 16455, 0, 'Marshal Isildor');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19380, 0, 1, 'If you\'re no good at fixing, we\'ll see if you\'re good for eating. Now WORK!', 12, 1, 100, 5, 0, 0, 16538, 0, 'Guard Untula to 0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19383, 0, 1, 'Please don\'t beat me!', 12, 7, 100, 33, 0, 0, 16541, 0, 'Captured Gnome to Guard Untula');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19392, 0, 0, 'WE\'RE UNDER ATTACK! RUN FOR COVER!', 14, 0, 100, 0, 0, 0, 16575, 0, 'Watch Commander Leonus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19392, 0, 1, 'INFERNALS! CLEAR THE COURTYARD!', 14, 0, 100, 0, 0, 0, 16574, 0, 'Watch Commander Leonus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19392, 0, 2, 'INFERNAL RAIN ON THE RISE! TAKE COVER!', 14, 0, 100, 0, 0, 0, 16573, 0, 'Watch Commander Leonus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19447, 0, 0, 'Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality.', 12, 0, 100, 0, 0, 0, 16769, 0, 'Brother Daniels');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19447, 1, 0, 'Light be with you now and forever.', 12, 0, 100, 0, 0, 0, 16770, 0, 'Brother Daniels');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19447, 2, 0, 'Light, give me strength.', 12, 0, 100, 0, 0, 0, 16767, 0, 'Brother Daniels');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19447, 3, 0, 'The priest kneels in silent prayer.', 16, 0, 100, 0, 0, 0, 16772, 0, 'Brother Daniels');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19477, 0, 1, 'Flee!  Flee!', 12, 0, 100, 0, 0, 0, 13373, 0, 'Dreghood Fleeing Warrior');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19477, 0, 2, 'This is our chance!  Run for it!', 12, 0, 100, 0, 0, 0, 13374, 0, 'Dreghood Fleeing Warrior');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19477, 0, 0, 'The taskmaster\'s dead!  Quick, Dreghoods!  Run!', 12, 0, 100, 0, 0, 0, 13372, 0, 'Dreghood Fleeing Warrior');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19523, 10, 0, 'This one die easy!', 14, 0, 100, 0, 0, 10310, 16922, 0, 'omrogg Killing_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19523, 11, 0, 'That\'s because I do all the hard work!', 14, 0, 100, 0, 0, 10321, 16923, 0, 'omrogg KillingDelay_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19561, 0, 0, 'I\'m blind, you stupid sow-spawn, not deaf. Don\'t even think about it.', 12, 1, 100, 1, 0, 0, 16990, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19569, 0, 0, 'I hope those blood elves across the way don\'t think that I\'m spying on them!', 12, 0, 100, 5, 0, 0, 16952, 0, 'Netherologist Coppernickels');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (19569, 1, 0, 'If I were smart, I\'d be selling this research for a seat on the rocket ship.  I have a feeling we\'re going to need to get out of here soon!', 12, 0, 100, 5, 0, 0, 16955, 0, 'Netherologist Coppernickels');
