INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 1, 0, 'Guards, go already! Who you more afraid of, dem... or me?', 14, 0, 100, 0, 0, 12067, 22146, 1, 'Nalorakk - YELL_NALORAKK_WAVE2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 2, 0, 'Ride now! Ride out dere and bring me back some heads!', 14, 0, 100, 0, 0, 12068, 22151, 1, 'Nalorakk - YELL_NALORAKK_WAVE3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 3, 0, 'I be losin\' me patience! Go on: make dem wish dey was never born!', 14, 0, 100, 0, 0, 12069, 22155, 1, 'Nalorakk - YELL_NALORAKK_WAVE4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 4, 0, 'You be dead soon enough!', 14, 0, 100, 0, 0, 12070, 23166, 1, 'Nalorakk - YELL_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 5, 0, 'I bring da pain!', 14, 0, 100, 0, 0, 12071, 23167, 1, 'Nalorakk - YELL_SURGE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 6, 0, 'You call on da beast, you gonna get more dan you bargain for!', 14, 0, 100, 0, 0, 12072, 23168, 1, 'Nalorakk - YELL_SHIFTEDTOBEAR ');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 7, 0, 'Make way for da Nalorakk!', 14, 0, 100, 0, 0, 12073, 23169, 1, 'Nalorakk - YELL_SHIFTEDTOTROLL');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 8, 0, 'You had your chance; now it be too late!', 14, 0, 100, 0, 0, 12074, 23170, 1, 'Nalorakk - YELL_BERSERK');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 9, 0, 'Now whatchoo got to say?', 14, 0, 100, 0, 0, 12075, 23171, 1, 'Nalorakk - YELL_KILL_ONE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 10, 0, 'Da Amani gonna rule again!', 14, 0, 100, 0, 0, 12076, 23172, 1, 'Nalorakk - YELL_KILL_TWO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 11, 0, 'I... be waitin\' on da udda side....', 14, 0, 100, 0, 0, 12077, 23173, 1, 'Nalorakk - YELL_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 12, 0, 'What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me!', 14, 0, 100, 0, 0, 12078, 23305, 1, 'Nalorakk - YELL_NALORAKK_EVENT1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23576, 13, 0, 'I smell you, strangers. Don\'t be delayin\' your fate. Come to me now. I make your sacrifice quick.', 14, 0, 100, 0, 0, 12079, 23306, 1, 'Nalorakk - YELL_NALORAKK_EVENT1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23780, 0, 0, 'Archers at the ready!  Hold your fire!', 14, 0, 100, 0, 0, 0, 22662, 0, 'High Executor Anselm');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23784, 0, 0, 'You couldn\'t have come at a better time!  Let\'s get out of here.', 12, 1, 100, 0, 0, 0, 22485, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23784, 1, 0, 'Yes, let us leave... but not before we leave our Alliance hosts something to remember us by!', 12, 1, 100, 0, 0, 0, 22486, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23784, 2, 0, 'They have limited supplies in this camp.  It would be a real shame if something were to happen to them.', 12, 1, 100, 0, 0, 0, 22487, 0, 'Apothecary Hanes');
