INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23843, 3, 0, 'Jarl, you haven\'t been yourself ever since I arrived. What\'s going on?', 12, 0, 100, 1, 0, 0, 22352, 0, 'Mordant Grimsby');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23843, 4, 0, 'I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless!', 12, 0, 100, 5, 0, 0, 22354, 0, 'Mordant Grimsby');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23844, 0, 0, 'Is he okay?', 12, 0, 100, 1, 0, 0, 23105, 0, 'Westguard Officer - Say');
