INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23872, 1, 0, 'Are we going to hide in our mountain and let those swill-peddlers have their little shindig without us?!', 12, 0, 100, 15, 0, 0, 22388, 0, 'Coren Direbrew SAY_INTRO2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23872, 2, 0, 'DAMN RIGHT! We\'ll show \'em why you don\'t cross the Dark Iron dwarves!', 12, 0, 100, 15, 0, 0, 22389, 0, 'Coren Direbrew SAY_INTRO3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23872, 3, 0, 'You\'ll pay for this insult, $c!', 12, 0, 100, 15, 0, 0, 25921, 0, 'Coren Direbrew SAY_INSULT');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (23941, 0, 0, 'I surrender! Stay your blade!', 12, 0, 100, 5, 0, 0, 22469, 0, 'Gavis Greyshield');
