INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27054, 0, 0, '%s beams, unaware that she is teasing him, answering in his thickly-accented Common.', 16, 0, 100, 0, 0, 0, 29550, 0, 'Modoru');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27054, 1, 0, 'Well! You see, was gift from Grandmother Oluba. She hand-weave from finest silk on Draenor! Am quite proud of, yes.', 12, 7, 100, 0, 0, 0, 29552, 0, 'Modoru');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27055, 0, 0, '%s huffs with annoyance, glaring briefly at Sentinel Sweetspring before turning away with her nose in the air.', 16, 0, 100, 0, 0, 0, 29557, 0, 'Sentinel Amberline');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 0, 0, '%s grins at Sentinel Amberline across the camp, waggling her long eyebrows suggestively.', 16, 0, 100, 0, 0, 0, 29555, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 1, 0, '%s peers at Modoru\'s Draenic robes.', 16, 0, 100, 0, 0, 0, 27085, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 2, 0, 'Pssst. Naohain. Why does Modoru still wear those ridiculous robes? He looks like some interdimensional bumpkin who stumbled out of Ammen Vale not six hours past.', 12, 2, 100, 0, 0, 0, 27086, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 3, 0, 'Say, Modoru. Where did you get those... ah... "wonderful" robes of yours, hmmm?', 12, 7, 100, 0, 0, 0, 29546, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 4, 0, 'Oh. Oh... I see. How nice!', 12, 7, 100, 0, 0, 0, 29547, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27056, 5, 0, '%s bites back a laugh.', 16, 0, 100, 0, 0, 0, 29548, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 0, 0, 'Let me take care of it, sister.', 12, 0, 100, 0, 0, 0, 27108, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 1, 0, 'What were you thinking, provoking her like that?', 15, 0, 100, 0, 0, 0, 27109, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 2, 0, 'I used good judgment when you showed none.  I don\'t want to see you lead us into ruin, Krenna!', 12, 0, 100, 0, 0, 0, 27027, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 3, 0, 'We\'ll see about that.', 12, 0, 100, 0, 0, 0, 27028, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 4, 0, 'You... half-idiot!  You really did it this time.  I\'m going to have to make it look like your leg\'s broken.', 12, 0, 100, 0, 0, 0, 27110, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27102, 5, 0, 'Now don\'t move if you ever want to walk again!', 12, 0, 100, 0, 0, 0, 27111, 0, 'Gorgonna to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27119, 0, 0, '%s snickers, nuzzling into Sentinel Sweetspring\'s ankles.', 16, 0, 100, 0, 0, 0, 29553, 0, 'Snowpaw');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27247, 0, 0, 'Hey, who rang the bell?', 12, 0, 100, 0, 0, 0, 26610, 0, 'Devout Bodyguard');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 1, 0, 'You need to learn to appreciate your surroundings, Cersei!', 12, 0, 100, 5, 0, 0, 27007, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 5, 'To this day, I ponder why I, myself, was chosen for this particular station. I can only imagine my superior felt I was uniquely suited to the task and would quickly become accustomed to the quaint culture of the Horde found herein, but I heard a truly mean-spirited rumor that it may be because I talk too much.', 12, 0, 100, 6, 0, 0, 26997, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 4, 'Before I was stationed out here, I\'d only heard about the rejuvenating effects a mudbath can have on skin. When I first tripped over a tree root, I was exposed to the full benefits! Amazing what nature can do, even without a bit of magic to help it along.', 12, 0, 100, 1, 0, 0, 26996, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 3, 'Here I am, in a rough-hewn stone tower squatting on marshy, muddy ground while clouds of midges fly unchecked through the open doorways, standing amongst hunched, greenskin figures of dubious hygiene and coarse manners. I\'ve never been happier.', 12, 0, 100, 1, 0, 0, 26995, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 1, 'While visitors complain about the exceptionally humid air, I find it helps my complexion! Dry skin is unflattering.', 12, 0, 100, 1, 0, 0, 26993, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 2, 'It is a shame, a dreadful shame, that my brethren cannot see what benefits come from truly steeping oneself in a culture.', 12, 0, 100, 6, 0, 0, 26994, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (27705, 0, 0, 'Ah, I love the smell of Stonard! It smells like... like... orc sweat. And rot. Still, combined, the effect can be quite pleasant! It\'s an acquired scent.', 12, 0, 100, 1, 0, 0, 26992, 0, 'Lorrin Foxfire');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28006, 0, 1, 'Behold! The Scythe of Antiok!', 14, 0, 100, 0, 0, 0, 27416, 0, 'Antiok Yell2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28006, 1, 1, 'The Lich King demands more frost wyrms be sent to Angrathar! Meet his demands or face my wrath!', 14, 0, 100, 0, 0, 0, 27408, 0, 'Antiok Yell4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28006, 1, 2, 'Faster, dogs! We mustn\'t relent in our assault against the interlopers!', 14, 0, 100, 0, 0, 0, 27405, 0, 'Antiok Yell5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28006, 1, 3, 'Attackers are upon us! Let none through to this ancient grave!', 14, 0, 100, 0, 0, 0, 27409, 0, 'Antiok Yell6');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28006, 1, 4, 'Hear me, minions! Hear your lord, Antiok! Double your efforts or pay the consequences of failure!', 14, 0, 100, 0, 0, 0, 27407, 0, 'Antiok Yell7');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28115, 0, 0, 'You feel a massive jolt of energy as your body binds with that of Soo-holu.', 42, 0, 100, 0, 0, 0, 29003, 0, 'Soo-holu to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28116, 0, 0, 'You feel rage rise within you as your body binds with Kartak.', 42, 0, 100, 0, 0, 0, 29005, 0, 'Kartak the Abominable to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28139, 0, 0, 'I wanna see!', 12, 0, 100, 396, 0, 0, 29461, 0, 'Frenzyheart Pup');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28139, 1, 0, 'Show me too!', 12, 0, 100, 1, 0, 0, 29462, 0, 'Frenzyheart Pup');
