INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 0, 1, 'Nobody steals Kitrik\'s horses!', 12, 0, 100, 0, 0, 0, 28676, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 0, 2, 'You\'ll be hanging at the gallows soon...', 12, 0, 100, 0, 0, 0, 28677, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 0, 3, 'How many more of you do I need to send back to the Lich King in pieces before he\'ll stop?', 12, 0, 100, 0, 0, 0, 28678, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 1, 0, 'And keep the change, ya filthy animal!', 12, 0, 100, 0, 0, 0, 28702, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 1, 1, 'Perhaps you\'ll think twice about stealin\' one of Kitrik\'s horses next time, eh?', 12, 0, 100, 0, 0, 0, 28703, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 1, 2, 'Is that all you got?', 12, 0, 100, 0, 0, 0, 28704, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 1, 3, 'What a colossal waste of time.', 12, 0, 100, 0, 0, 0, 28705, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28683, 1, 4, 'At least make me work for it next time.', 12, 0, 100, 0, 0, 0, 28706, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28709, 0, 0, 'Mee likee SHINYYY!', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28750, 0, 2, 'Mhrrumph rummrhum phurr!', 12, 0, 100, 1, 0, 0, 28757, 0, 'Blight Geist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28750, 0, 3, 'Umphrum mmphur, murruphru...', 12, 0, 100, 0, 0, 0, 28658, 0, 'Blight Geist');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 6, 1, 'We made it! Thank you for getting me out of that hell hole. Tell Hemet to expect me!', 12, 0, 100, 0, 0, 0, 28865, 0, 'engineer helice SAY_WP_7');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 5, 1, 'You really shouldn\'t play with this stuff. Someone could get hurt.', 12, 0, 100, 0, 0, 0, 28864, 0, 'engineer helice SAY_WP_6');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 4, 1, 'Oh, look, it\'s another cartload of explosives! Let\'s help them dispose of it.', 12, 0, 100, 0, 0, 0, 28862, 0, 'engineer helice SAY_WP_5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 2, 1, 'Or THIS is what you get.', 12, 0, 100, 0, 0, 0, 28859, 0, 'engineer helice SAY_WP_3');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 1, 1, 'Listen up, Venture Company goons! Rule #1: Never keep the prisoner near the explosives.', 12, 0, 100, 0, 0, 0, 28857, 0, 'engineer helice SAY_WP_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28787, 0, 1, 'Let\'s get the hell out of here.', 12, 0, 100, 0, 0, 0, 28856, 0, 'engineer helice SAY_WP_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28845, 0, 0, 'Die $N, die!', 12, 0, 50, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28860, 7, 3, 'All will be reduced to ash!', 14, 0, 100, 0, 0, 14102, 31906, 0, 'sartharion SAY_SARTHARION_SPECIAL_4');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28913, 2, 0, 'But in death... We are the children of the damned. The orphaned sons and daughters of the Scourge.', 12, 0, 100, 1, 0, 0, 29194, 0, 'Tharassian');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28913, 0, 0, 'What, Orbaz? That he\'s a blood elf?', 12, 0, 100, 1, 0, 0, 29192, 0, 'Tharassian');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28913, 1, 0, 'In life we were hated enemies - this is true...', 12, 0, 100, 1, 0, 0, 29193, 0, 'Tharassian');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28913, 3, 0, 'In death we are brothers.', 12, 0, 100, 1, 0, 0, 29195, 0, 'Tharassian');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28914, 1, 0, 'Why do you care, Thassarian?', 12, 0, 100, 1, 0, 0, 29189, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28914, 2, 0, 'His weakness led to his capture.', 12, 0, 100, 1, 0, 0, 29190, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28914, 3, 0, 'Only the strong should survive. Not to mention...', 12, 0, 100, 1, 0, 0, 29191, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28914, 4, 0, 'To hell with you, Thassarian.', 12, 0, 100, 25, 0, 0, 29196, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (28921, 1, 0, '%s moves up the tunnel!', 41, 0, 100, 0, 0, 0, 29852, 3, 'Hadronox HADRONOX_EMOTE_MOVE');
