INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31412, 5, 0, 'If we are forced into a conflict, the Lich King will destroy our divided forces in Northrend.', 12, 0, 100, 378, 0, 16227, 32294, 0, 'Thrall - herald of war');
