INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31412, 7, 0, 'Kor\'kron, prepare transport to the Undercity.', 12, 0, 100, 378, 0, 16229, 32300, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31418, 0, 0, 'Thrall, what has happened? The King is preparing for war...', 12, 0, 100, 0, 0, 16129, 32288, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31418, 1, 0, 'I will deliver this information to King Wrynn, Thrall, but...', 12, 0, 100, 378, 0, 16130, 32296, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31418, 2, 0, 'Bolvar was like a brother to him. In the King\'s absence, Bolvar kept the Alliance united. He found strength for our people in our darkest hours. He watched over Anduin, raising him as his own.', 12, 0, 100, 378, 0, 16131, 32297, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31418, 3, 0, 'I fear that the rage will consume him, Thrall. I remain hopeful that reason will prevail, but we must prepare for the worst... for war.', 12, 0, 100, 378, 0, 16132, 32298, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31418, 4, 0, 'Farewell, Warchief. I pray that the next time we meet it will be as allies.', 12, 0, 100, 0, 0, 16133, 32299, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31419, 0, 0, 'Lady Proudmoore, the Warchief speaks the truth. This subterfuge was set in motion by Varimathras and Grand Apothecary Putress. It was not the Horde\'s doing.', 12, 0, 100, 0, 0, 16315, 32290, 0, 'Sylvanas - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31419, 1, 0, 'As the combined Horde and Alliance forces began their assault upon the Wrath Gate, an uprising broke out in the Undercity. Varimathras and hordes of his demonic brethren attacked. Hundreds of my people were slain in the coup. I barely managed to escape with my life.', 12, 0, 100, 0, 0, 16316, 32291, 0, 'Sylvanas - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31423, 0, 0, 'Why is Thrall allowing this to happen to our city?', 12, 0, 100, 0, 0, 0, 32239, 0, 'Kaja - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31425, 0, 0, 'Where is the Warchief?', 12, 0, 100, 0, 0, 0, 32236, 0, 'olvia - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31425, 1, 0, 'What are all these Forsaken doing here?', 12, 0, 100, 0, 0, 0, 32233, 0, 'olvia - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31427, 0, 0, 'What is going on?', 12, 0, 100, 0, 0, 0, 45914, 0, 'felika - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31427, 1, 0, 'This is an outrage!', 12, 0, 100, 0, 0, 0, 46620, 0, 'felika - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31428, 1, 0, 'Look! What\'s a frostwyrm doing here? It\'s coming straight for us.', 12, 0, 100, 25, 0, 0, 32173, 0, 'Crusader Olakin Sainrith to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31429, 0, 0, 'We demand answers!', 12, 0, 100, 0, 0, 0, 32235, 0, 'Sana - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31430, 0, 0, 'What is going on?', 12, 0, 100, 0, 0, 0, 32232, 0, 'auctioneer thathung - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31431, 0, 0, 'SILENCE!', 12, 0, 100, 0, 0, 0, 32240, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31431, 1, 0, 'We are on the brink of all out war with the Alliance!', 12, 0, 100, 0, 0, 0, 32241, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31431, 2, 0, 'Tragic events have unfolded in Northrend. The Warchief is doing all that he can to keep us safe.', 12, 0, 100, 0, 0, 0, 32242, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31431, 3, 0, 'All services and shops are to remain closed until further notice! That is all!', 12, 0, 100, 0, 0, 0, 32243, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31432, 0, 0, 'Destroy our raw materials, will you? Well, the master has a special plan in mind for you... ', 12, 0, 100, 460, 0, 0, 32171, 0, 'Ghostwing to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31433, 0, 0, 'When can we reopen our shops? I\'m losing gold here!', 12, 0, 100, 0, 0, 0, 32237, 0, 'inkeeper gryshka - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31433, 1, 0, 'This is an outrage!', 12, 0, 100, 0, 0, 0, 32238, 0, 'inkeeper gryshka - herald of war');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (31892, 0, 0, '%s makes a strange noise that translates as laughter in your mind.', 16, 0, 100, 0, 0, 0, 32533, 0, 'Darkspeaker R\'khem');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 0, 0, 'An elite Blue Dragonflight squad appears from the portal!', 41, 0, 100, 0, 0, 0, 32994, 3, 'Lieutenant Sinclari - Blue Dragonflight squad');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 1, 0, 'A Portal Guardian defends the new portal!', 41, 0, 100, 0, 0, 0, 32995, 3, 'Lieutenant Sinclari - Portal Guardian');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 2, 0, 'A Portal Keeper emerges from the portal!', 41, 0, 100, 0, 0, 0, 32996, 3, 'Lieutenant Sinclari - Portal Keeper');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 3, 0, 'Adventurers, the door is beginning to weaken!', 14, 0, 100, 0, 0, 0, 32557, 3, 'Lieutenant Sinclari - Door weaken');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 4, 0, 'Only half of the door seal\'s strength remains! You must fight on!', 14, 0, 100, 0, 0, 0, 32558, 3, 'Lieutenant Sinclari - Half Door Seal reains');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32204, 5, 0, 'The door seal is about to collapse! All is lost if the Blue Dragonflight breaks through the door!', 14, 0, 100, 0, 0, 0, 32559, 3, 'Lieutenant Sinclari - Door Seal broken');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32236, 1, 2, 'NOOOOOO!', 12, 0, 100, 0, 0, 0, 32634, 0, 'Dark Subjugator');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32311, 0, 0, 'Take courage, crusaders.  You do not fight alone!', 14, 0, 100, 0, 0, 0, 32708, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32311, 1, 0, 'The Lich King is badly hurt.  We ought to stay behind and finish him.', 12, 0, 100, 0, 0, 0, 32725, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 1, 0, 'Whoa! You\'re me, but from the future! Hey, my equipment got an upgrade! Cool!', 15, 0, 100, 396, 0, 0, 32764, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 2, 0, 'Here come the Infinites! I\'ve got to keep the hourglass safe. Can you help?', 15, 0, 100, 396, 0, 0, 32769, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 3, 0, 'This equipment looks cool and all, but couldn\'t we have done a little better? Are you even raiding?', 15, 0, 100, 396, 0, 0, 32770, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 4, 0, 'So, how does it all turn out in Icecrown?', 15, 0, 100, 396, 0, 0, 32771, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 5, 0, 'It might help if you could tell me how many of these guys we\'re going to fight.  You can remember that, right, grandpa?', 15, 0, 100, 396, 0, 0, 32775, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 6, 0, 'Chromie said that if I don\'t do this just right, I might wink out of existence. If I go, then you go!', 15, 0, 100, 396, 0, 0, 32772, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 7, 0, 'I just want you to know that if we get through this alive, I\'m making sure that we turn out better than you. No offense.', 15, 0, 100, 396, 0, 0, 32776, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 8, 0, 'Looks like I\'m an underachiever.', 15, 0, 100, 396, 0, 0, 32773, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 9, 0, 'Wait a minute! If you\'re here, then that means that in the not-so-distant future I\'m going to be you helping me? Are we stuck in a time loop?!', 15, 0, 100, 396, 0, 0, 32777, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 10, 0, 'I think I\'m going to turn to drinking after this.', 15, 0, 100, 396, 0, 0, 32774, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 11, 0, 'What the heck? Nozdormu is up there!', 15, 0, 100, 396, 0, 0, 32781, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32331, 12, 0, 'I feel like I\'m being pulled away through time. Thanks for the help....', 15, 0, 100, 396, 0, 0, 32782, 0, 'Past You');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32706, 0, 0, 'Anar\'alah belore $N!', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32714, 0, 0, 'Elune bless you $N.', 12, 0, 100, 0, 0, 0, 0, 0, 'Dalaran priest');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32718, 0, 0, 'Be well $N.', 12, 0, 50, 0, 0, 0, 0, 0, 'Dalaran shamy');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32719, 0, 0, 'How you be doin\' it, mon!', 12, 0, 50, 0, 0, 0, 0, 0, 'Dalaran Troll');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32720, 0, 0, 'Good to see you $N.', 12, 0, 50, 0, 0, 0, 0, 0, 'Dalaran mage');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32751, 0, 0, '...', 12, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32800, 1, 0, 'The Light\'s blessing be upon you for aiding me in my time of need, $N.', 12, 0, 100, 0, 0, 0, 31941, 0, 'Father Kamaros to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32800, 2, 0, 'I\'ve had my fill of this place. Let us depart.', 12, 0, 100, 1, 0, 0, 31942, 0, 'Father Kamaros to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32800, 3, 0, 'You must tell my brothers that I live.', 12, 0, 100, 1, 0, 0, 31944, 0, 'Father Kamaros to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32800, 4, 0, 'I have you to thank for my life. I will return to my comrades and spread word of your bravery. Fight the Scourge with all the strength you can muster, and we will be by your side.', 12, 0, 100, 2, 0, 0, 31943, 0, 'Father Kamaros to Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32820, 0, 0, 'Turkey Hunter!', 42, 0, 100, 0, 0, 0, 33163, 0, 'Wild Turkey');
