INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32838, 0, 0, 'Don\'t you hate it when you get punked by a GNOME?!', 12, 0, 100, 0, 0, 0, 0, 0, 'Manabonk');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32865, 4, 1, 'Pathetic.', 14, 0, 100, 0, 0, 15740, 35768, 0, 'Thorim SAY_SLAY_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32872, 0, 0, '%s surrounds itself with a crackling Runic Barrier!', 41, 0, 100, 0, 0, 0, 33267, 0, 'Runic Colossus');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (32873, 0, 0, '%s fortifies nearby allies with runic might!', 41, 0, 100, 0, 0, 0, 33523, 0, 'Ancient Rune Giant');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (33186, 2, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 0, 0, 34057, 0, 'Razorscale EMOTE_BERSERK');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (33196, 0, 0, 'Thorim, my lord!  Why else would these invaders have come into your sanctum but to slay you?  They must be stopped!', 14, 0, 100, 0, 0, 15668, 33325, 0, 'Sif - Start');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (33196, 1, 0, 'These pathetic mortals are harmless, beneath my station.  Dispose of them!', 14, 0, 100, 0, 0, 15669, 33368, 0, 'Sif - Despawn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (33196, 2, 0, 'Impossible!  Lord Thorim, I will bring your foes a frigid death!', 14, 0, 100, 0, 0, 15670, 33369, 0, 'Sif - Event');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (33686, 4, 0, 'Entity designate: Yogg-Saron. Security has been compromised. Prison operational status unknown. Unable to contact Watchers for notification purposes.', 12, 0, 0, 0, 0, 0, 33711, 0, 'Norgannon SAY_EVENT_5');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 2, 0, 'These are dark and difficult times for us all. The armies of the Scourge amass to the south. Companions fall on the battlefield and rise to serve our enemy moments later.', 12, 0, 100, 0, 0, 0, 34194, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 3, 0, 'Many of you have experienced a betrayal of trust.', 12, 0, 100, 0, 0, 0, 34196, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 4, 0, 'Yet you persevere, as we all must. In these times, no one is a stranger to the virtue of tenacity. In the face of despair and hatred, we continue to stand true to our beliefs and fight on.', 12, 0, 100, 0, 0, 0, 34197, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 5, 0, 'There can be no doubt as to your tenacity... but there are other virtues to consider.', 12, 0, 100, 0, 0, 0, 34199, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 6, 0, 'Respect is the first virtue taught to those joining the path of the Light.', 12, 0, 100, 0, 0, 0, 34200, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 7, 0, 'Look across the aisle to those you would call enemy. Despite the hatred that separates you, there are qualities to respect in your opponent. Their prowess should be acknowledged.', 12, 0, 100, 0, 0, 0, 34201, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 8, 0, 'All of us gathered here have a concept of honorable combat. Conducting yourself with honor and treating your opponent as you wish to be treated shows respect.', 12, 0, 100, 0, 0, 0, 34202, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 9, 0, 'Respect your opponent on the field, whether he be your brother or your sworn enemy. No matter where your beliefs truly lie, such respect is acknowledging a connection...', 12, 0, 100, 0, 0, 0, 34203, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 11, 0, 'There is one further step on the path of Light: compassion.', 12, 0, 100, 0, 0, 0, 34206, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 12, 0, 'Rivalries abound in this day and age. It is easy to see differences wherever you turn.', 12, 0, 100, 0, 0, 0, 34207, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 13, 0, 'The challenge is in looking beyond appearances and understanding our similarities. Through this understanding, you can feel compassion for the losses others have suffered...', 12, 0, 100, 0, 0, 0, 34208, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 14, 0, '...even if the victim bears another banner.', 12, 0, 100, 0, 0, 0, 34209, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 15, 0, 'By feeling and understanding compassion in enemy and ally alike, you reaffirm your connection with the world.', 12, 0, 100, 0, 0, 0, 34210, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 16, 0, 'Acting on these tenets, these virtues, help make the world a place we all can appreciate. A world of honor and justice.', 12, 0, 100, 0, 0, 0, 34211, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 17, 0, 'Think on my words, friends. In the darkness that surrounds us all, the Light is needed more than ever.', 12, 0, 100, 0, 0, 0, 34212, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 18, 0, 'I\'ll be behind the confessional screen if anyone wishes to speak with me. Any doubts, any worries, any uncertainties I will happily soothe.', 12, 0, 100, 0, 0, 0, 34216, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 19, 0, 'Take heart. Size isn\'t what matters, but how you use the skills you have.', 12, 0, 100, 0, 0, 0, 34250, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 24, 0, 'Besides... while an axe can be a valuable tool in war, lances have their uses as well.', 12, 0, 100, 0, 0, 0, 34287, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 25, 0, 'It may seem pointless, but try to find pride in a good fight. The time will come when all this practicing and competition will be put to good use.', 12, 0, 100, 0, 0, 0, 34292, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 26, 0, 'And your hair looks wonderful.', 12, 0, 100, 0, 0, 0, 34293, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 27, 0, 'Your heart is in the right place, and I know you seek to serve the Light. But these small strikes do little to the great undead host.', 12, 0, 100, 0, 0, 0, 34264, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 28, 0, 'This tournament is helping to form a strong, concerted force, though it may not appear obvious. When we\'re ready, the eyes of the Crusade and all its champions will turn towards Icecrown.', 12, 0, 100, 0, 0, 0, 34265, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34102, 29, 0, 'We will bring the might of all races to the Lich King, and he will be defeated.', 12, 0, 100, 0, 0, 0, 34266, 0, 'Argent Confessor Paletress');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34320, 0, 0, 'The %s\'s strong jaws tear into the food.', 16, 0, 100, 0, 0, 0, 34521, 0, 'Venomhide Hatchling');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34320, 0, 1, 'The %s quickly finishes the food and glances around for more.', 16, 0, 100, 0, 0, 0, 34522, 0, 'Venomhide Hatchling');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34320, 0, 2, 'The %s devours the food.', 16, 0, 100, 0, 0, 0, 34519, 0, 'Venomhide Hatchling');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34320, 0, 3, 'The %s catches the food in its mouth and swallows it in one gulp.', 16, 0, 100, 0, 0, 0, 34520, 0, 'Venomhide Hatchling');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34496, 6, 1, 'Unworthy!', 14, 0, 100, 0, 0, 16276, 35740, 0, 'Eydis Darkbane - Killing a player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34497, 6, 1, 'Unworthy!', 14, 0, 100, 0, 0, 16276, 35740, 0, 'Fjola Lightbane - Killing a player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 0, 0, 'Talk to me, Tahu. Something. Anything! I\'m going stir-crazy.', 12, 0, 100, 0, 0, 0, 34762, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 1, 0, 'Not much.', 12, 0, 100, 0, 0, 0, 34763, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 2, 0, 'Like Mu\'sha.', 12, 0, 100, 0, 0, 0, 34764, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 3, 0, 'Where are you going with this?', 12, 0, 100, 6, 0, 0, 34765, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 4, 0, 'I hear the "but" in your voice...', 12, 0, 100, 0, 0, 0, 34766, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 5, 0, 'Do you think his teachings are wrong?', 12, 0, 100, 6, 0, 0, 34767, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 6, 0, 'So are you going to bring this up to the elder?', 12, 0, 100, 0, 0, 0, 34768, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 7, 0, 'I suppose so. It\'s not silly, though, what you said.', 12, 0, 100, 1, 0, 0, 34769, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 8, 0, 'I see that thoughtful frown, Tahu.', 12, 0, 100, 0, 0, 0, 34770, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 9, 0, 'But something is on your mind, right?', 12, 0, 100, 6, 0, 0, 34771, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 10, 0, 'What about it?', 12, 0, 100, 0, 0, 0, 34772, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 11, 0, 'There\'s a phrase... "It\'s always darkest before the dawn." The dawn will come, though, Tahu. The sun will rise. The balance will shift back to where it should be.', 12, 0, 100, 1, 0, 0, 34773, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 12, 0, '%s smiles and gestures to the north.', 16, 0, 100, 25, 0, 0, 34774, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 13, 0, 'Even on the glacier itself, the sun manages to peek through the gloom. I have hope, Tahu. I think we\'ll make it through this, no matter how dire it looks.', 12, 0, 100, 1, 0, 0, 34775, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 14, 0, 'But I\'d still rather be there on the front lines helping it happen rather than trying to placate myself with belief. We can\'t just sit around and hope it happens.', 12, 0, 100, 0, 0, 0, 34776, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34526, 15, 0, 'And through that hope, strengthen our side in the fight?', 12, 0, 100, 0, 0, 0, 34777, 0, 'Aponi Brightmane');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 0, 0, '%s laughs softly.', 16, 0, 100, 0, 0, 0, 34745, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 1, 0, 'All right, Aponi. I\'ve enough on my mind to share. Have you ever spoken to the elves of Moonglade?', 12, 0, 100, 1, 0, 0, 34746, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 2, 0, 'The elves speak of a moon goddess, did you know? They put great stock in the light given by the moon.', 12, 0, 100, 0, 0, 0, 34747, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 3, 0, 'Just like her. The parallels I\'ve heard are interesting. And it\'s no secret all druids, Shu\'halo and elf alike, can call upon Mu\'sha\'s light.', 12, 0, 100, 273, 0, 0, 34748, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 4, 0, 'I wonder. Hamuul has guided us well, and I\'ve learned so much from him. The legends say that our people were druids when time began...', 12, 0, 100, 1, 0, 0, 34749, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 5, 0, '...but what Hamuul teaches is what the elves know. The night elves. They put such stock in their moon goddess, as creatures of the night.', 12, 0, 100, 0, 0, 0, 34750, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 6, 0, 'No! No, nothing like that. He is an elder for good reason, sister. Mu\'sha is one of the Earthmother\'s eyes, and she watches over us. That isn\'t sinister.', 12, 0, 100, 274, 0, 0, 34751, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 7, 0, 'But we\'re nothing if not people who strive for balance. Our warriors fight only when there is need. Our hunters take only what the tribes require to live, and use all they can when they do. The shaman stand as guide and mediator to the elemental spirits.', 12, 0, 100, 0, 0, 0, 34753, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 8, 0, 'And while we, as druids, are guardians of nature, I wonder if we\'ve overlooked a key aspect of balance in all things.', 12, 0, 100, 1, 0, 0, 34754, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 9, 0, 'No, no. No need for him to trouble about a student\'s idle philosophizing while he entertains a friend.', 12, 0, 100, 274, 0, 0, 34755, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 10, 0, 'Well, it isn\'t exactly a new thought, sister.', 12, 0, 100, 6, 0, 0, 34756, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 11, 0, 'Sorry, sister. It\'s nothing to worry about.', 12, 0, 100, 0, 0, 0, 34778, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 12, 0, 'I\'m thinking about the front to the north. The one you\'re so eager to return to.', 12, 0, 100, 1, 0, 0, 34779, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 13, 0, 'I know I\'m counseling patience, Aponi, but I don\'t like remaining here any more than you do. Times are bleak, and failing to act only makes me worry that my idle hand may have been the one to turn the tide.', 12, 0, 100, 0, 0, 0, 34780, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 14, 0, 'Still... there is balance in all things, even death. I simply hate the concept that such destruction and darkness might be necessary.', 12, 0, 100, 1, 0, 0, 34781, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 15, 0, 'I know, sister. I know. We\'ll return to fighting one day. For now, though, maybe it\'s best to spread the word to others... give them the hope you have.', 12, 0, 100, 1, 0, 0, 34783, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34528, 16, 0, 'That\'s the idea.', 12, 0, 100, 273, 0, 0, 34784, 0, 'Tahu Sagewind');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 0, 0, 'Thank you, good herald. Your words are too kind.', 12, 0, 100, 2, 0, 16245, 35762, 0, 'Argent Confessor Paletress - SAY_INTRO_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 1, 0, 'May the Light give me strength to provide a worthy challenge.', 12, 0, 100, 16, 0, 16246, 35763, 0, 'Argent Confessor Paletress - SAY_INTRO_2');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 2, 0, 'Well, then. Let us begin.', 14, 0, 100, 0, 0, 16247, 35229, 0, 'Argent Confessor Paletress - SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 3, 0, 'Take this time to consider your past deeds.', 14, 0, 100, 0, 0, 16248, 35764, 0, 'Argent Confessor Paletress - SAY_MEMORY_SUMMON');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 4, 0, 'Even the darkest memory fades when confronted!', 14, 0, 100, 0, 0, 16249, 35231, 0, 'Argent Confessor Paletress - SAY_MEMORY_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 5, 0, 'Take your rest.', 14, 0, 100, 0, 0, 16250, 35765, 0, 'Argent Confessor Paletress - SAY_KILL_PLAYER');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 5, 1, 'Be at ease.', 14, 0, 100, 0, 0, 16251, 35766, 0, 'Argent Confessor Paletress - SAY_KILL_PLAYER');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (34928, 6, 0, 'Excellent work!', 14, 0, 100, 0, 0, 16252, 35232, 0, 'Argent Confessor Paletress - SAY_DEFEATED');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35004, 0, 0, 'Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure!', 12, 0, 100, 0, 0, 8574, 35542, 0, 'Jaeren Sunsworn - SAY_JAEREN_EADRIC_INTRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35005, 0, 0, 'Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure!', 14, 0, 100, 0, 0, 8574, 35542, 0, 'Arelas Brightstar - SAY_EADRIC_INTRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35119, 0, 0, 'Are you up to the challenge? I will not hold back.', 12, 0, 100, 397, 0, 16134, 35347, 0, 'Eadric the Pure - SAY_INTRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35119, 1, 0, 'Prepare yourselves!', 14, 0, 100, 0, 0, 16135, 35758, 0, 'Eadric the Pure - SAY_AGGRO');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35119, 2, 0, '%s begins to radiate light. Shield your eyes!', 41, 0, 100, 0, 0, 0, 35415, 0, 'Eadric the Pure - EMOTE_RADIANCE');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35119, 3, 0, '%s targets $n with the Hammer of the Righteous!', 41, 0, 100, 0, 0, 16136, 35408, 0, 'Eadric the Pure - EMOTE_HAMMER_RIGHTEOUS');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35119, 4, 0, 'Hammer of the Righteous!', 14, 0, 100, 0, 0, 16136, 35442, 0, 'Eadric the Pure - SAY_HAMMER_RIGHTEOUS');
