INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35476, 0, 2, 'It won\'t be long before Fizzlebang is a household word, used to scare children into bed! "Beware, or Fizzlebang will summon a mighty demon to eat you!"', 12, 0, 100, 1, 0, 0, 35817, 0, 'Wilfred Fizzlebang');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35476, 0, 3, 'I, Wilfred Fizzlebang, have been invited to this quaint tournament to make use of my extensive knowledge of the summoning arts. What better choice?', 12, 0, 100, 1, 0, 0, 35815, 0, 'Wilfred Fizzlebang');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35589, 0, 0, 'The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament.', 12, 0, 100, 0, 0, 0, 35896, 0, 'Jaeren Sunsworn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35589, 1, 0, 'The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers.', 12, 0, 100, 0, 0, 0, 35871, 0, 'Jaeren Sunsworn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35604, 0, 0, 'The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament.', 12, 0, 100, 0, 0, 0, 35896, 0, 'Arelas Brightstar');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (35604, 1, 0, 'The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers.', 12, 0, 100, 0, 0, 0, 35871, 0, 'Arelas Brightstar');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36095, 0, 0, 'Champions, you\'re alive! Not only have you defeated every challenge of the Trial of the Crusader, but also thwarted Arthas\' plans! Your skill and cunning will prove to be a powerful weapon against the Scourge. Well done! Allow one of the Crusade\'s mages to transport you to the surface!', 14, 0, 100, 5, 0, 16053, 36129, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36095, 1, 0, 'Let me hand you the chests as a reward, and let its contents will serve you faithfully in the campaign against Arthas in the heart of the Icecrown Citadel!', 41, 0, 0, 0, 0, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36272, 0, 0, 'Great. We\'re not gutless, we\'re incompetent.', 12, 0, 100, 0, 0, 0, 38596, 3, 'SAY_FRYE_DEATH');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36296, 0, 0, 'Did they bother to tell you who I am and why I am doing this?', 12, 0, 100, 6, 0, 0, 38586, 3, 'SAY_INTRO_0');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36296, 1, 0, '...or are they just using you like they do everybody else?', 12, 0, 100, 396, 0, 0, 38600, 3, 'SAY_INTRO_1');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (36296, 2, 0, 'But what does it matter.  It is time for this to end.', 12, 0, 100, 397, 0, 0, 38591, 3, 'SAY_INTRO_2');
