INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3980, 1, 0, 'By the Light! This is not good. There\'s something you don\'t understand, $n.', 12, 7, 100, 5, 0, 0, 1378, 0, 'Raleigh the Devout');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3980, 2, 0, '%s throws Anton\'s letter down on the table.', 16, 0, 100, 0, 0, 0, 1379, 0, 'Raleigh the Devout');

