INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4062, 0, 1, 'No sign of the final explosives shipment to the west either.  Where are those lollygaggers?', 12, 7, 100, 0, 0, 0, 783, 0, 'Dark Iron Bombardier');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4062, 0, 2, 'This bridge should have been destroyed by now.  How long does it take for those lazy sods to get here from the Highlands!', 12, 7, 100, 0, 0, 0, 784, 0, 'Dark Iron Bombardier');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4064, 1, 0, 'Feel the power of the Blackrock Orcs!', 12, 1, 100, 0, 0, 0, 1874, 0, 'Blackrock Scout');

