INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4429, 0, 0, 'Hm, these are pretty good bones you got this time, Pozzik.', 12, 0, 100, 0, 0, 0, 1524, 0, 'Goblin Pit Crewman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4429, 1, 0, 'All done! The modifications should give us a little boost in speed, but I don\'t think she\'ll hold up to too much bumping.', 12, 0, 100, 0, 0, 0, 1525, 0, 'Goblin Pit Crewman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4429, 2, 0, 'BANG! BANG! BANG!  I\'m beefing up the car!!!', 12, 0, 100, 0, 0, 0, 1506, 0, 'Goblin Pit Crewman');

