INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4494, 0, 3, 'The Scarlet Crusade shall smite the wicked and drive evil from these lands!', 12, 7, 100, 0, 0, 0, 2628, 0, 'Scarlet Spellbinder');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4500, 2, 0, 'Me still boss of ogres. Haw!', 14, 0, 100, 0, 0, 0, 1514, 0, 'Overlord Mok\'Morokk');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4568, 0, 0, 'Take a chest of containment coffers and a cantation of manifestation, $n.  You will need them for your mission.', 12, 1, 100, 25, 0, 0, 2682, 0, 'Anastasia Hartwell');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4576, 0, 0, 'One moment while I stitch this together...', 12, 1, 100, 0, 0, 0, 2761, 0, 'Josef Gregorian');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4576, 1, 0, 'There.  I am finished with your robe, $n.', 12, 1, 100, 1, 0, 0, 2762, 0, 'Josef Gregorian');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4630, 1, 0, 'Thanks again, $n.  Now I\'ll use this indurium to beef up my car!', 12, 0, 100, 0, 0, 0, 1505, 0, 'Goblin Pit Crewman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4630, 2, 0, 'Now for the next race, the car will be even FASTER than before!', 12, 0, 100, 4, 0, 0, 1508, 0, 'Goblin Pit Crewman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4709, 0, 0, 'Hehehe! Things go boom!', 12, 0, 100, 11, 0, 0, 1540, 0, 'Zamek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4720, 0, 0, 'What was that!', 14, 0, 100, 5, 0, 0, 1541, 0, 'Rizzle Brassbolts');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4720, 1, 0, 'Bloody... Must have been the goblins... Let\'s see how bad the damage is.', 12, 0, 100, 5, 0, 0, 1542, 0, 'Rizzle Brassbolts');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4720, 2, 0, 'Guess there\'s nothing more to be done. Blast!', 12, 0, 100, 6, 0, 0, 1543, 0, 'Rizzle Brassbolts');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4726, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Raging Thunder Lizard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4895, 0, 0, 'He\'ll bring you mead, he\'ll bring you beer...', 12, 7, 100, 0, 0, 0, 1632, 0, 'Smiling Jim');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4895, 1, 0, 'A grinning face from ear to ear...', 12, 7, 100, 0, 0, 0, 1633, 0, 'Smiling Jim');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4895, 2, 0, 'He\'s served us all from year to year...', 12, 7, 100, 0, 0, 0, 1634, 0, 'Smiling Jim');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4895, 3, 0, 'We call him Smiling Jim...', 12, 7, 100, 11, 0, 0, 1635, 0, 'Smiling Jim');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4962, 0, 0, 'Oh, it\'s on now! Bet you thought I\'d be alone too, huh?!', 12, 7, 100, 0, 0, 0, 5827, 0, 'Tapoke \"Slim\" Jahn');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4962, 1, 0, 'Okay, okay! No need to get all violent. I\'ll talk. I\'ll talk!', 12, 7, 100, 0, 0, 0, 1743, 0, 'Tapoke \"Slim\" Jahn');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4962, 2, 0, 'I have a few notes from the job back at my place. I\'ll get them and then meet you back in the inn.', 12, 7, 100, 0, 0, 0, 1744, 0, 'Tapoke \"Slim\" Jahn');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4963, 0, 0, 'I\'m glad the commotions died down some around here. The last thing this place needs is another brawl.', 12, 7, 100, 0, 0, 0, 4169, 0, 'Mikhail');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4967, 1, 0, 'Ah, Private Hendel. A pleasure to meet one of the men who would betray his kingdom for pride.', 12, 7, 100, 0, 0, 0, 1657, 0, 'Archmage Tervosh');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4967, 2, 0, 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower.', 12, 7, 100, 0, 0, 0, 1663, 0, 'Archmage Tervosh');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4967, 3, 0, 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', 12, 7, 100, 0, 0, 0, 1761, 0, 'Archmage Tervosh');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4967, 4, 0, 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', 12, 7, 100, 0, 0, 0, 1808, 0, 'Archmage Tervosh');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4969, 0, 0, 'Yeah, okay, boss. No problem.', 12, 0, 100, 0, 0, 0, 1715, 0, 'Old Town Thug');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4971, 0, 0, 'Whoa! This is way more than what I bargained for, you\'re on your own, Slim!', 12, 7, 100, 0, 0, 0, 5828, 0, 'Slim\'s Friend');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4973, 0, 0, 'Oops! That was a bit off. I didn\'t even hit the target!', 12, 7, 100, 0, 0, 0, 1792, 0, 'Guard Lasiter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4973, 1, 0, 'That was a nice shot... but not perfect. Maybe a bit more to the left.', 12, 7, 100, 0, 0, 0, 1789, 0, 'Guard Lasiter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4973, 2, 0, 'Barely hit the target that time. Hmmm,... maybe I need to adjust back to the right.', 12, 7, 100, 0, 0, 0, 1790, 0, 'Guard Lasiter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4973, 3, 0, 'Whoa! Look at that bullseye!', 12, 7, 100, 0, 0, 0, 1791, 0, 'Guard Lasiter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4979, 3, 0, '%s reads the leaflet.', 16, 0, 100, 6, 0, 0, 22112, 0, 'Theramore Guard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5109, 0, 0, 'Fresh Bread! Get your oven fresh bread here!', 12, 7, 100, 0, 0, 0, 4014, 0, 'Myra Tyrngaarde');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5109, 0, 1, 'Fresh bread for sale!', 12, 7, 100, 0, 0, 0, 4013, 0, 'Myra Tyrngaarde');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5109, 0, 2, 'Come get yer fresh bread!', 12, 7, 100, 0, 0, 0, 4015, 0, 'Myra Tyrngaarde');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5184, 0, 0, 'It\'s Proudmoore! Get out of here!', 12, 7, 100, 0, 0, 0, 7325, 0, 'Theramore Sentry');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5184, 1, 0, 'Run!', 12, 7, 100, 0, 0, 0, 7324, 0, 'Theramore Sentry');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5184, 1, 1, 'Get out of here!', 12, 7, 100, 0, 0, 0, 7340, 0, 'Theramore Sentry');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5184, 1, 2, 'Don\'t let them catch you!', 12, 7, 100, 0, 0, 0, 7341, 0, 'Theramore Sentry');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5184, 1, 3, 'She\'ll have our heads if we\'re caught!', 12, 7, 100, 0, 0, 0, 7342, 0, 'Theramore Sentry');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5353, 0, 0, 'I have given you an oathstone of Ysera\'s Dragonflight, $n.  When you are ready and able to aid us, use it, and then we may begin.', 12, 0, 100, 0, 0, 0, 4481, 0, 'Itharius');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5386, 0, 0, 'It wasn\'t my fault, Master Mazen!', 12, 7, 100, 0, 0, 0, 1828, 0, 'Acolyte Dellis');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5386, 0, 1, 'It was a simple mistake. A wrong turn I tell you!', 12, 7, 100, 0, 0, 0, 1829, 0, 'Acolyte Dellis');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5386, 0, 2, 'The swamp was horrible! I was so... so... scared!', 12, 7, 100, 0, 0, 0, 1830, 0, 'Acolyte Dellis');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5386, 0, 3, 'I\'m lucky to be alive!', 12, 7, 100, 0, 0, 0, 1831, 0, 'Acolyte Dellis');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5413, 0, 0, '%s begins to work...', 16, 0, 100, 0, 0, 0, 2341, 0, 'Furen Longbeard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5413, 1, 0, 'Finished!  And the results are amazing.  I must convey them to my associates Grimand, Mathiel and Klockmort!', 12, 7, 100, 4, 0, 0, 2342, 0, 'Furen Longbeard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5413, 2, 0, 'Your armor is ready, $n.', 12, 7, 100, 1, 0, 0, 2343, 0, 'Furen Longbeard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5414, 0, 0, 'The serum will only take a few minutes to prepare, $n.', 12, 0, 100, 1, 0, 0, 1848, 0, 'Apothecary Faustin');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5414, 1, 0, 'Almost done...', 12, 0, 100, 0, 0, 0, 1849, 0, 'Apothecary Faustin');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (5414, 2, 0, 'At last, the serum is complete, $n. Now, come closer. We must talk.', 12, 0, 100, 1, 0, 0, 1850, 0, 'Apothecary Faustin');

