INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6224, 1, 2, 'This sickness clouds my vision, but I know you must be a trogg.  Die foul invader!', 12, 7, 100, 0, 0, 0, 2424, 0, 'Leprous Machinesmith');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6224, 1, 3, 'A foul trogg if ever I saw one.  Die!', 12, 7, 100, 0, 0, 0, 2425, 0, 'Leprous Machinesmith');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6244, 0, 0, 'A thousand years ago, the immortal elves, demigods of the forest and dragons fought against the Legion on the slopes of Hyjal.', 12, 7, 100, 1, 0, 0, 2310, 0, 'Takar the Seer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6244, 1, 0, 'Anathera and Latherion fought back to back where the battle was thickest. Two lovers determined to survive the war together.', 12, 7, 100, 1, 0, 0, 2311, 0, 'Takar the Seer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6244, 2, 0, 'But it was not to be. As their foes pressed close, Anathera\'s armor was pierced by a demon\'s tainted blade. In the thick of battle, there would be nothing to save her.', 12, 7, 100, 1, 0, 0, 2312, 0, 'Takar the Seer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6244, 3, 0, 'Ignoring the battle that raged about him, Latherion dropped his guard to cradle his love in her dying moments. But the demons gave him no quarter, and quickly laid him low.', 12, 7, 100, 1, 0, 0, 2314, 0, 'Takar the Seer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6244, 4, 0, 'Where the two lovers fell, a tree would bloom. The elves call it the heartswood. It will surely serve your ends.', 12, 7, 100, 1, 0, 0, 2317, 0, 'Takar the Seer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6245, 0, 0, 'So this... this is how it ends, my love...', 12, 7, 100, 0, 0, 0, 2316, 0, 'Anathera');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6246, 0, 0, 'Anathera!', 12, 7, 100, 15, 0, 0, 2313, 0, 'Latherion');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6246, 1, 0, 'Anathera... I cannot bear to face the rest of eternity without you by my side... If you are taken from me, then let this be the day I also return to the earth...', 12, 7, 100, 0, 0, 0, 2315, 0, 'Latherion');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6266, 1, 0, 'Ok, let\'s get this orb made...', 12, 0, 100, 0, 0, 0, 2663, 0, 'Menara Voidrender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6266, 2, 0, 'All right.  It\'s made!', 12, 0, 100, 1, 0, 0, 2664, 0, 'Menara Voidrender');

