INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6826, 1, 0, 'Let me grab a spell focus from inside my home here, and then we\'ll get started!', 12, 7, 100, 0, 0, 0, 3121, 0, 'Talvash del Kissel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6826, 2, 0, 'Bah, it isn\'t here.  Where did I have that blasted thing at last?', 12, 7, 100, 6, 0, 0, 3122, 0, 'Talvash del Kissel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6826, 3, 0, 'That\'s right, it\'s over by the bed.  Heh, sorry about that!', 12, 7, 100, 0, 0, 0, 3123, 0, 'Talvash del Kissel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6826, 4, 0, 'Fantastic then!  Let\'s get this thing fixed for you.', 12, 7, 100, 0, 0, 0, 3124, 0, 'Talvash del Kissel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6826, 5, 0, 'Success!  Huzzah!  The power source restored the necklace!', 12, 7, 100, 4, 0, 0, 3125, 0, 'Talvash del Kissel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6966, 0, 0, 'Oh! I almost forgot! I slipped you a set of thieven\' tools. You\'ll need those where you\'re going.', 12, 0, 100, 0, 0, 0, 3562, 0, 'Lucius');

