INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7052, 3, 0, 'I dunno, Jill. If Klaven comes down while we\'re slacking, we may end up as one of those things!', 12, 0, 100, 274, 0, 0, 3129, 0, 'Defias Tower Patroller');

