INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7783, 0, 0, 'You will need to open the Felhound Tracker Kit and read the instruction manual. Summon the beast when you are near the peninsula of cliff giants, south of here.', 12, 0, 100, 0, 0, 0, 4576, 0, 'Loramus Thalipedes');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7843, 0, 0, 'Aaaaahhh! So close to escaping.', 12, 7, 100, 0, 0, 0, 3829, 0, 'Gnomeregan Evacuee');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7850, 0, 0, 'I see the exit! Hurry, hurry!', 12, 0, 100, 5, 0, 0, 3948, 0, 'Kernobee');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7850, 1, 0, '%s cheers!', 16, 0, 100, 4, 0, 0, 3928, 0, 'Kernobee');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7850, 2, 0, 'Thank you for rescuing me! Word of your deed will not go unnoticed.', 12, 0, 100, 0, 0, 0, 3929, 0, 'Kernobee');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7850, 3, 0, '%s waves goodbye to everyone.', 16, 0, 100, 3, 0, 0, 3930, 0, 'Kernobee');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7850, 4, 0, 'Uh oh....', 12, 0, 100, 0, 0, 0, 3953, 0, 'Kernobee');

