INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7853, 0, 0, 'Give me a moment while I calibrate the transponder to your height and weight specifications, $n.', 12, 0, 100, 0, 0, 0, 3904, 0, 'Scooty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7853, 1, 0, 'Alright, $n, your transponder is ready!', 12, 0, 100, 0, 0, 0, 3905, 0, 'Scooty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7897, 0, 0, '%s activates!', 14, 0, 100, 0, 0, 0, 3926, 0, 'Alarm-a-bomb 2600');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7897, 1, 0, 'Self-destruct sequence activated.', 14, 0, 100, 0, 0, 0, 3927, 0, 'Alarm-a-bomb 2600');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7897, 2, 0, 'Anti-escape countermeasures activated. Self-destruct in t-minus 20 seconds....', 14, 0, 100, 0, 0, 0, 3949, 0, 'Alarm-a-bomb 2600');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7897, 3, 0, 'Alarm-a-bomb unit 2600 has failed. Self-destruct override [ACTIVE]. Cooldown [ACTIVE].', 14, 0, 100, 0, 0, 0, 3950, 0, 'Alarm-a-bomb 2600');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7907, 0, 0, 'Take a look around, $n. Perhaps you will find something that I have missed.', 12, 0, 100, 0, 0, 0, 4042, 0, 'Daryn Lightwind');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7907, 1, 0, '%s gestures at the piles of books.', 16, 0, 100, 25, 0, 0, 4043, 0, 'Daryn Lightwind');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7918, 0, 0, 'Salutations!  I am a guardian of entry.  Please use the proper discs for access to Uldum.', 12, 0, 100, 0, 0, 0, 4049, 0, 'Stone Watcher of Norgannon');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (7918, 1, 0, 'Your discs will be returned to you once you access the pedestal.', 12, 0, 100, 0, 0, 0, 4065, 0, 'Stone Watcher of Norgannon');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8216, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Retherokk the Berserker');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8256, 1, 0, 'I have just the spot for this horn!', 12, 0, 100, 0, 0, 0, 4305, 0, 'Curator Thorius');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8256, 2, 0, 'I\'ve been saving this spot for something special. I don\'t think it gets any more special than this, $n.', 12, 0, 100, 25, 0, 0, 4306, 0, 'Curator Thorius');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8256, 3, 0, 'There we go. All done!', 12, 0, 100, 0, 0, 0, 4307, 0, 'Curator Thorius');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 0, 0, 'Stop... need to catch.... my breath.', 12, 0, 100, 0, 0, 0, 4345, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 1, 0, 'This is the place, $r: The Cauldron! Mark it on a map or do something so that you don\'\'t forget. If anything ever happened to me, my team would be stuck in that mine forever.', 12, 0, 100, 25, 0, 0, 4354, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 2, 0, 'I\'\'ve got to quit smoking! Give me a minute to catch my breath.', 12, 0, 100, 0, 0, 0, 4356, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 3, 0, 'We\'\'re almost to safety. From here, we run. Try not to get too far behind! GO!', 12, 0, 100, 0, 0, 0, 4357, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 4, 0, 'The gate is in sight! We\'\'re going to make it! But first, I need to take a breather.', 12, 0, 100, 0, 0, 0, 4363, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 5, 0, 'ARRRRRGH! He got me!', 12, 0, 100, 0, 0, 0, 4359, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 6, 0, '%s points to the parchment on the ground.', 16, 0, 100, 0, 0, 0, 5944, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 7, 0, '%s disappears in a cloud of dust.', 16, 0, 100, 0, 0, 0, 4362, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 8, 0, 'Are you going to let an injured dwarf do all the work for you?', 12, 0, 100, 0, 0, 0, 4352, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 8, 1, 'It\'s on! $n, meet my fists. Fists, say hello to $n.', 12, 0, 100, 0, 0, 0, 4353, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8284, 9, 0, 'Onward! And be on guard this time! I\'m not going to carry anymore fights.', 12, 0, 100, 0, 0, 0, 4355, 0, 'Dorius Stonetender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8337, 0, 0, 'The secrets of Suntara will not leave these lands.', 14, 0, 100, 0, 0, 0, 4346, 0, 'Summon Dark Iron Steelshifter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8338, 0, 0, 'He\'\'s mine...', 14, 0, 100, 0, 0, 0, 4358, 0, 'Dark Iron Marksman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8439, 0, 0, 'Use this Staff of Command on Raze when you are near Shadowsilk Poachers and he will make them rue the day they decided to poach in these lands.', 12, 0, 100, 1, 0, 0, 4414, 0, 'Nilith Lokrav');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8441, 0, 0, 'Raze follow $n! Raze SMASH poachers! SMASH SMASH SMASH! Tell Raze to SMASH by playing with stick! SMASH!', 12, 0, 100, 0, 0, 0, 4521, 0, 'Raze');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8441, 1, 0, 'Use staff, tell Raze what do! Show Raze poacher! Raze smash!', 12, 0, 100, 0, 0, 0, 5949, 0, 'Raze');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8441, 2, 0, 'Raze need go home now. Bye, bye, $r.', 12, 0, 100, 0, 0, 0, 4413, 0, 'Raze');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8441, 2, 1, 'Bye, bye, tiny little $r. Raze go now.', 12, 0, 100, 0, 0, 0, 4415, 0, 'Raze');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8578, 0, 0, 'You dare! Now feel my wrath!', 14, 0, 100, 0, 0, 0, 4495, 0, 'Magus Rimtori');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8579, 0, 0, 'It is true, $n.  The wheels of doom now turn, inevitably, toward the true return of Hakkar!', 12, 0, 100, 5, 0, 0, 10456, 0, 'Yeh\'kinya');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8579, 1, 0, 'Blood and souls for Hakkar!  HAHAHAH!', 14, 0, 100, 0, 0, 6963, 10457, 0, 'Yeh\'kinya');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8581, 0, 0, 'Who dares?! I will destroy you!', 12, 0, 100, 0, 0, 0, 4502, 0, 'Blood Elf Defender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8581, 1, 0, 'Mistress!! They seek to destroy your work!!', 12, 0, 100, 0, 0, 0, 4491, 0, 'Blood Elf Defender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8581, 2, 0, 'Ha! Now your death is ensured!', 12, 0, 100, 0, 0, 0, 4492, 0, 'Blood Elf Defender');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8668, 0, 0, '%s searches for a source of arcane energy.', 16, 0, 100, 0, 0, 0, 4570, 0, 'Felhound Tracker');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8668, 1, 0, '%s growls.', 16, 0, 100, 35, 0, 0, 4571, 0, 'Felhound Tracker');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 0, 'Leave this place. <The relic emits a white hot arc of flame. A memory has been gained: A lone Dark Iron dwarf is surrounded by seven corpses, kneeling before a monolith of flame.>', 15, 0, 100, 0, 0, 0, 4594, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 1, 'You will perish here. <Your mind fills with visions of chaos and destruction.>', 15, 0, 100, 0, 0, 0, 4595, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 2, 'Do not taint these ruins, mortal. <You are engulfed in a blinding flash of light. A creature composed entirely of flame is the only thing you can remember seeing.>', 15, 0, 100, 0, 0, 0, 4596, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 3, 'Help us, outsider. <The relic crumbles to dust. A vision of eight Dark Iron dwarves performing some sort of ritual fills your head.>', 15, 0, 100, 0, 0, 0, 4597, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 4, 'He cannot be defeated. <The relic burns to nothing. The memories it held are now your own. This city was destroyed by a being not of this world.>', 15, 0, 100, 0, 0, 0, 4598, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 5, 'Your existence is acknowledged. <The relic turns to dust. Your head throbs with newfound wisdom. Something evil lurks in the heart of the mountain.>', 15, 0, 100, 0, 0, 0, 4599, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8887, 0, 6, 'Defiler... you will be punished for this incursion. <A symbol of flame radiates from the relic before it crumbles to the earth.>', 15, 0, 100, 0, 0, 0, 4600, 0, 'A tormented voice');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8891, 0, 0, 'You can\'t hide from us.  Prepare to burn!', 14, 0, 100, 0, 0, 0, 5271, 0, 'Anvilrage Guardsman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8905, 0, 0, 'Intruders!!', 12, 0, 100, 0, 0, 0, 4961, 0, 'Warbringer Construct');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8906, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 2384, 0, 'Ragereaver Golem');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8929, 0, 0, '%s is visibly shaken by the death of Emperor Thaurissan.', 16, 0, 100, 18, 0, 0, 5429, 0, 'Princess Moira Bronzebeard');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9017, 0, 0, 'I cannot be destroyed! By the will of Ragnaros, I shall be reborn!', 12, 0, 100, 0, 0, 0, 5061, 0, 'Lord Incendius');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9042, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Verek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 0, 'I know it\'ll work this time...', 12, 0, 100, 1, 0, 0, 4839, 0, 'J.D. Collie');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 1, 'This will be my best experiment yet!', 12, 0, 100, 1, 0, 0, 4840, 0, 'J.D. Collie');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 2, 'I know it\'ll work this time... My last 13 tries were just practice!', 12, 0, 100, 1, 0, 0, 4842, 0, 'J.D. Collie');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 9, 0, 'Now, if I add the yellow ones...', 12, 0, 100, 0, 0, 0, 4841, 0, 'J.D. Collie');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9119, 0, 0, 'I hope this works!', 12, 0, 100, 0, 0, 0, 4966, 0, 'Muigin');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9119, 1, 0, '%s adds the haze to the Bloodpetal sprouts.', 16, 0, 100, 0, 0, 0, 4967, 0, 'Muigin');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9119, 2, 0, 'Look at them go!', 12, 0, 100, 0, 0, 0, 4968, 0, 'Muigin');

