INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12858, 6, 0, 'Steady, people.  We\'ll raid soon enough.', 12, 0, 100, 0, 0, 0, 8420, 0, 'Torek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12858, 6, 1, 'With luck, we\'ll gain some good scars today.', 12, 0, 100, 0, 0, 0, 8421, 0, 'Torek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12858, 6, 2, 'These woods make my tusks itch...', 12, 0, 100, 0, 0, 0, 8422, 0, 'Torek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12858, 6, 3, 'They say Duriel is a fierce night elf.  It\'ll be a good fight!', 12, 0, 100, 0, 0, 0, 8423, 0, 'Torek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12858, 6, 4, 'We\'ll be notching our axes after this.', 12, 0, 100, 0, 0, 0, 8424, 0, 'Torek');

