INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14469, 3, 0, 'This is it, Impsy. I can feel it! This is the big one!', 12, 0, 100, 5, 0, 0, 9615, 0, 'Niby the Almighty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14469, 3, 1, 'Brace yourself for the glorious impact, Impsy!', 12, 0, 100, 5, 0, 0, 9616, 0, 'Niby the Almighty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14469, 3, 2, 'Kick me out, will they!? I\'ll show those uptight cone heads in Stormwind the true power of Niby! BEHOLD!', 12, 0, 100, 15, 0, 0, 9617, 0, 'Niby the Almighty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14469, 4, 0, '%s shakes his fist in frustration.', 16, 0, 100, 0, 0, 0, 9618, 0, 'Niby the Almighty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14469, 5, 0, 'Why you little!', 12, 0, 100, 0, 0, 0, 9622, 0, 'Niby the Almighty');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14470, 2, 0, 'Niby, you are stupid.', 12, 0, 100, 0, 0, 0, 9619, 0, 'Impsy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14470, 2, 1, 'Oh my, what a surprise! Another failure!? But how, oh great one??', 12, 0, 100, 0, 0, 0, 9620, 0, 'Impsy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14470, 2, 2, 'Awe inspiring, master. Your ineptitude is awe inspiring.', 12, 0, 100, 0, 0, 0, 9621, 0, 'Impsy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14470, 3, 0, 'Ack! Help! Crazed gnome trying to kill me!', 12, 0, 100, 0, 0, 0, 9623, 0, 'Impsy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14523, 0, 0, 'You dog!  Now I\'ll trap your soul into a shard and give you to my imp as a plaything!', 12, 0, 100, 0, 0, 0, 9735, 0, 'Ulathek');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (14757, 0, 0, 'Finally, I am free! I come home soon, my dear!', 12, 0, 100, 4, 0, 0, 10013, 0, 'Elder Torntusk');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15542, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Twilight Marauder');

