INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16113, 0, 0, 'Thank you, Mrs. Chambers.', 12, 0, 100, 1, 0, 0, 12140, 0, 'Father Inigo Montoy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16113, 1, 0, 'Is this a mango? Stupendous!', 12, 0, 100, 4, 0, 0, 12141, 0, 'Father Inigo Montoy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16113, 2, 0, 'You\'ve really outdone yourself this time, Mrs. Chambers. Not a spot of mold to be found!', 12, 0, 100, 1, 0, 0, 12142, 0, 'Father Inigo Montoy');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 0, 0, 'Your message did reach us, Commander, but by no means is our involvement here sanctioned by the Grand Crusader.', 12, 0, 100, 1, 0, 0, 12047, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 1, 0, 'The heathens will be forced to react. Orcs, bull-men...', 12, 0, 100, 1, 0, 0, 12052, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 2, 0, 'Whatever. The revolting, unkempt Horde filth...', 12, 0, 100, 5, 0, 0, 12054, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 3, 0, 'Too little, too late. The dam has broken. We are a bump in the road at best.', 12, 0, 100, 274, 0, 0, 12056, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 4, 0, 'There is always... Ashbringer.', 12, 0, 100, 1, 0, 0, 12058, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 5, 0, 'Thank you, Jessica.', 12, 0, 100, 2, 0, 0, 12143, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16114, 6, 0, 'And might I add, that dress is lovely! You must tell me where you got it from.', 12, 0, 100, 1, 0, 0, 12144, 0, 'Scarlet Commander Marjhan');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 0, 0, 'Your leader takes residence at the doorstep to Naxxramas. Do you think he does not feel the pressure?', 12, 0, 100, 6, 0, 0, 12048, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 1, 0, '%s lowers the sound of his voice to a whisper.', 16, 0, 100, 0, 0, 0, 12049, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 2, 0, 'Even demons are capable of fear...', 12, 0, 100, 0, 0, 0, 12050, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 3, 0, 'Scourge activity in the Plaguelands has increased ten-fold. We are receiving reports of Scourge attacks from as far away as Kalimdor! Now how do you suppose this has happened? How have the wretched undead done that which was previously thought impossible? Is nowhere safe?', 12, 0, 100, 1, 0, 0, 12051, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 4, 0, 'Tauren.', 12, 0, 100, 0, 0, 0, 12053, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 5, 0, 'You lack tact, Commander. This is not surprising. However, you are right in that the Horde will be forced to act. But not just the Horde. They already come - heroes from across the world.', 12, 0, 100, 1, 0, 0, 12055, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 6, 0, 'So why are you here? What are we to do if there is no hope?', 12, 0, 100, 6, 0, 0, 12057, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 7, 0, 'So this is what you have come for? To try and claim that which is the reason for the Dawn\'s existence and the Crusade\'s downfall? You open old wounds with your words, Commander!', 12, 0, 100, 6, 0, 0, 12062, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 8, 0, 'The Ashbringer is dead and there will never be another like him. The lesser Mograine is a far cry from the man his father was. You know this, Commander! It was, after all, your own leadership that was responsible for the death of the Scarlet Highlord Mograine.', 12, 0, 100, 5, 0, 0, 12063, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 9, 0, 'It was your leadership that manipulated a grieving child to try and recover the sword. A child that has grown into a man with nothing but vengeance and hatred in his heart! And for what? You are no closer to the sword now than you were five years ago.', 12, 0, 100, 25, 0, 0, 12065, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 10, 0, 'Yet you think that recovering Ashbringer will somehow turn the tide of battle? Let me let you in on a little secret, Commander: The power of the Ashbringer came from the man who would wield it... I was there, Commander. I watched him burn legions of undead in righteous fire before he would even unsheathe the blade. Alas, your grasp on history is ... lacking.', 12, 0, 100, 1, 0, 0, 12066, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 11, 0, 'Now be silent and note where you are, lest I inform Lord Fordring and his knights that you are no longer here on amicable terms. I am certain he would take great pleasure in seeing to your \'atonement.\'', 12, 0, 100, 5, 0, 0, 12067, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 12, 0, 'The fish is exquisite, Jessica.', 12, 0, 100, 1, 0, 0, 12138, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 13, 0, 'My compliments to the chef!', 12, 0, 100, 1, 0, 0, 12137, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16115, 14, 0, 'Delicious!', 12, 0, 100, 0, 0, 0, 12139, 0, 'Commander Eligor Dawnbringer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16116, 0, 0, 'Oh, quite succulent. Many thanks, Mrs. Chambers.', 12, 0, 100, 2, 0, 0, 12147, 0, 'Archmage Angela Dosantos');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16116, 1, 0, 'The boar is superb!', 12, 0, 100, 4, 0, 0, 12146, 0, 'Archmage Angela Dosantos');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16116, 2, 0, 'Indeed... lovely dress, Jessica.', 12, 0, 100, 273, 0, 0, 12145, 0, 'Archmage Angela Dosantos');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16134, 0, 0, 'Watch, druid. The earth still breathes. It yearns to be cleansed.', 12, 0, 100, 1, 0, 0, 11946, 0, 'Rimblat Earthshatter');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16135, 0, 0, 'That is wonderous, Rimblat! Even this foul, sundered earth is not beyond healing.', 12, 0, 100, 273, 0, 0, 11945, 0, 'Rayne');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16222, 2, 0, 'Even the Light is no match for the indomitable will of our people.', 12, 1, 100, 0, 0, 0, 14793, 0, 'Silvermoon City Guardian');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16222, 3, 0, 'Your power strengthens us all.', 12, 1, 100, 0, 0, 0, 14792, 0, 'Silvermoon City Guardian');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16256, 0, 0, 'Ok, you had the fish platter, Commander. Enjoy!', 12, 0, 100, 1, 0, 0, 12132, 0, 'Jessica Chambers');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16256, 1, 0, 'Father, you had the bowl of fruit? Right?', 12, 0, 100, 6, 0, 0, 12133, 0, 'Jessica Chambers');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16256, 2, 0, 'And for the ladies, roast baby boar. Enjoy!', 12, 0, 100, 1, 0, 0, 12134, 0, 'Jessica Chambers');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16256, 3, 0, 'Please let me know if you need anything else!', 12, 0, 100, 1, 0, 0, 12135, 0, 'Jessica Chambers');

