INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17099, 0, 0, 'Uther Lightbringer brought only darkness to Quel\'Thalas.  Let us see if we can bring some darkness to his tomb!', 12, 0, 100, 1, 0, 0, 14112, 0, 'Mehlar Dawnblade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17099, 1, 0, 'At last I will have my revenge!', 12, 0, 100, 11, 0, 0, 14115, 0, 'Mehlar Dawnblade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17626, 1, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 38630, 0, 'Laughing Skull Legionnaire');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 0, 'Taste blade, mongrel!', 12, 0, 100, 0, 0, 0, 10950, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 1, 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', 12, 0, 100, 0, 0, 0, 10951, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 2, 'As if we don\'t have enough problems, you go and create more!', 12, 0, 100, 0, 0, 0, 10953, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 3, 'You dare spill blood on neutral ground? OUT! OUT, I SAY!', 12, 0, 100, 0, 0, 0, 10948, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 4, 'We don\'t take kindly to miscreants, $r.', 12, 0, 100, 0, 0, 0, 10949, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 5, 'Get a rope!', 12, 0, 100, 0, 0, 0, 10952, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 6, 'Believe me when I tell you this: You\'re gonna wish you weren\'t born, sissy!', 12, 0, 100, 0, 0, 0, 10954, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17855, 0, 7, 'Your actions shame us all, $c. I hurt inside as I beat you senseless.', 12, 0, 100, 0, 0, 0, 10955, 0, 'Expedition Warden');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18109, 0, 0, 'Burn! Burn! Burn!', 12, 12, 100, 0, 0, 0, 14778, 0, 'Living Fire');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18459, 0, 0, 'Rejoice, my friends! $n has saved us from the looming shadow that threatened our very lives!', 14, 7, 100, 22, 0, 0, 19075, 0, 'Jenai Starwhisper');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18482, 0, 0, 'What is the meaning of this?!', 12, 0, 100, 0, 0, 0, 15487, 0, 'Empoor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18482, 1, 0, 'I\'m leaving now!', 12, 0, 100, 1, 0, 0, 15491, 0, 'Empoor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18548, 0, 0, 'This belongs to Firewing Point.  You\'ll not be putting your grubby hands on it!', 12, 0, 100, 0, 0, 0, 15528, 0, 'Firewing Courier');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18554, 4, 0, 'I can assure you that we will not fail, my master.  I am personally overseeing every aspect of the construction, and I hold the final codes, myself.  Within a day\'s time, I will have the bomb detonated on those nearby pests.', 12, 0, 100, 2, 0, 0, 19164, 0, 'Sharth Voldoun');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18677, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Mekthorg the Wild');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18679, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 10677, 0, 'Vorakem Doomspeaker');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 0, 0, 'Rifling through the stacks of books and scrolls on the table, Lieutenant Gravelhammer begins to examine one in particular with interest.', 16, 0, 100, 0, 0, 0, 15827, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 1, 0, 'What?  What\'s this?!', 12, 7, 100, 5, 0, 0, 15829, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 1, 1, 'That is not even remotely imaginable!', 12, 7, 100, 5, 0, 0, 15830, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 1, 2, 'Looks like I picked a bad day to stop drinking.', 12, 7, 100, 5, 0, 0, 15831, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 1, 3, 'How did this get missed?!', 12, 7, 100, 5, 0, 0, 15832, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 2, 0, 'Cap\'n, sir, we have a slight problem.', 12, 7, 100, 1, 0, 0, 15833, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 0, 'As you say, sir.  It would appear that someone failed to mention to me that the druids at the Cenarion Thicket have been wiped out!', 12, 7, 100, 1, 0, 0, 15838, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 1, 'Bad news.  We\'ve been hit again.  They\'re gone, but the front sentry tower is on fire.', 12, 7, 100, 1, 0, 0, 15839, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 2, 'One of our merchants has gone missing!', 12, 7, 100, 1, 0, 0, 15840, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 3, 'That group that recently arrived from Honor Hold -- they\'re mercs!  They\'re starting to stir up trouble.  Maybe I should have a talk with them?', 12, 7, 100, 1, 0, 0, 15842, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 4, 'One of our scouts is reporting that a shadowy group has moved into the ruins of Grangol\'var Village.  Oops, this report is over a week old.', 12, 7, 100, 1, 0, 0, 15843, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 3, 5, 'We have reports from a scout in the field that one of the Cenarion druids has been taken captive up at Firewing Point.', 12, 7, 100, 1, 0, 0, 15844, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18713, 4, 0, 'Aye, aye, sir.  Right away!', 12, 7, 100, 66, 0, 0, 15853, 0, 'Lieutenant Gravelhammer');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 5, 0, 'At ease, lieutenant. Go on.', 12, 7, 100, 66, 0, 0, 15834, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 5, 1, 'What is it now, Gravel?', 12, 7, 100, 6, 0, 0, 15835, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 5, 2, 'What else could possibly go wrong?', 12, 7, 100, 6, 0, 0, 15836, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 5, 3, 'Yes, lieutenant?  Report.', 12, 7, 100, 1, 0, 0, 15837, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 6, 0, 'That\'s troubling news indeed.  See to it that it\'s taken care of immediately!', 12, 7, 100, 1, 0, 0, 15849, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 6, 1, 'I don\'t have time for this, lieutenant.  Make sure that you handle it.', 12, 7, 100, 1, 0, 0, 15850, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 6, 2, 'If we\'re to help with that in any way we\'ll need more people.  Call some of the scouts in to help with that.', 12, 7, 100, 1, 0, 0, 15851, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18745, 6, 3, 'Lieutenant, I want you to make that your top priority.  Notify anyone that you think can help.', 12, 7, 100, 1, 0, 0, 15852, 0, 'Captain Auric Sunchaser');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18952, 0, 1, 'For Spinebreaker!', 12, 0, 100, 0, 0, 0, 12838, 0, 'Bonechewer Scavenger');

