INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (19191, 3, 0, 'Your weapons are nothing to me! Relent!', 14, 0, 100, 0, 0, 0, 16859, 0, 'Arazzius the Cruel');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (19191, 4, 0, 'You have confidence in your skills? Think carefully as the answer may surprise you.', 14, 0, 100, 0, 0, 0, 16860, 0, 'Arazzius the Cruel');

