INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21400, 1, 0, 'It\'s time to PARTY!', 12, 7, 100, 0, 0, 0, 19077, 0, 'Eckert');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21504, 13, 0, 'Rise, young Voldoun.  Do not worry yourself with pleasantries.  I come at the behest of our master, Prince Kael\'thas.  He bids me to check on the progress of the mana bomb.', 12, 0, 100, 1, 0, 0, 19157, 0, 'Pathaleon the Calculator\'s Image');

