INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 0, 0, 'Get to work on those crystals!', 12, 0, 100, 0, 0, 0, 21112, 0, 'Mo\'\'arg Extractor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 0, 1, 'Move it!  These crystals aren\'t going to collect themselves.', 12, 0, 100, 0, 0, 0, 21114, 0, 'Mo\'\'arg Extractor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 0, 2, 'These crystals will work.  Get them!', 12, 0, 100, 0, 0, 0, 21113, 0, 'Mo\'\'arg Extractor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 1, 0, 'At least you didn\'\'t screw this batch up.', 12, 0, 100, 0, 0, 0, 21116, 0, 'Mo\'\'arg Extractor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 1, 1, 'I can\'\'t believe you\'\'ve actually done something right for once.', 12, 0, 100, 0, 0, 0, 21117, 0, 'Mo\'\'arg Extractor');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22304, 1, 2, 'You\'\'ve ruined this batch!  Why must I work with you worthless Gan\'\'arg.', 12, 0, 100, 0, 0, 0, 21115, 0, 'Mo\'\'arg Extractor');

