INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25807, 0, 0, 'Are you crazy? You\'re about to blow us sky high! I\'ll take care of this wreck once everything else is finished.', 12, 0, 100, 0, 0, 0, 25043, 0, 'Iggy Tailspin Cogtoggle');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (29655, 0, 0, 'Saragosa! What has become of you?', 14, 0, 100, 0, 0, 0, 25480, 0, 'Malygos');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34565, 0, 0, 'Mmmm... There\'s nothing like good, home-cooked human food.', 12, 0, 100, 1, 0, 0, 34782, 0, 'Innocuous Townsman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (34565, 1, 0, 'I lied. Your miserable food stinks, but not as much as this town is going to stink!', 12, 0, 100, 11, 0, 0, 34785, 0, 'Innocuous Townsman');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 0, 0, 'Sentinel, I would speak with your High Priestess.', 12, 7, 100, 1, 0, 0, 36428, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 1, 0, 'Must I? I doubt Tyrande has more pressing business.', 12, 7, 100, 6, 0, 0, 36429, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 2, 0, 'The lure of power is great. Mistakes were made.', 12, 7, 100, 1, 0, 0, 36430, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 3, 0, 'I will not return until I am heard. There is too much at stake.', 12, 7, 100, 5, 0, 0, 36431, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 4, 0, 'There is change on the wind, and we cannot ignore it. I have traveled from the dust and ruin of the past to come to an accord.', 12, 7, 100, 1, 0, 0, 36432, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 5, 0, 'The time may soon come when the kaldorei require the knowledge and skill we have to offer.', 12, 7, 100, 1, 0, 0, 36433, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 6, 0, 'I will wait, Sentinel... but I will have my audience.', 12, 7, 100, 1, 0, 0, 36434, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 7, 0, '%s looks over at the moonwell with a sigh.', 16, 0, 100, 0, 0, 0, 36440, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 8, 0, 'Calm yourself, Sentinel. I do not seek to use it. I am merely... remembering.', 12, 7, 100, 1, 0, 0, 36435, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 9, 0, 'Enough. I have been apart from this for centuries. The well within this temple may be a pale spectre of the Well of Eternity, but it still holds the same... beauty. Purity.', 12, 7, 100, 1, 0, 0, 36436, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 10, 0, 'I had forgotten.', 12, 7, 100, 0, 0, 0, 36437, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 11, 0, 'I do not believe admiration and wistful thoughts are against the societal norm.', 12, 7, 100, 6, 0, 0, 36438, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36479, 12, 0, 'You were the one who challenged me for merely looking. I still await my audience with Tyrande.', 12, 7, 100, 1, 0, 0, 36439, 0, 'Archmage Mordent Evenshade');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 0, 0, 'You must wait for an audience.', 12, 7, 100, 25, 0, 0, 36446, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 1, 0, 'You show some nerve coming to Teldrassil, Highborne. Your kind are unwelcome here. We have not forgotten the War of the Ancients or the minds behind it.', 12, 7, 100, 1, 0, 0, 36447, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 2, 0, 'And we would not see those mistakes repeated. Return to your exile.', 12, 7, 100, 5, 0, 0, 36448, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 3, 0, 'Take your gaze off the moonwell, exile. The power within is not yours to wield.', 12, 7, 100, 5, 0, 0, 36449, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 4, 0, 'Recalling your treachery?', 12, 7, 100, 6, 0, 0, 36450, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 5, 0, 'Watch yourself, Highborne.', 12, 7, 100, 25, 0, 0, 36451, 0, 'Sentinel Stillbough');

INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (36481, 6, 0, 'In good time.', 12, 7, 100, 1, 0, 0, 36452, 0, 'Sentinel Stillbough');

