(4440, 'frFR', 'Faites attention, $c. Respectez notre mode de vie ou soyez $gmaudit:maudite; !', 'Faites attention, $c. Respectez notre mode de vie ou soyez $gmaudit:maudite; !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(4441, 'frFR', 'Je ferai ce que je peux pour faire de vous un prêtre, si c\'est ça que vous voulez.', 'Je ferai ce que je peux pour faire de vous un prêtre, si c\'est ça que vous voulez.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(4442, 'frFR', 'Que peut pour vous cet insignifiant serviteur, $N ? Vous entraîner peut-être ?', 'Que peut pour vous cet insignifiant serviteur, $N ? Vous entraîner peut-être ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(4443, 'frFR', 'Je cherche quelqu\'un, $c. Laissez-moi... Il est important que je le trouve.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
