(6274, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6275, 'frFR', 'Salut. Vous êtes au bon endroit si vous voulez apprendre à utiliser une nouvelle arme.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6276, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6277, 'frFR', 'Quelle arme qu’vous voulez apprendre à manier, $gmon p’tit gars:ma p’tite dame; ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6279, 'frFR', 'Ce sera un honneur pour moi de vous apprendre à utiliser les armes que je maîtrise, si vous avez la capacité d\'apprendre.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6281, 'frFR', 'Si vous avez besoin d’un autre apprentissage, je serai heureux de vous indiquer qui pourra vous aider. Quelle arme vous a tapé dans l’œil ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6282, 'frFR', 'Mort aux Foudrepique !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6284, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6286, 'frFR', 'Je suppose que vous venez apprendre l\'utilisation d\'une nouvelle arme ? Faites vite, je n\'ai pas le temps de m\'amuser.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6287, 'frFR', 'Je vous dirai où aller, si vous me dites ce qui vous intéresse.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6288, 'frFR', '$N, c’est bon de voir des vétérans comme vous prendre part à l’action ! Je suis désolé, mais nous n’avons pas assez de matériel pour lancer une offensive terrestre importante contre la Horde.$B$BDiscutez-en avec notre intendant, il a des missions de ravitaillement à vous confier.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6289, 'frFR', 'Êtes-vous ici pour apprendre l\'art du combat armé ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6290, 'frFR', 'Quelle arme vous intéresse en particulier ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6291, 'frFR', NULL, 'La grâce avec laquelle je me bats n\'est pas quelque chose que je peux enseigner, mais je peux vous apprendre à utiliser différentes armes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6292, 'frFR', NULL, 'Je peux vous indiquer d\'autres maîtres d\'armes, si vous préférez. Qu\'est-ce qui vous intéresse ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6293, 'frFR', 'Vous voulez apprendre l\'utilisation d\'une arme, n\'est-ce pas ? Ne restez pas là comme une cruche, parlez !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6294, 'frFR', 'Quelle arme qu\'ça vous dirait d\'apprendre ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6295, 'frFR', NULL, 'Salut ! Vous êtes là pour apprendre à utiliser correctement une nouvelle arme ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6296, 'frFR', NULL, 'Oooh, vous ne voulez pas vous entraîner avec moi ? Quelles sont les autres armes qui vous ont tapé dans l’œil ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
