(7435, 'frFR', '<Le sergent Foudrepique salue.>$B$BNom, grade et numéro d’identité, soldat ! Ce sont les seules choses que vous donnerez jamais à ces chiens de Loup-de-givre !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7436, 'frFR', '$964w, jeune $C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7437, 'frFR', '$962w commandant(s) Foudrepique et $950w lieutenant(s) Foudrepique.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7438, 'frFR', 'Cette vallée nous appartiendra. Je vous le promets.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
