(7514, 'frFR', 'Je suis Jin’rokh le Briseur, envoyé de Mugamba, Poing de Rastakhan !$B$BJe m’adresse aux guerriers et aux païens.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7515, 'frFR', 'Je suis Al’tabim Qui-Voit-Tout, envoyé du roi Rastakhan.$B$BJe m’adresse aux prêtres, aux mages et aux démonistes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7516, 'frFR', 'La Ligue d’Arathor a juré de reprendre Arathi pour les humains de Stromgarde et l’Alliance. C’est une dure bataille, mais elle mérite d’être livrée.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7517, 'frFR', NULL, 'Si nous voulons gagner cette guerre, nous ne devons pas rester assis sans rien faire. Nous devons frapper, vite et fort, ou nous ne reprendrons jamais Arathi.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
