(7683, 'frFR', 'Vous devez gagner en force, $c, avant de pouvoir nous aider dans la vallée d\'Alterac.', 'Vous devez gagner en force, $c, avant de pouvoir nous aider dans la vallée d\'Alterac.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7684, 'frFR', 'Salut, $c. La vallée d’Alterac a grand besoin de soldats, mais il y a beaucoup d’honneur et de renom à y gagner. Voulez-vous rejoindre notre combat contre la garde Foudrepique ?', 'Salut, $c. La vallée d’Alterac a grand besoin de soldats, mais il y a beaucoup d’honneur et de renom à y gagner. Voulez-vous rejoindre notre combat contre la garde Foudrepique ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
