(7688, 'frFR', 'Malheureusement, vous n\'avez pas encore assez de force pour survivre dans le goulet des Chanteguerres, $C.', 'Malheureusement, vous n\'avez pas encore assez de force pour survivre dans le goulet des Chanteguerres, $C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7689, 'frFR', '$C, nous avons grand besoin de votre aide dans le goulet des Chanteguerres contre les Sentinelles d’Aile-argent. Vous pourrez y gagner beaucoup d’honneur et de réputation auprès des voltigeurs Chanteguerre ! Avez-vous le courage de rejoindre la bataille ?', '$C, nous avons grand besoin de votre aide dans le goulet des Chanteguerres contre les Sentinelles d’Aile-argent. Vous pourrez y gagner beaucoup d’honneur et de réputation auprès des voltigeurs Chanteguerre ! Avez-vous le courage de rejoindre la bataille ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(7690, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
