(8286, 'frFR', NULL, 'Comment, vous n\'avez pas de gage à me donner ? Vous ne m\'aimez pas ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8287, 'frFR', 'Je vous parlerais si vous sentiez bon. De l\'eau de Cologne, voilà qui serait parfait.', 'Je vous parlerais si vous sentiez bon. De l\'eau de Cologne, voilà qui serait parfait.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8288, 'frFR', 'Comment, vous n\'avez pas de gage à me donner ? Vous ne m\'aimez pas ?', 'Comment, vous n\'avez pas de gage à me donner ? Vous ne m\'aimez pas ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8289, 'frFR', 'Vous seriez davantage à mon goût si vous mettiez du parfum.', 'Vous seriez davantage à mon goût si vous mettiez du parfum.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8290, 'frFR', 'Vous seriez davantage à mon goût si vous mettiez du parfum.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8291, 'frFR', 'Comment, vous n\'avez pas de gage à me donner ? Vous ne m\'aimez pas ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8292, 'frFR', 'Quoi ? Comment avez-vous fait pour éviter mon geôl… mon sbire, là dehors ? Enfin, je suppose que c’est une bonne chose. Ces temps-ci, les ventes sont médiocres.$B$BQuels besoins infernaux puis-je combler pour vous, $c ?', NULL, 'C’est très astucieux, la façon dont vous êtes $gentré:entrée; ici, $N.$B$BQuoi ? Cela vous surprend que je connaisse votre nom ? Je suis un démon, c’est mon métier. Je connais aussi votre vrai nom, donc vous feriez mieux d’acheter quelque chose de très cher.', NULL, 'Vous êtes ici pour le bâtonnet ? Ou peut-être pour faire une provision de runes ?$B$BAttendez une minute ! Vous essayez seulement de me fourguer tout un tas de trucs inutiles, c’est ça, hein ? Que soit maudite mon obsession continuelle d’acheter, acheter, acheter !', NULL, 'Quoi ? Non, non, je ne répare pas les armures. Long-guet se trouve à une demi-lieue au nord, $gmon frère:ma sœur;.$B$BMaintenant, achetez-moi quelque chose, ou fichez le camp avant que j’appelle mon démoniste pour qu’il s’occupe de vous !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8296, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8298, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8301, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8303, 'frFR', 'Je vous parlerais si vous sentiez bon. Avez-vous pensé à mettre de l\'eau de Cologne ou du parfum ?', 'Je vous parlerais si vous sentiez bon. Avez-vous pensé à mettre de l\'eau de Cologne ou du parfum ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8304, 'frFR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8305, 'frFR', 'Ma mission est achevée. Si vous en avez besoin, il me reste peut-être quelques fournitures.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8306, 'frFR', 'Moi non, mais beaucoup de gens cherchent l’amour. Vous devriez mettre un peu de parfum ou d’eau de Cologne, acheter mes gages d’amour, et les faire circuler !', 'Moi non, mais beaucoup de gens cherchent l’amour. Vous devriez mettre un peu de parfum ou d’eau de Cologne, acheter mes gages d’amour, et les faire circuler !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8307, 'frFR', 'Les gages d’amour sont de petits messages d’affection que vous pouvez donner aux habitants des villes et des capitales qui ont le cœur à l’amour. En échange, elles vous offriront un cadeau d’adoration.$B$BSi vous êtes déjà $gadoré:adorée; par quelqu’un d’autre, elles vous remettront au moins un cadeau d’amitié.', 'Les gages d’amour sont de petits messages d’affection que vous pouvez donner aux habitants des villes et des capitales qui ont le cœur à l’amour. En échange, elles vous offriront un cadeau d’adoration.$B$BSi vous êtes déjà $gadoré:adorée; par quelqu’un d’autre, elles vous remettront au moins un cadeau d’amitié.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(8308, 'frFR', 'Si vous mettez de l’eau de Cologne ou du parfum, vous verrez qui a le cœur à l’amour. Tous acceptent les gages d’amour… et vous offrent un don en échange.$B$BAttention, pour donner un gage d’amour à quelqu’un, vous devez avoir la bonne odeur. Les hommes aiment l’odeur du parfum, et les femmes celles de l’eau de Cologne.', 'Si vous mettez de l’eau de Cologne ou du parfum, vous verrez qui a le cœur à l’amour. Tous acceptent les gages d’amour… et vous offrent un don en échange.$B$BAttention, pour donner un gage d’amour à quelqu’un, vous devez avoir la bonne odeur. Les hommes aiment l’odeur du parfum, et les femmes celles de l’eau de Cologne.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
