(9696, 'frFR', 'Les druides d\'Azeroth organisent en ce moment une grande fête à Reflet-de-Lune. Si vous rencontrez des fêtards dans la ville, ils pourront vous y transporter.', 'Les druides d\'Azeroth organisent en ce moment une grande fête à Reflet-de-Lune. Si vous rencontrez des fêtards dans la ville, ils pourront vous y transporter.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(9698, 'frFR', 'Si vous voulez vraiment faire l\'expérience de la fête du Voile d\'hiver, vous devriez aller à Orgrimmar.', 'Si vous voulez vraiment faire l\'expérience de la fête du Voile d\'hiver, vous devriez aller à Orgrimmar.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
