(10947, 'frFR', 'Je dois partir d\'ici tout de suite !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(10948, 'frFR', 'La relique apogide se met à briller, trahissant la présence d\'une intelligence dissimulée à l\'intérieur.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(10949, 'frFR', 'Le monument apogide se dresse devant vous.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(10950, 'frFR', 'Garde à vous, capitaine !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(10951, 'frFR', 'Les yeux ardents à l\'intérieur du prisme vous font froid dans le dos... d\'autant plus que vous savez que vous n\'avez pas les trente-cinq éclats apogides nécessaires pour en faire sortir le démon.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(10953, 'frFR', 'Vous êtes un jeune oisillon, c\'est ça ? Mes yeux ne sont plus ce qu\'ils étaient.$B$BJe suppose que vous êtes ici pour acheter l\'un de mes textes exotiques ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
