(11714, 'frFR', 'Bienvenue dans mon auberge, $gvoyageur épuisé:voyageuse épuisée;. Que puis-je faire pour vous ?', 'Bienvenue dans mon auberge, $gvoyageur épuisé:voyageuse épuisée;. Que puis-je faire pour vous ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(11724, 'frFR', 'Tant d\'applications pour cette peste, mais si peu de temps pour en profiter !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(11738, 'frFR', NULL, 'Ça fait plaisir de vous voir !$B$BIl y a du pain sur la planche et pas beaucoup de temps.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(11742, 'frFR', 'Gorth a pas peur d\'être tué. Les Réprouvés, ils vont juste me recoudre.', NULL, 'Gorth comprend pas tout, des fois. Gorth tue bien les gens, alors Gorth devient sergent. Mais maintenant, Gorth a plus personne à tuer !', NULL, 'Les vrykuls, ils sont plus grands et plus forts. Mais nous, on est plus beau !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
