(12578, 'frFR', 'Les kvaldirs ignorent la pitié, $N. Mais je ne peux pas abandonner le village sacré de Kaskala. Si nous ne fuyons pas, les esprits ne nous abandonneront pas non plus.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12579, 'frFR', 'Kaskala est attaquée ! Nous devons repousser l\'ennemi en attendant de pouvoir nous enfuir.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12580, 'frFR', 'Maudits hommes-géants ! S\'ils veulent se battre, on va se battre !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
