(12842, 'frFR', 'Quoi ? Vous voyez pas que je suis occupé ?$B$BVous croyez qu\'il va se réparer tout seul, le transmatière ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12843, 'frFR', 'Le généralissime Bolvar Fordragon combat pour nous tous. S\'il n\'était pas là, Arthas serait tranquillement assis sur le trône de Hurlevent à l\'heure qu\'il est.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(12844, 'frFR', 'Je suis si près de la solution. Encore une minute et je mettrai le doigt dessus !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
