(16264, 'frFR', 'Même ici, avec le soleil et les vagues, je sens les ténèbres qui rôdent autour de nous.$B$BC\'est fascinant, vous ne pensez pas ?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(16265, 'frFR', 'Nous sommes fiers d\'être la dernière tribu de trolls des forêts à pouvoir se vanter de servir la Horde.$B$BAvec le temps, nous allons triompher des autres tribus, et des nains, et les Hinterlands seront à nous.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(16269, 'frFR', 'Les trolls m\'acceptent très bien parmi eux. Après tout, ils parlent aux morts depuis des siècles, bien avant la création des Réprouvés.$B$BC\'est bon pour les affaires.', 'Les trolls m\'acceptent très bien parmi eux. Après tout, ils parlent aux morts depuis des siècles, bien avant la création des Réprouvés.$B$BC\'est bon pour les affaires.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
