(16319, 'frFR', 'Throm\'ka, mon $Gami:amie; !$B$BLe chef de guerre m\'a ordonné de rassembler ses meilleurs vétérans pour donner l\'assaut aux hautes-terres du Crépuscule.$B$BVous semblez être $Gun:une:c; $C coriace, mais on a besoin de soldats qui soient un peu plus... endurcis. Peut-être qu\'un de ces jours on se battra côte à côte. Mais en attendant, continuez à vous entraîner !', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(16320, 'frFR', 'Throm\'ka, mon $Gami:amie; ! Ça fait plaisir de retrouver d\'autres vétérans de la campagne du Norfendre.$B$BLe chef de guerre m\'a ordonné de rassembler ses plus grands combattants pour donner l\'assaut aux hautes-terres du Crépuscule.$B$B<Le seigneur de guerre Krogg vous observe attentivement.>$B$BOn pourrait avoir besoin de votre aide. Allez trouver le chef de guerre à Orgrimmar et voyez s\'il a déjà quelque chose à vous confier.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(16321, 'frFR', 'Un soldat pourrait s\'attirer beaucoup d\'ennuis dans une ville comme celle-là.$B$BBeaucoup d\'ennuis.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
