INSERT INTO `version_db_world` (`sql_rev`) VALUES ('1648471089049913097');
REPLACE INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES
(8502, 'ruRU', 'Отличная работа, $N. Без рабочих силитидам придется нелегко. Кто будет восстанавливать их ульи?', 0),
(8505, 'ruRU', 'Твой вклад очень ценен для всех нас, $C. Только объединив наши усилия, мы сможем отбросить армию безжалостных силитидов и одержать победу. Даже Орда занимается сбором лилового лотоса. Если снова наберешь этих цветов, возвращайся ко мне.', 0),
(8507, 'ruRU', 'Отлично, $N. Я подготовила целую серию заданий, которые наилучшим образом распорядятся твоими навыками.$B$BЕсли ты и такие как ты будут биться, не покладая рук, скоро мы изгоним врагов!$B$BЕсли тебе понадобится новое задание, возвращайся к капитану Черной Наковальне.', 0),
(8508, 'ruRU', 'Да, я полагаю, что эта небольшая стычка перейдет в боевое дежурство. Отнеси их обратно в Крепость Кенария, я уверен, у них для тебя найдется работа.', 0),
(8509, 'ruRU', 'Неплохо сработано, $C. Должна сказать, я приятно удивлена. Ты никогда бы не $Gповерил:поверила;, сколько людей приходят, обещают помочь и не возвращаются. Одну секунду, сейчас я упакую травы в ящик и прикреплю к нему бирку с твоим именем, а потом сможем поговорить на тему того, нужны ли мне еще эти растения.', 0),
(8515, 'ruRU', 'О, я знала, что ты справишься, $C, конечно же, я это знала! Я отправлю эту кожу по нужному адресу, как только смогу. Ага, а теперь давай-ка посмотрим. Похоже, в итоге все получается очень, очень миленько. Загляни ко мне еще разочек, я сейчас проверю, не нужно ли нам еще кожи. Если понадобится, то было бы так здорово, если бы ты $Gсмог:смогла; достать для нас еще!', 0),
(8516, 'ruRU', 'У тебя снова все получилось! Просто невероятно, как ты умудряешься выбраться отсюда, собрать все, что нужно, а потом еще вернуться и сдать все мне. Когда люди узнают, сколько ты для нас $Gсделал:сделала;, они встанут в очередь за твоими автографами!$B$BБольшое-пребольшое спасибо за столь благородное и щедрое пожертвование плотной кожи, $N.', 0),
(8519, 'ruRU', 'После предательства Оленьего Шлема я собрал осколки скипетра Зыбучих песков и вернулся сюда. Я желал охранить скипетр от посягательств тех, кто был готов воспользоваться им во зло – так были созданы драконы.$B$BМне казалось, что если разделить части скипетра между четырьмя могущественными Аспектами, то взыскующие Хаос не смогут восстановить его. Я был глупцом. Проклятые драконы охотятся на меня по сей день.', 0),
(8521, 'ruRU', 'Приятно иметь дело с бескорыстными ребятами вроде тебя, $N. Мы не забудем твой вклад в подготовку к войне. Именно от таких, как ты, и зависит, победим мы или проиграем. Я надеюсь, что наших усилий хватит для победы.$B$BЕще раз большое спасибо!', 0),
(8523, 'ruRU', 'Я рад что этот путь мы проделали вместе, $N. Работать с тобой – одно удовольствие. Хочу еще раз поблагодарить за бескорыстную помощь Альянсу тебя и всех, подобных тебе. Возращайся скорей, побеседуем еще.', 0),
(8528, 'ruRU', 'Спасибо за быструю и своевременную помощь, $C. Теперь наши солдаты не будут испытывать недостатка в провианте. Подожди немного, я позабочусь о размещении рыбы на складе, и мы обсудим доставку еще одной порции, если ты не против.', 0),
(8529, 'ruRU', 'Сразу видно, что ты заботишься о нуждах Альянса, как о своих собственных, $N. Твои старания не останутся незамеченными, и награда обязательно найдет своего героя. Спасибо за отлично приготовленную рыбу. Теперь ее можно хранить долго и без особых проблем. Пойду-ка я запишу, сколько ты $Gпринес:принесла;. Когда близится война, на счету должна быть каждая рыбина!', 0),
(8532, 'ruRU', 'Вот они, слитки, хорошо, что ты не $Gстал:стала; полировать их. В блеске нет никакого смысла, они все равно пойдут на военные нужды. Пойду уложу их в штабели, и нужно позаботиться, чтобы они были пущены в дело. Если добудешь еще двадцать, обращайся прямо ко мне.', 0),
(8533, 'ruRU', 'Ты опять $Gдоказал:доказала;, что ты многого стоишь, $C. Я чувствую, эта медь хорошо нам послужит, когда придет время. А теперь мне надо сложить ее в штабеля и подсчитать. Если у тебя не пропало желание, ты, вероятно, сможешь помочь нам, если принесешь еще медных слитков.', 0),
(8534, 'ruRU', 'Мы очень ценим твои усилия, $N. Я немедленно займусь этим донесением, а ты можешь переходить к следующему заданию.', 0),
(8538, 'ruRU', 'Сегодня ты добился большого успеха, $N. Ты $Gпобедил:победила; некоторых из наших самых могущественных врагов. За это я тебе очень благодарен.', 0),
(8539, 'ruRU', 'Отличная работа, $N. Ты очень ценный союзник.', 0),
(8543, 'ruRU', 'Тром\'ка, $N! Своими делами ты оказываешь и мне, и Орде большую честь. Сейчас я позабочусь об этих слитках. А когда мы сделаем все, что нужно, то отправимся на войну и покажем этому ничтожному Альянсу, как надо давить тараканов!$B$BТак что ступай и принеси еще слитков!', 0),
(8545, 'ruRU', 'Ну вот, очень неплохой зачин в нашей работе, $C. Теперь на складе лежит еще 20 новеньких слиточков, сейчас я внесу их в список. Когда на пороге война, на счету каждая крупинка мифрила!$B$BТы заглядывай, похоже, нам скоро еще понадобятся слитки.', 0),
(8546, 'ruRU', 'Отлично сработано, $N! Мы ближе к цели на целых двадцать слитков. Сейчас я прослежу, чтобы их отгрузили. Если раздобудешь еще двадцать, дружище, сразу же тащи их мне.', 0),
(8549, 'ruRU', 'Подготовка близится к концу, и скоро грянет война. Спасибо за помощь, $N. Мироцвет очень нужен нам, и твой вклад в общее дело не останется незамеченным. Если сможешь, возвращайся и помоги мне собрать еще мироцвета. А пока ступай, и пусть Мать-Земля хранит тебя.', 0),
(8550, 'ruRU', 'Спасибо, что продолжаешь помогать нам, $N. Твоя самоотдача делает честь тебе и твоим предкам. Пойду упакую травы, чтобы они не потеряли своей свежести к тому времени, когда они понадобятся. Продолжай трудиться на благо нашей победы, во имя Орды!', 0),
(8551, 'ruRU', 'Ты его $Gнашел:нашла;! Славный день для меня. Спасибо тебе, $N! Теперь, когда мой сундук ко мне вернулся, я немного успокоился', 0),
(8552, 'ruRU', 'Тебе удалось вернуть мой кушак? Тогда... ты, должно быть, убил морского великана! Урааа!$B$BКак я рад, что этот негодяй мертв – Мок\'раш был одним из трех великанов, которые уничтожили мои корабли и поубивали всю команду.$B$BИ так три раза подряд!$B$BТы достойный $C, $N.', 0),
(8553, 'ruRU', 'Привет, привет, $N. Капитан сказал мне, что ты будешь бороться с Неголашем, да?', 0),
(8554, 'ruRU', 'Ты $Gзабрал:забрала; саблю у Неголаша! Я не верил, что мне повезет, $N! Встретив тебя, моя удача вернулась, без сомнения!$B$BСпасибо! И если я когда-нибудь заполучу новый корабль, и ты захочешь прокатиться, то ты будешь не нем почетным гостем.', 0),
(8555, 'ruRU', 'Эраникус, Ваэластраз и Азурегос... Несомненно, ты знаешь об этих драконах, смертный. Поэтому не случайно, что они играли такие влиятельные роли в качестве наблюдателей за нашим миром.$B$BК сожалению (и частично виновата моя собственная наивность), будь то агенты Древних богов или предательство со стороны тех, кто называл их друзьями, каждый страж трагически погиб. Это подогревало мое недоверие к твоему виду.$B$BИ $R, приготовьcя к худшему.', 0),
(8556, 'ruRU', 'Ты $Gпринес:принесла; мне кольцо и материалы, $N?', 0),
(8557, 'ruRU', 'Воспользуйся этим плащом, $N. Пусть он защитит тебя от нечестивой магии Киражи.', 0),
(8558, 'ruRU', 'Ты отлично поработал, |3-6($C). Это шедевр с рукоятью... баланс просто совершенен.$B$BВозьми это оружие, $N. Пусть оно поможет тебе победить наших врагов.', 0),
(8560, 'ruRU', 'Да... использовав шкуру червя на вещи, можно не плохо усилить ее. Возьми эти набедренники. Пусть они помогут тебе в сражении против Киражи!', 0),
(8575, 'ruRU', '<Нарайн рассматривает письмо>$B$BВыдающийся, $R! Что это? Я не могу читать это.', 0),
(8576, 'ruRU', 'Перво-наперво, $N! Мы должны выяснить, что написал Азурегос в этой книге.$B$BГоворишь, что он сказал тебе сделать арканитовый буй и это есть схема? Странно, что он использовал драконий язык, зная, что я не могу читать это.$B$BНу что же, тогда мне понадобятся мои гадальные очки, 500-фунтовая курица и второй том книги \'Драконий язык для чайников\'. Не обязательно в таком порядке.', 0),
(8577, 'ruRU', 'Я не могу поверить, что он нашел меня. Я был уверен, что потерял его, когда был в Запределье. Я не собираюсь возвращаться туда, $R! Никогда!', 0),
(8578, 'ruRU', 'Замечательно! А куда же я дел 500-фунтовую курицу и второй том \'Драконий язык для чайников\'? Да, вот кое-что тебе, за твою тяжелую работу.', 0),
(8580, 'ruRU', '$C! Пеле\'кейки просто счастлив. Сейчас он заберет эти цветочки и сложит их в коробку, чтоб не попортились. Уж Пеле\'кейки присмотрит, чтоб их употребили на всякие военные штуки, а не на какую-нибудь дребедень. А если добудешь еще цветочков, сразу же тащи Пеле\'кейки, ему это всяко понадобится.', 0),
(8581, 'ruRU', 'Это не шутка, $C? Ты так быстро $Gпринес:принесла; огнецвета Пеле\'кейке? Пеле\'кейки пересчитает их прямо сейчас, дружище! Ты делаешь Пеле\'кейки таким счастливым, он всем рассказывал, $N лучший коллекционер огнецвета! Пеле\'кейки не может дождаться начала войны.', 0),
(8582, 'ruRU', 'Твоя работа заслуживает одобрения, $C. Я должна рассортировать образцы и не забыть отметить, что это был твой вклад. Пока у меня не дойдут руки до этих цветов, нужно их правильно хранить. Как ты $Gмог:могла; отметить, лотоса нужно гораздо больше, чем мы сейчас имеем. Далеко не уходи.', 0),
(8583, 'ruRU', 'Безукоризненно точная работа, $N. Не могу не похвалить твое прилежание, а так же быстроту. Я лично присмотрю, чтобы ни один из этих цветков не пропал понапрасну.$B$BБольшое тебе спасибо. Если соберешь еще лилового лотоса, бегом неси ко мне.', 0),
(8584, 'ruRU', '<Могиль чешет в затылке.>$B$B500-фунтовая курица? Таких не бывает! То есть, уже не бывает. Знавал я такую птицу... Черного бройлера-убийцу...$B$B<У Могиля дрожит голос.>$B$BНо его давно уже никто не видел, и эта птица теперь существует только в сказках. Однако у меня есть идейка, от которой будет выгода нам обоим.', 0),
(8585, 'ruRU', 'Посмотри на это филе! Это просто фантастика!$B$BТеперь о курице... Хм, дай посмотреть. Мне нужно несколько минут, чтобы разобраться в рецепте.', 0),
(8586, 'ruRU', 'Все готово! Я представляю тебе свое величайшее творение: зажигательные отбивные Могиля из химерока!', 0),
(8587, 'ruRU', '<Нарайн помещает обе руки на цыпленка и запихивает в него свое лицо.>$B$BМммм... Это невероятно! Лучший цыпленок, которого я когда-то видел!', 0),
(8588, 'ruRU', 'О, тебе удалось достать толстую кожу. У тебя острый нож, $C, кожи отлично выделаны! Пойду уберу их на склад. Нужно убедиться, что твой вклад не пропадет и послужит на благо Орде. Если удастся раздобыть еще, неси сюда.', 0),
(8589, 'ruRU', 'Ну ты даешь, дружище! Огромное тебе спасибо за эти кожи. Каждая из них хоть немного, но приближает нас к завершению военной подготовки. Пойду сдам кожи на склад, чтобы кожевники могли приступить к работе.$B$BБольшое тебе спасибо еще раз. Если раздобудешь следующую порцию кож, то ты знаешь, где меня найти.', 0),
(8590, 'ruRU', 'Ты хорошо постарался, $C. Эта кожа не будет истрачена зря – она пойдет на усиление наших дирижаблей и изготовление оружия и брони. Благодарю тебя от лица всей Орды. Если тебе в своих путешествиях удастся добыть еще плотной кожи, неси ее мне. И удачи тебе.', 0),
(8591, 'ruRU', 'Ты справился, я знала, что у тебя все выйдет! От имени всей Орды я снова благодарю тебя. Твой вклад не останется незамеченным. Если достанешь еще плотной кожи, приноси ее мне.$B$BЛок\'тар огар, $C!', 0),
(8593, 'ruRU', 'Да... Эту шкуру невозможно пробить почти ничем. Если добавить самые прочные куски панцирей киражи, убитых нами по дороге сюда, из нее получится великолепный доспех. Пусть он защитит тебя при встрече с невообразимыми ужасами, что ожидают внутри Ан\'Киража.', 0),
(8595, 'ruRU', 'Мы довольны твоей службой, $N. Наш договор остается в силе.', 0),
(8597, 'ruRU', 'Книга пропала! Но здесь лежит записка.', 0),
(8598, 'ruRU', '<Лицо Нарайна окрашивается в красный цвет.>$B$BНу хорошо... я покажу им, что значит связаться с гномом!', 0),
(8599, 'ruRU', 'Что!?$B$B*Нарайн начинает суетиться.*$B$BНу, хммм... От меня-то вы чего хотите??? Она же рыба! РЫБА, говорю вам! А я гном. У нас все равно ничего бы не вышло.', 0),
(8600, 'ruRU', 'Прекрасно, прекрасно, ты $Gпроделал:проделала; неплохую работенку, хотя некоторые из этих кож по краям обрезаны не слишком ровно. Но, без сомнений, задание выполнено, и ты получишь обещанную награду. Если вдруг раздобудешь еще прочной кожи, то тащи ее мне поскорее.', 0),
(8601, 'ruRU', 'Тренировка, тренировка и еще раз тренировка. Хорошо заточенный нож и умение им пользоваться еще не раз сослужат тебе добрую службу, $N. Похоже, края этих кож стали ровнее, чем было вначале. Ты $Gкупил:купила; себе новый нож?$B$BВ любом случае, большое спасибо за потраченные силы. Твой вклад не останется незамеченным, уж будь $Gуверен:уверена;. Если вдруг сыщещь еще прочной кожи, приходи узнать, не нужна ли она мне.', 0),
(8603, 'ruRU', 'Я не ожидала, что ты сумеешь вернуться $Gживым:живой; $N.$B$BТебе удалось исполнить то, что не под силу многим бессмертным.$B$BУбив К\'Туна, ты не только $Gспас:спасла; Азерот, ты $Gпоколоебал:поколебала; чаши космических весов, на которых лежат события, далеко превосходящие твое понимание.$B$BПрими эти одежды как символ великой славы и знак великого груза, который ты $Gвзвалил:взвалила; на себя своим поступком.$B$BБыть может, они помогут преодолеть тяготы пути, который отныне лежит перед тобой, богоубийца.', 0),
(8605, 'ruRU', 'Ты знаешь, что такое настоящее благородство, $N! Я прослежу, чтобы эти бинты попали в нужные руки. Прими мою благодарность и благодарность Орды.$B$BСтупай с миром и обязательно возвращайся, если у тебя будут лишние шерстяные бинты. Нам понадобится вся помощь, которая только возможна.', 0),
(8606, 'ruRU', 'Возможно мы их недооценили.', 0),
(8607, 'ruRU', 'Удивительно, но все в порядке. Возможно, я недооценила твои способности, $C. Все возможно. К тому же, задание было несложное, а награда гораздо больше, чем следует. Впрочем, не бери в голову. Если хочешь в самом деле доказать, чего стоишь, ступай и принеси еще бинтов из магической ткани.', 0),
(8608, 'ruRU', 'Просто поразительно, $C, тебе опять все удалось! Ну, говорят, что у меня нюх на помощников. Хорошо, твой вклад не останется незамеченным и ты несоменно получишь социальные льготы соразмерно своим заслугам. А пока, я полагаю, тебе следует вернуться к сбору бинтов из магической ткани, если ты действительно хочешь доказать, чего стоишь.', 0),
(8609, 'ruRU', 'Орда в долгу перед тобой, $C. Спасибо тебе за то, что ты для нас $сделал:сделала;, мы сохраним твои поступки в своем сердце. Я должна складировать эти бинты и посчитать, насколько мы приблизились к выполнению цели. Если получится, добудь еще бинтов из рунной ткани и возвращайся ко мне.', 0),
(8610, 'ruRU', 'С твоей стороны это очень бескорыстный поступок, $N. Может быть, именно он спасет жизнь многим бойцам нашей армии в Ан\'Кираже. Орда в долгу перед тобой. Если сумеешь достать еще бинтов из рунной ткани, пожалуйста, неси их мне.', 0),
(8614, 'ruRU', 'Лучший улов сезона, $N! Отличная работенка. Вот это я понимаю, никаких костей в рыбе. За такого желтохвоста всякий боец спасибо скажет. Наловишь еще, тащи сюда обзательно.', 0),
(8620, 'ruRU', 'Фантастика! Поверить не могу что вы прошли через барьер! Вы часом не машина? Будь я на вашем месте – да горел бы этот мир синим огнем!$B$BСнимаю перед вами тюрбан $Gсэр:мисс;. И тоже доброе дело не должно остаться без награды.', 0),
(8625, 'ruRU', 'Я изготовила это наплечье, $Gсмертный:смертная;, из принесенных тобой вещей и панцирей поверженных киражи. Тебя они защитят, а сердца врагов их вид наполнит ужасом.', 0),
(8633, 'ruRU', 'Я не ожидала, что ты сумеешь вернуться $Gживым:живой;, $N.$B$BТебе удалось исполнить то, что не под силу многим бессмертным.$B$BУбив К\'Туна, ты не только $Gспас:спасла; Азерот, ты $Gпоколоебал:поколебала; чаши космических весов, на которых лежат события, далеко превосходящие твое понимание.$B$BПрими эти одежды как символ великой славы и знак великого груза, который ты $Gвзвалил:взвалила; на себя своим поступком.$B$BБыть может, они помогут преодолеть тяготы пути, который отныне лежит перед тобой, богоубийца.', 0),
(8687, 'ruRU', 'Отличная работа, $N. Без копаталей силитидам придется нелегко. Кто будет восстанавливать строение улья? Думаю, ты хочешь получить новое задание.', 0),
(8728, 'ruRU', 'Сделка есть сделка. Я очень надеюсь, что ты знаешь, во что ввязываешься, $N. Чтобы порыбачить, придется пройти через очень много проблем.', 0),
(8729, 'ruRU', 'Молодец, $N. Я скоро смогу переделать Скипетр Зыбучих Песков.', 0),
(8730, 'ruRU', 'Душа Ваэластраза успокойна, $Gчемпион:чемпионка;. Все Аспекты с большим интересом следят за твоими успехами. Знай, что у тебя есть сильные союзники.$B$BМне было велено дать тебе кое-что, чтобы помочь тебе в этой борьбе. Используй это правильно...', 0),
(8731, 'ruRU', 'Сразу видно, что Круг немного внимания уделяет писанине. Он подписал эти бумаги кровью силитидов!$B$BХорошо, Илиторий. Я подготовил для тебя задания, которые наилучшим образом используют твои способности, $C.$B$BЕсли ты и такие как ты будут биться, не покладая рук, скоро мы изгоним врагов!', 0),
(8732, 'ruRU', 'Да, я полагаю, что эта небольшая стычка перейдет в боевое дежурство. Отнеси их обратно в Крепость Кенария, я уверен, у них для тебя найдется работа.', 0),
(8733, 'ruRU', 'Огонек молчит, только иногда тихо жужжит. Странно, но он способен ощаться с вами мысленно.', 0),
(8734, 'ruRU', 'Невозможно будет выманить Эраникуса из Сна, если он того не пожелает. Однако, его намерения заставят его прийти в наш мир. Он жаждет уничтожить живое проявление Малфуриона. Если мы добьемся успеха, это может погубить одного из самых могучих союзников Сна. Кошмар полностью поглотит все, что еще сохранилось, если Малфурион падет...', 0),
(8735, 'ruRU', '$GГотов:Готова; ли ты? Готов ли мир встретить то, что мы собираемся призвать? Я не знаю... У меня есть долг перед Малфурионом. Я связан долгом и честью...', 0),
(8736, 'ruRU', 'Он пришел в себя, $N. Ныне он возвращаяется в Сон, дабы исправить тот вред, что причинил. Эраникус станет могучим союзником Малфуриону и моему отцу.$B$BПрими эти доспехи, $N, они подарены мне самой Изерой. Они пригодятся тебе в сражении с киражами.', 0),
(8737, 'ruRU', 'Отличная работа, $N. Вот следующее задание.', 0),
(8740, 'ruRU', 'Молодец, $N. Сумеречные мародеры причинили нам множество неприятностей. Мириться с ними было нельзя. Вот тебе следующее задание.', 0),
(8741, 'ruRU', 'О твоих подвигах будет знать каждый, чемпион. Они будут петь тебе дифирамбы от Оргриммара до дальних уголков Южных морей. Все будут знать о чемпионе Бронзового полета, $N.', 0),
(8742, 'ruRU', 'Прошла тысяча лет, и, как и было суждено, теперь ты передо мной. Тот, кто приведет свой народ к новой эре.$B$BСтарый Бог трепещет, $N. О да, он боится твоей веры. Разрушь пророчество К\'Туна.$B$BОн знает, что ты $Gпришел:пришла;, чемпион - и вместе с тобой приходит мощь Калимдора. Ты $Gдолжен:должна; только сообщить мне, когда будешь $Gготов:готова;, и я дам тебе Скипетр Зыбучих песков.', 0),
(8743, 'ruRU', 'Из земли возле гонга вырывается особый кристалл.', 0),
(8744, 'ruRU', 'Надпись на подарке гласит:$B$B$GДорогой:Дорогая; $N,$B$BЖелаю счастливого праздника Зимнего Покрова.', 0),
(8746, 'ruRU', 'Ты $Gсделал:сделала; это! Ура!$B$BМетцен прилетел обратно в конюшни прадеда Винтера - да, Метцен действительно умеет летать - совсем недавно! Пыль действовала как заклинание. Я знал, что так и будет. Ты $Gспас:спасла; Праздник Зимнего Покрова и Пастбища Дымного Леса от катастрофы. Спасибо, $N.$B$BПослушай...за что ты здесь $Gсделал:сделала;, я хочу, чтобы ты $Gполучил:получила; это. Это что-то ОЧЕНЬ особенное.Если у вас есть маунт, я думаю, вам это понравится. С Зимним Покровом!', 0),
(8747, 'ruRU', 'Недурно, $Gмалыш:малышка;. Тебе пришлось немало попотеть, чтобы заслужить благосклонность Бронзовых драконов. Мы не забудем твоих усилий.$B$BПерстень-печатка победителя придаст мощи твоим ударам.$B$BЕще раз подумай хорошенько. Избрав путь, ты не сможешь сойти с него.', 0),
(8751, 'ruRU', 'Хвала тебе, $GЗащитник:Защитница; Калимдора! Великая тебе хвала!', 0),
(8752, 'ruRU', 'Недурно, $Gмалыш:малышка;. Тебе пришлось немало попотеть, чтобы заслужить благосклонность Бронзовых драконов. Мы не забудем твоих усилий.$B$BПерстень-печатка победителя придаст мощи твоим ударам.$B$BЕще раз подумай хорошенько. Избрав путь, ты не сможешь сойти с него.', 0),
(8756, 'ruRU', 'Хвала тебе, $GЗащитник:Защитница; Калимдора! Великая тебе хвала!', 0),
(8757, 'ruRU', 'Недурно, $Gмалыш:малышка;. Тебе пришлось немало попотеть, чтобы заслужить благосклонность Бронзовых драконов. Мы не забудем твоих усилий.$B$BПерстень-печатка победителя придаст мощи твоим ударам.$B$BЕще раз подумай хорошенько. Избрав путь, ты не сможешь сойти с него.', 0),
(8761, 'ruRU', 'Хвала тебе, $GВеликий:Великая; $Gзаклинатель:заклинательница;! Великая тебе хвала!', 0),
(8768, 'ruRU', 'На нарядно упакованном подарке стоит ваше имя.$B$BСтойте, он шевельнулся или показалось?', 0),
(8769, 'ruRU', 'Пожелание счастливого праздника Зимнего Покрова от друзей из \'Пастбищ Дымного Леса\'.', 0),
(8770, 'ruRU', 'Отличная работа, $N. Мы не забудем твоих усилий.', 0),
(8772, 'ruRU', 'Отличная работа, $N. Твои подвиги в сражении при Улье Зора не прошли незамеченными.', 0),
(8773, 'ruRU', 'Я слышал немало добрых слов о твоем участии в нападении на Улей Зора. Продолжай в том же духе, и победа будет за нами.', 0),
(8778, 'ruRU', 'Ах, да! Они очень пригодятся! Большое спасибо, $N!', 0),
(8780, 'ruRU', 'Спасибо, я передам их нашим ребятам.', 0),
(8781, 'ruRU', 'Отлично! Мне больше не придется слышать жалобы на тупые лезвия. Спасибо, $N.', 0),
(8784, 'ruRU', 'Какая поразительная находка, $N. Просто изумительная! Определенно, она поможет нам проникнуть в извращенные умы наших врагов.', 0),
(8785, 'ruRU', 'Ах, да. То что нужно! Из этих веществ я сделаю особое зелье. Оно оч-чень не понравится силитидам!', 0),
(8786, 'ruRU', 'Превосходная работа. Нам их не хватало, $N.', 0),
(8787, 'ruRU', 'Отлично... Я раздам все комплекты солдатам. Легион Оргриммара благодарит тебя, $N!', 0),
(8789, 'ruRU', 'Это оружие, усиленное элементием, одинаково легко пронзит как обычного киражи, так и их бога.', 0),
(8790, 'ruRU', 'Используй это оружие во благо, $N. Пусть киражи ощутят ту боль, которую они так жаждут причинить другим.', 0),
(8791, 'ruRU', 'Выражаю тебе нашу наиглубочайшую благодарность. Ты $Gоказал:оказала; огромную услугу жителям Калимдора. Можешь выбрать один из этих амулетов, $N.', 0),
(8798, 'ruRU', 'Ты тоже инженер, прекрасно! Так, как ты $Gпомог:помогла; мне проучить моих друзей, я обучу тебя созданию своего собственного йети!$B$BЧто скажешь, $N? Не желаешь узнать секрет создания механического йети?', 0),
(8800, 'ruRU', 'Тебя прислал Калдон? Хорошо, посмотрим, чем я могу тебе помочь.', 0),
(8801, 'ruRU', 'Теперь мы можем покинуть это место, $GГосподин:Госпожа; $N. Великое зло полностью уничтожено, и ваши странствия, которые войдут в легенды, близятся к своему завершению.', 0),
(8802, 'ruRU', 'Повелитель оставил это для вас, $Gзащитник:защитница;. В тайнике безвременья хранятся бесценные артефакты. Можете выбрать, что пожелаете.', 0),
(8803, 'ruRU', 'Надпись на подарке гласит:$B$B$GДорогой:Дорогая; $N.$B$BНадеюсь, это поможет тебе нести мир и радость зимних дней всему Азероту.$B$BТвой Дедушка Зима.', 0),
(8829, 'ruRU', 'Да, думаю, собранных тобой материалов хватит. Я окрашу это одеяние в цвета, которые носят посланники Сумеречного Молота. Да поможет Элуна вновь обмануть наших врагов.$B$BНо не беспокойся, когда ты выполнишь свою роль в этой игре, то получишь достойную награду. Ты $Gверный:верная; и $Gдостойный:достойная; $Gсоюзник:союзница;, $N.', 0),
(8857, 'ruRU', 'Ох, сколько же я наделаю цыплят из этого отличного панциря! Молодец, $Gмой:моя; $Gмальчик:девочка;!', 0),
(8858, 'ruRU', 'Великолепно. Из этого хитина можно сделать необыкновенно мощный доспех. Вот обещанные богатства.', 0),
(8859, 'ruRU', 'Отлично, и как раз вовремя! $GМолодец:Умница;.', 0),
(8860, 'ruRU', 'О, припасы от Пастбища Дымного Леса! Чудесно! Они мне понадобятся, чтобы накормить тех, кто собрался на праздник.$B$BСпасибо тебе, $N. Вот, возьми деньги и не забудь как следует выпить и поплясать на праздновании!', 0),
(8862, 'ruRU', 'Что ж, я принимаю эти монеты предков. Пожалуйста, возьми свечу Элуны с моего благословения. Также, пожалуйста, насладитесь этим бесплатным фейерверком.$B$BПусть твой Лунный праздник будет радостным и наполненным весельем, $N.$B$BИ если вы решите сразиться с Оменом и его приспешниками, свеча Элуны вам пригодится...', 0),
(8863, 'ruRU', 'Замечательно! Наслаждайся клецками и прими мои поздравления. Хотя это всего лишь крохи благодарности по сравнению с той славой, которой ты покрыл себя, свершая великие деяния, мне кажется, что они тебе все равно понравятся.$B$BА если бы у тебя были еще монетки наследия, то у меня нашлось бы для тебя много интересных вещей.', 0),
(8864, 'ruRU', 'Отлично – беру эти монеты наследия. Бери платье и мое благословение. И полюбуйся, пожалуйста, праздничным фейерверком.$B$BТочно так же, как ты $Gпринес:принесла; честь и славу своим старшим, ты $Gоказал:оказала; честь и мне, посетив меня. Спасибо тебе, и радостного Лунного фестиваля!', 0),
(8865, 'ruRU', 'Идет – я беру эти монеты наследия. Бери этот брючный костюм и мое благословение. И полюбуся, пожалуйста, праздничным фейерверком!$B$BЕсли у тебя найдется еще несколько монет наследия, обращайся ко мне. Я буду продавать разные вещи в течение всего Лунного фестиваля.', 0),
(8876, 'ruRU', 'О, великолепно. Вот тебе инструкция, $N, и да благословит тебя Элуна.', 0),
(8877, 'ruRU', 'Вот, $N. Это чертеж пусковой установки для фейерверков. Ставишь ее там, где хочешь повеселиться, и приглашай друзей на праздник!', 0),
(8878, 'ruRU', 'Вот твои выкройки, $N. Изделия, которые ты сошьешь благодаря им, будут вне всякого сомнения прекрасны.', 0),
(8879, 'ruRU', 'Отлично, беру. Вот твои инструкции, $N. Воспользуйся ими к вящей славе Элуны!', 0),
(8880, 'ruRU', 'Вот, $N. Бери эти чертежи и изучай их секреты. Желаю тебе удачи и надеюсь в будущем увидеть твои прекрасные творения.', 0),
(8881, 'ruRU', 'Спасибо, $N. Вот рецепт большой связки фейерверков. Пусть ваши товары принесут радость и удачу всем, кто на них смотрит.', 0),
(8882, 'ruRU', 'Вот ваша схема. Изучите её внимательно…$B$BУдачи, $N. Пусть ваши торжества будут соперничать с самим Лунным фестивалем!', 0),
(8886, 'ruRU', 'Прекрасно! Ты $Gспас:спасла; меня от разорения и $Gотомстил:отомстила; этим мерзким тварям!$B$BТеперь, если мне только удастся уговорить наших славных следопытов помочь мне с грузом, я наконец-то выберусь отсюда! Надо было вернуться сразу, как только Велендрис сумел отбить верфь.$B$BВот, возьми. Большего я себе позволить не могу, но должна же я хоть как-то вознаградить тебя за труды!', 0),
(8887, 'ruRU', 'Ох, |3-6($C), ты прелесть, просто прелесть! Я и понятия не имела, что эти гадкие мурлоки из племени Темной Чешуи украли еще и навигационные карты! А ведь без них я не смогла бы ходить по морю, даже если бы мы отвоевали причал и отремонтировали мой корабль!$B$BСпасибо тебе большое! Вот, возьми это золото в знак моей благодарности!', 0),
(8892, 'ruRU', 'Пусть знают, что с нами лучше не связываться! Спасибо, $N.', 0),
(8897, 'ruRU', 'Как это мило! Он впервые догадался мне написать. Ты думаешь, он это серьезно?$B$BКолара Нельдрик? По-моему, звучит неплохо. О, но я забегаю вперед. Спасибо тебе.', 0),
(8898, 'ruRU', 'Тормек? О да, я помню его. У него была такая красивая борода, и он все время в нее улыбался.$B$BАх, поверить не могу, что он меня не забыл!', 0),
(8899, 'ruRU', 'Ничего подобного я прежде не получала. Как трогательно!', 0),
(8900, 'ruRU', 'Вот не ожидала, что орк окажется таким красноречивым. Конечно же, до нынешних времен у нас не было возможности как следует их узнать. Спасибо за доставку письма!', 0),
(8901, 'ruRU', 'Какие милые слова написал мне Темма! Но, хотя его слова меня тронули, я не могу всерьез рассматривать любовь таурена. Мы слишком разные по крови, разуму и духу.$B$BКак хорошо, что он не сделал мне это предложение лично. Мне было бы невыносимо разбивать ему сердце.', 0),
(8902, 'ruRU', 'Это возвращает воспоминания о прошлой жизни, $N. Эления Тор.Знаешь, это должно было быть моим именем. У нас была бы красивая церемония на берегу озера.$B$BЗначит, простые мечты.$B$BНо если бы он увидел меня сейчас, я сомневаюсь, что он вообще увидел бы во мне Элению. Только холодная плоть и мертвое сердце...', 0),
(8918, 'ruRU', 'О, да... Мукс будет в восторге.$B$BЭти наручи – часть набора доспехов. Если ты согласишься помогать мне и дальше, то я, пожалуй, готов буду расстаться с остальным.', 0),
(8921, 'ruRU', 'Да, именно этих предметов мне и не хватало. Довольно таки быстро, $N!$B$BЗа считанные минуты, твой дистиллятор будет готов к работе!', 0),
