	(7675, 1, 'deDE', 'Ich habe viele Eurer Oger getötet, Lantresor. Ich habe keine Furcht.', ''),
	(7676, 0, 'deDE', 'Was muss ich machen?', NULL),
	(7676, 0, 'esES', '¿Qué debo hacer?', NULL),
	(7676, 0, 'esMX', '¿Qué debo hacer?', NULL),
