(2991,'frFR','<HTML>\n<BODY>\n<BR/>\n<P>Traduction approximative...</P>\n<BR/>\n<P>Par la lumière, par la lumière du soleil,</P>\n<P>Enfants du sang,</P>\n<P>Nos ennemis forcent l\'entrée.</P>\n<P>Enfants du sang,</P>\n<P>Par la lumière,</P>\n<P>Défaillants enfants du sang,</P>\n<P>Ils forcent l\'entrée.</P>\n<P>Ô enfants du sang</P>\n<P>Par la lumière du soleil.</P>\n<P>Défaillants enfants du sang,</P>\n<P>Ils forcent l\'entrée.</P>\n<P>Ô enfants du sang,</P>\n<P>Par la lumière du soleil,</P>\n<P>Du soleil.</P>\n</BODY>\n</HTML>',0),
(2991,'ruRU','<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Вольный перевод...</P>\r\n<BR/>\r\n<P>Во имя света, во имя солнечного света,</P>\r\n<P>Дети крови,</P>\r\n<P>Наши враги побеждают.</P>\r\n<P>Дети крови,</P>\r\n<P>Во имя света,</P>\r\n<P>Слабеющие дети крови,</P>\r\n<P>Они побеждают.</P>\r\n<P>О, дети крови,</P>\r\n<P>Во имя солнечного света.</P>\r\n<P>Слабеющие дети крови,</P>\r\n<P>Они побеждают.</P>\r\n<P>О, дети крови,</P>\r\n<P>Во имя солнечного света,</P>\r\n<P>Солнце.</P>\r\n<BR/>\r\n</BODY>\r\n</HTML>',0),
